bool Intersect(Sphere s, Ray r, inout Hit ht){
    vec3 so = s.o;
    vec3 oc = r.o - so;
    float a = dot(r.d, r.d);
    float b = 2.0 * dot(r.d, oc);
    float c = dot(oc, oc) - s.r * s.r;
    float delt = b * b - 4.0 * a * c;
    float t = ht.t;
    if(delt > 0.0) {
        float t = 0.;
        t = (-b - sqrt(delt)) / (2.0 * a);
        if(t < 0.001){
            t = (-b + sqrt(delt)) / (2.0 * a);
        }
        if(t > 0.001 && t < ht.t){
            ht.t = t;
            ht.p = r.o + ht.t * r.d;
            ht.mat = s.mat;
            ht.n = normalize(ht.p - so);
            ht.uv = GetShpereUV(ht.p, ht.n);
            return true;
        }
    }

    return false;
}

//漫反射模型
float3 Diffuse_Lambert( float3 DiffuseColor )
{
	return DiffuseColor * (1. / PI);
}

//高光
float NDF_DistributionGGX(float noh, float roughness)
{
    float a = roughness*roughness;
    float a2 = a*a;
    float noh2 = noh*noh;

    float nom   = a2;
    float denom = (noh2 * (a2 - 1.0) + 1.0);
    denom = PI * denom * denom;

    return nom / max(denom, 0.001); // prevent divide by zero for roughness=0.0 and NdotH=1.0
}

float G_SchlickGGX(float nov, float roughness)
{
    float r = (roughness + 1.0);
    float k = (r*r) / 8.0;
    float nom   = nov;
    float denom = nov * (1.0 - k) + k;
    return nom / denom;
}

float G_Smith(float nov, float nol, float roughness)
{
    return G_SchlickGGX(nov, roughness) * G_SchlickGGX(nol, roughness);
}

float saturate(float x){
    if(x < 0.) return 0.;
    if(x > 1.) return 1.;
}

vec3 F_Schlick(float hov, vec3 F0)
{
    //float Fc = pow( 1. - hov , 5.0);// 1 sub, 3 mul
	//return clamp(50.0 * F0.g, 0., 1.) * Fc + (1 - Fc) * F0;
    return F0 + (1.0 - F0) * pow(1.0 - hov, 5.0);
}
/////////////
vec3 CalcLight(Ray r, Hit ht, Light lt){
    vec3 col = vec3(0);
    vec3 n = normalize(ht.n);          //法线
    vec3 v = normalize(r.o - ht.p);    //观察方向
    vec3 l = normalize(lt.o - ht.p);   //光线方向
    vec3 h = normalize(v+l);           //中间向量
	float nov = max(dot(n,v), 0.);
    float nol = max(dot(n,l),0.);
    float noh = max(dot(n,h), 0.);
    float hov = max(dot(h,v), 0.);

    vec3 F0 = ht.mat.F0;
	F0 = mix(F0, ht.mat.col, ht.mat.metalness);

    //辐射强度,按照距离衰减
    float dis = length(lt.o - ht.p);
    float attenuation = 1.0 / (dis * dis);
    vec3 radiance = lt.col * attenuation * nol;

    //漫反射
    vec3 diff = Diffuse_Lambert(ht.mat.col);

    //高光
	float D = NDF_DistributionGGX(noh, ht.mat.roughness);
    float G = G_Smith(nov,nol,ht.mat.roughness);
	vec3 F  = F_Schlick(hov, F0);
    vec3 specular = (D*G*F) / (4.0*nov*nol+0.001);

    //brdf
    vec3 Ks = F;
    vec3 Kd = vec3(1.) - Ks;
    Kd *= 1.0 - ht.mat.metalness;
    vec3 brdf = Kd * diff + specular;

    return brdf * radiance;
}

