vec4 quat(vec3 axis, float angle) { return vec4(axis*sin(angle*0.5), cos(angle*0.5)); }
vec4 quat_i() { return vec4(0,0,0,1); }
vec4 quat_conj(vec4 q) { return vec4(-q.xyz,q.w); }
vec4 quat_mul(vec4 a, vec4 b) { return vec4(cross(a.xyz,b.xyz) + a.xyz*b.w + b.xyz*a.w, a.w*b.w - dot(a.xyz,b.xyz)); }
vec3 quat_mul(vec4 q, vec3 v) { return v-2.0*cross(cross(q.xyz,v)-q.w*v,q.xyz); }


//http://www.cmap.polytechnique.fr/~peyre/cours/x2005signal/hdr_photographic.pdf
float3 ReinhardToneMapping(float3 color, float adapted_lum) 
{
    const float MIDDLE_GREY = 1.;
    color *= MIDDLE_GREY / adapted_lum;
    return color / (1.0f + color);
}

//https://knarkowicz.wordpress.com/2016/01/06/aces-filmic-tone-mapping-curve/
float3 ACESToneMapping(float3 color, float adapted_lum)
{
	const float A = 2.51f;
	const float B = 0.03f;
	const float C = 2.43f;
	const float D = 0.59f;
	const float E = 0.14f;

	color *= adapted_lum;
	return clamp((color * (A * color + B)) / (color * (C * color + D) + E), vec3(0.), vec3(1.));
}

vec3 CalcLights(Ray r, Hit ht){
    vec3 col = vec3(0);

    //计算所有光源
    Light lt = Light(vec3(3,3,3), vec3(100,100,100));
    lt.o = quat_mul(quat(normalize(vec3(0,1,0)),iTime), lt.o);


    col += CalcLight(r, ht, lights[0]);
    //col += CalcLight(r, ht, Light(vec3(-5,1,5), vec3(100,0,0)));
    //col += CalcLight(r, ht, Light(vec3(-5,-3,1), vec3(0,100,100)));
    //col += CalcLight(r, ht, Light(vec3(-3,-1,-5), vec3(0,100,0)));
    //col += CalcLight(r, ht, Light(vec3(-1,3,-3), vec3(40,100,100)));

    //叠加环境光，后续用IBL
    vec3 ambient = vec3(0.01) * ht.mat.col * ht.mat.ao;
    col = ambient+ col;

    //hdr tonemapping
    col = ACESToneMapping(col, 1.0);

    //gamma correct
    col = pow(col, vec3(1.0/GAMMA)); 
    return col;
}

vec3 GetNormalFromMap(sampler2D normalMap, vec3 Normal, vec3 WorldPos, vec2 TexCoords)
{
    vec3 tangentNormal = texture(normalMap, TexCoords).xyz * 2.0 - 1.0;

    vec3 Q1  = dFdx(WorldPos);
    vec3 Q2  = dFdy(WorldPos);
    vec2 st1 = dFdx(TexCoords);
    vec2 st2 = dFdy(TexCoords);

    vec3 N   = normalize(Normal);
    vec3 T  = normalize(Q1*st2.t - Q2*st1.t);
    vec3 B  = -normalize(cross(N, T));
    mat3 TBN = mat3(T, B, N);

    return normalize(TBN * tangentNormal);
}

