    //球3，显示光源
    spheres[2].o = lights[0].o;
    spheres[2].r = 0.5;
    spheres[2].mat.type = MAT_TYPE_SOLID;
    spheres[2].mat.col = lights[0].col;
    spheres[2].mat.F0 = vec3(0.04);
    spheres[2].mat.ao = 0.;

    vec3 col = vec3(0);

    Hit ht;
    const float MAX_DIS = 99999999.;
    ht.t = MAX_DIS;
    const int samplerCount = 1;
    for(int i = 0; i < samplerCount; i++){
        Ray ray = BuildRay(fragCoord + vec2(rand(), rand()));
        Intersect(spheres[0], ray, ht);
        Intersect(spheres[1], ray, ht);
        Intersect(spheres[2], ray, ht);
        if(ht.t == MAX_DIS){
            col += vec3(0);//texture(skybox, ray.d).xyz;
        }
        else {
            if(ht.mat.type == MAT_TYPE_TEX){
                #ifdef LOC_SHADER
                ht.mat.roughness = texture(tex_roughness, ht.uv).r;
                ht.mat.metalness = texture(tex_metallic, ht.uv).r;
                //ht.mat.F0 = texture(tex_specular, ht.uv).xyz;
                ht.mat.col = pow(texture(tex_basecolor, ht.uv).xyz, vec3(GAMMA));
                ht.mat.ao = texture(tex_ao, ht.uv).r;
                ht.n = GetNormalFromMap(tex_normal, ht.n, ht.p, ht.uv);
                #else
                ht.mat.col = texture(tex_basecolor, ht.uv).xyz * 1.;
                ht.mat.roughness = fbm(ht.p);
                ht.mat.metalness = 1. - fbm(ht.p);
                ht.mat.ao = 0.0;
                #endif

                col += CalcLights(ray, ht);
            }
            else if(ht.mat.type == MAT_TYPE_PBR){
                ht.mat.roughness = fbm(ht.p);
                ht.mat.metalness = 1. - fbm(ht.p);
                col += CalcLights(ray, ht);
            }
            else if(ht.mat.type == MAT_TYPE_SOLID){
                col += ht.mat.col;
            }
        }
    }

    col /= float(samplerCount);
    fragColor = vec4(col,1.0);
}

