vec3 trans(vec3 p, float space)
{
    return mod(p, space) - space * 0.5;
}

float distSphere(vec3 p, float size)
{
    return length(p) - size;
}

float distCylinderXY(vec3 p, vec3 c)
{
    return length(p.xy - c.xy) - c.z;
}

float distCylinderXZ(vec3 p, vec3 c)
{
    return length(p.xz - c.xy) - c.z;
}

float distCappedCylinder(vec3 p, vec2 h)
{
    vec2 d = abs(vec2(length(p.xz), p.y)) - h;
    return min(max(d.x, d.y), 0.0) + length(max(d, 0.0));
}

float distFunc(vec3 p)
{
    float s = 4.0;
    float d1 = distCylinderXY(p, vec3(0.0, 0.0, s));
    float d2 = distCylinderXY(p, vec3(0.0, 0.0, s - 0.2));
    float d3 = distCappedCylinder(trans(p, 4.0), vec2(1.0, 3.5));
	//return d3;
    return max(max(d1, -d2), -d3);
}

vec3 getNormal(vec3 p)
{
    vec2 d = vec2(0.01, 0.0);
    return normalize(vec3(distFunc(p + d.xyy) - distFunc(p - d.xyy),
                     	  distFunc(p + d.yxy) - distFunc(p - d.yxy),
 	                      distFunc(p + d.yyx) - distFunc(p - d.yyx)));
}

mat3 camera(in vec3 ro, vec3 ta, float cr)
{
    vec3 up = vec3(sin(cr), cos(cr), 0);
    vec3 cw = normalize(ta - ro);
    vec3 cu = normalize(cross(cw, up));
	vec3 cv = normalize(cross(cu, cw));
    return mat3(cu, cv, cw);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord * 2.0 - iResolution.xy) / iResolution.y;

    float z = iTime * 10.0;
    vec3 ro = vec3(0, 0, z);
    vec3 ta = vec3(0, 10.0, z + 10.0);

    vec3 ray = camera(ro, ta, iTime) * normalize(vec3(uv, 1.5));

    float d = 0.0;
    vec3 p = ro;

    for (int i = 0; i < 256; i++)
    {
        d = distFunc(p);

        if (d < 0.01)
        {
            break;
        }

        p += ray * d;
    }

    vec3 col = vec3(0.0);
    vec3 light = normalize(vec3(1.0, 1.0, 0.5));

    if (d < 0.01)
    {
    	vec3 nor = getNormal(p);
        float d = dot(nor, light);
        d = clamp(d, 0.05, 1.0);
        col = vec3(d) * nor;
        col = pow(col, vec3(0.4545));
    }

    fragColor = vec4(col,1.0);
}

