// ray marching
const int max_iterations = 128;
const float grad_step = 0.0001;
const float min_dist = 0.002;
const float clip_far = 1000.0;

// math
const float PI = 3.14159265359;
const float DEG_TO_RAD = PI / 180.0;

vec3 diffuse_colour = vec3(0.0);
vec3 final_diff_colour = vec3(0.0);

const vec3 sky_colour = vec3(0.2, 0.1, 0.4);
const vec3 clouds_colour = vec3(1.0);
const vec3 sun_colour = 32.0 * vec3(1.0, 1.0, 0.0);
const vec3 sun_pos = normalize(vec3( 20.0, 35.0, 25.0 ));

/*vec2 random2( vec2 p ) {
    return fract(sin(vec2(dot(p,vec2(127.1,311.7)),dot(p,vec2(269.5,183.3))))*43758.5453);
}*/

// this isn't very random but it's a good deal quicker than the above
float random( vec2 p ) {
    return fract(dot(p, vec2(98.61, 35.28)));
}

float cell_height(vec2 cell) {
	return 0.5 + 0.5 * sin(iTime + random(cell) * 2.0 * PI);
}

const float block_radius = 0.1;
float blockie(vec3 p, vec2 cell) {
    float height = p.y + cell_height(cell) + block_radius;
    vec3 pr = vec3( clamp(p.xz, cell, cell+1.0)-p.xz, max(0.0, height)).xzy;
    return dot(pr,pr);
}

float dist_field(vec3 p) {
    vec2 mpos = vec2(0.0);
    vec2 cell = floor(p.xz);

    float height = p.y + cell_height(cell) + block_radius;
    float nearest = height * height;

    nearest = min(nearest, blockie(p, cell + vec2(-1.0, 0.0)));
    nearest = min(nearest, blockie(p, cell + vec2( 1.0, 0.0)));
    nearest = min(nearest, blockie(p, cell + vec2( 0.0,-1.0)));
    nearest = min(nearest, blockie(p, cell + vec2( 0.0, 1.0)));

    // corners could have a slightly optimised version
    nearest = min(nearest, blockie(p, cell + vec2(-1.0, -1.0)));
    nearest = min(nearest, blockie(p, cell + vec2(-1.0,  1.0)));
    nearest = min(nearest, blockie(p, cell + vec2( 1.0, -1.0)));
    nearest = min(nearest, blockie(p, cell + vec2( 1.0,  1.0)));

    diffuse_colour = vec3(0.1, 0.7, 0.9);
    return sqrt(nearest) - block_radius;
}

vec3 skybox(vec3 dir) {
    float horizon = smoothstep(0.0, 0.25, dir.y);
    float cloud = mix(0.8, 0.0, horizon);
    return mix(sky_colour, clouds_colour, cloud);
}

// get gradient in the world
vec3 gradient( vec3 p ) {
    const vec2 k = vec2(1,-1);
    const vec2 kg = vec2(grad_step,-grad_step);
    return vec3( k.xyy*dist_field( p + kg.xyy) +
                 k.yyx*dist_field( p + kg.yyx) +
                 k.yxy*dist_field( p + kg.yxy) +
                 k.xxx*dist_field( p + kg.xxx) );
}

// parallel light source
vec3 shading( vec3 v, vec3 n, vec3 dir, vec3 eye ) {
	float diffuse  = max( 0.0, dot( sun_pos, n ) );
	float shininess = 80.0;

	vec3 ref = reflect( dir, n );
	float specular = max( 0.0, dot( sun_pos, ref ) );

	specular = pow( specular, shininess );

	return final_diff_colour * diffuse + vec3(specular);
}

// ray marching
vec3 ray_marching(vec3 o, vec3 dir) {
    vec3 colour = vec3(0.0);
    vec3 acc_refl = vec3(1.0);

    // give the ray a bit of a head start by jumping
    // straight to the upper bounding plane!
    float t = 0.0;
    if (dir.y < 0.0) {
        t = -o.y/dir.y;
    } else {
        return skybox(dir);
    }
	t = max(t, min_dist*2.0);

    vec3 v;
    for (int i = 0; i < max_iterations; i++) {
       	float d = dist_field(v = o + dir * t);

        if (d <= min_dist) {
            break;
        }
		if ((t+=d) >= clip_far) {
            return skybox(dir);
	    }
    }
	final_diff_colour = diffuse_colour;

    vec3 n = normalize( gradient(v) );
    colour += shading( v, n, dir, o );
    if (dot(n, sun_pos) < 0.0) {
        return colour;
    }

    // shadows
    t = 0.1;
    for (int i = 0; i < max_iterations; i++) {
        vec3 r = v + sun_pos * t;
		if (r.y > 0.0) {
            break;
	    }

        float d = dist_field(r);
        if (d <= min_dist) {
            colour *= 0.3;
            break;
        }
        t+=d;
    }
    return colour;
}

// get ray direction
vec3 ray_dir( float fov, vec2 size, vec2 pos ) {
	vec2 xy = pos - size * 0.5;

	float cot_half_fov = tan( ( 90.0 - fov * 0.5 ) * DEG_TO_RAD );
	float z = size.y * 0.5 * cot_half_fov;

	return normalize( vec3( xy, -z ) );
}

// camera rotation : pitch, yaw
mat3 rotationXY( vec2 angle ) {
	vec2 c = cos( angle );
	vec2 s = sin( angle );

	return mat3(
		c.y      ,  0.0, -s.y,
		s.y * s.x,  c.x,  c.y * s.x,
		s.y * c.x, -s.x,  c.y * c.x
	);
}

const vec2 spin = vec2(0.005, 0.0041);
const float radius = 120.0;
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	// rotate camera
#ifdef MOUSE_CONTROL
	mat3 rot = rotationXY( vec2(PI, -PI) * ((iMouse.xy - iResolution.xy * 0.5)/iResolution.xy).yx );
#else
	mat3 rot = rotationXY( vec2( -0.6, iTime * 0.2 ) );
#endif

	// default ray origin
	vec3 eye = vec3( 0.0, 1.0, 0.0 );
	eye = rot * eye;

    // move in a sinusoidal path so we don't end up in floating point hell!
    vec2 camera = radius * cos(iTime * spin);

    eye += vec3(camera, 1.0).xzy;

    // default ray dir
	vec3 dir = rot * ray_dir( 60.0, iResolution.xy, fragCoord.xy);
	vec3 color = ray_marching(eye, dir);

    fragColor = vec4(color, 1.0);
    return;
}

#define pixel_width 3./iResolution.y

vec3 random3(vec3 c) {
	float j = 4096.0*sin(dot(c,vec3(17.0, 59.4, 15.0)));
	vec3 r;
	r.z = fract(512.0*j);
	j *= .125;
	r.x = fract(512.0*j);
	j *= .125;
	r.y = fract(512.0*j);
	return r;
}
const float F3 =  0.3333333;
const float G3 =  0.1666667;

float simplex3d(vec3 p) {
	 vec3 s = floor(p + dot(p, vec3(F3)));
	 vec3 x = p - s + dot(s, vec3(G3));

	 vec3 e = step(vec3(0.0), x - x.yzx);
	 vec3 i1 = e*(1.0 - e.zxy);
	 vec3 i2 = 1.0 - e.zxy*(1.0 - e);

	 vec3 x1 = x - i1 + G3;
	 vec3 x2 = x - i2 + 2.0*G3;
	 vec3 x3 = x - 1.0 + 3.0*G3;

	 vec4 w, d;

	 w.x = dot(x, x);
	 w.y = dot(x1, x1);
	 w.z = dot(x2, x2);
	 w.w = dot(x3, x3);

	 w = max(0.6 - w, 0.0);

	 d.x = dot(random3(s)-.5, x);
	 d.y = dot(random3(s + i1)-.5, x1);
	 d.z = dot(random3(s + i2)-.5, x2);
	 d.w = dot(random3(s + 1.0)-.5, x3);

	 w *= w;
	 w *= w;
	 d *= w;

	 return pow(dot(d, vec4(52.0)),2.);
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
	uv = uv*2.-1.;
	uv.x *= iResolution.x/iResolution.y;

    float circleSDF = length(uv);
    float radius = .6 + simplex3d(vec3(uv,iTime*.5))*.1;
    vec3 palette = 0.5 + 0.5*cos(iTime+vec3(0,2,4));
    vec3 col = smoothstep(pixel_width,0.0,circleSDF-radius) * palette;
    radius = .3 + simplex3d(vec3(uv,1000.+iTime*.3))*.05;
    col += smoothstep(pixel_width,0.0,circleSDF-radius);
    radius = .07 +simplex3d(vec3(uv,2000.+iTime*.2))*.05;
    col = clamp(col,vec3(0),vec3(1));
    col *= smoothstep(pixel_width,0.0,radius-circleSDF);

    // Output to screen
    fragColor = vec4(col,1.0);
}

