    vec4 arms = join(leftArm, rightArm);

    vec4 buttonTop = shape(sphere(atCoordSystem(coord, BUTTON_1_O, BUTTON_1_X, BUTTON_1_Y, BUTTON_1_Z), 0.3), RED);
    vec4 buttonBot = shape(sphere(atCoordSystem(coord, BUTTON_2_O, BUTTON_2_X, BUTTON_2_Y, BUTTON_2_Z), 0.3), RED);
    vec4 buttons = join(buttonTop, buttonBot);

    return join(snow, face, arms, buttons);
}

