void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    float x = (fragCoord.x-iResolution.x/2.0)*0.5;
    float y = (fragCoord.y-iResolution.y/2.0)*0.25;

    float zy = iTime                   + 160.0/abs(y);
    float zx = 1.0+2.0*cos(iTime/4.0)  + 1.0*x/abs(y);

    float a = zx;
    float b = zy;

    // makes it diagonal
    zx = a+b;
    zy = a-b;

    // it makes it be all checkerboardy
    zy = floor(zy*1.0)/2.0;
    zx = floor(zx*1.0)/2.0;

    float parx = 0.5 + 0.5*cos(zx*6.0);
    float pary = 0.5 + 0.5*cos(zy*6.0);

    // a differet textrue thingy
    parx = mod(abs(zx+1e4), 1.0)*2.0;
    pary = mod(abs(zy+1e4), 1.0)*2.0;


    // ugly cyan fog at the horizon. yuck.
    /*
	float fog = 15.0;
    fog = 0.0;

    float p1 = abs(y)/fog;
    p1 = p1*p1;

    if (p1 < 1.0) {
    	parx = 1.0-(1.0-parx)*p1;
        pary = 0.0+(0.0+pary)*p1;
    }
    */

    // much much better fog

    float fog = 2.0+iTime/2.0;
    if (zx > fog) {
        parx = parx/(zx-fog);
    }
    fog = -5.0-iTime/2.0;
    if (zy < fog) {
        pary = abs(pary/(zy-fog));
    }
    fog = iTime/2.0+20.0;
    if (zx > fog) {
        pary = abs(pary/(zx-fog));
    }
    fog = -iTime/2.0-20.0;
    if (zy < fog) {
        parx = abs(parx/(zy-fog));
    }


    vec3 col = vec3(min(parx, pary), parx, max(parx, pary));

    // Output to screen
    fragColor = vec4(col,1.0);
}

/// @brief Rotate position (pos) with radian on counter-clockwise.
vec3 RotateY(vec3 pos, float radian)
{
    return vec3(
        cos(radian) * pos.x + sin(radian) * pos.z, 
        pos.y,
        -sin(radian) * pos.x + cos(radian) * pos.z);
}

/// @brief Get shortest distance from point to infinity line.
float DistLineToPoint(vec3 ro, vec3 rd, vec3 pos)
{
    vec3 ap = pos - ro;
    return length(cross(ap, rd)) / length(rd);
}

/// @brief Directional light structure.
struct DLight 
{
    vec3 mDir;
    vec3 mCol;
};

/// @brief Plane structure.
struct DPlane
{
    vec3 mCenterPos;
    vec3 mNormal;
    vec3 mCol;
};

/// @brief Get signed-distance funciton weight value of plane.
float sdfPlane(vec3 pos, DPlane plane)
{
    // Calculus Early Transcendentals 7th p823.
    float denom = length(plane.mNormal);
    float numem = abs(dot(plane.mNormal, pos - plane.mCenterPos));
    return numem / denom;
}

/// @brief Get normal vector of plane.
vec3 GetNormalPlane(vec3 pos, vec3 dir, DPlane plane)
{
    return plane.mNormal;
}

/// @brief Sphere structure.
struct DSphere
{
    vec3 mPos;
    vec3 mCol;
    float mRadius;
};

/// @brief Get signed-distance function weight value of sphere.
/// 0 is isosurface, and negative value is in sphere.
float sdfSphere(vec3 pos, DSphere sphere)
{
    return distance(pos, sphere.mPos) - sphere.mRadius;
}

/// @brief Get proper normal vector from intersection of ray with sphere,
/// when ray is intersected with sphere.
vec3 GetNormalSphere(vec3 pos, vec3 dir, DSphere sphere)
{
    float longHypot  = distance(pos, sphere.mPos);
    float shortDist2 = pow(DistLineToPoint(pos, dir, sphere.mPos), 2.0);

    float t = sqrt(pow(longHypot, 2.0) - shortDist2);
    float minusT = sqrt(pow(sphere.mRadius, 2.0) - shortDist2);
    t -= minusT;

    return normalize((pos + t * dir) - sphere.mPos);
}

void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    vec2 uv = fragCoord.xy / min(iResolution.x, iResolution.y);
    uv.x -= (iResolution.x - iResolution.y) / (2. * min(iResolution.x, iResolution.y)); 
    uv -= 0.5;

    vec3 camera = RotateY(vec3(0, 3, 5), iTime);
    vec3 view   = vec3(0, 0, 0);
    vec3 up     = vec3(0, 1, 0);

    // Setting up View matrix (don't make view matrix itself, We just need v,s,u element).
    vec3 forward = normalize(view - camera);
    vec3 side    = cross(forward, up);
    vec3 camUp   = cross(side, forward);

    float zoom = 2.0f;

    // Setting up virtual screen, and lay direction of virtual screen pixel.
    vec3 scrOrg = camera + zoom * forward;
    vec3 pxlPos = scrOrg + (side * uv.x) + (camUp * uv.y);
    vec3 rayDir = normalize(pxlPos - camera);

    DPlane far;
    far.mCenterPos = camera + 10.0 * forward;
    far.mNormal = -forward;

    // Setting up light
    DLight dirLight;
    dirLight.mDir = normalize(vec3(0, 1, 1));
    dirLight.mCol = vec3(1, 1, 1);

    // Setting up background color.
    vec3 result = vec3(0);
    result = vec3(0., 56. / 255., 91. / 255.);

    // Setting up structure (model)
    DPlane plane;
    plane.mCenterPos = vec3(0, -1, 0);
    plane.mNormal = vec3(0, 1, 0);
    plane.mCol = vec3(1, .5, 0);

    DSphere sphere;
    sphere.mPos = vec3(0, 0, 1);
    sphere.mCol = vec3(0, .5, 1);
    sphere.mRadius = 0.5;

    DSphere sphere2;
    sphere2.mPos = RotateY(vec3(0, 0, 1), 0.78);
    sphere2.mCol = vec3(0, 1, 0);
    sphere2.mRadius = 0.25;

    DSphere sphere3;
    sphere3.mPos = RotateY(vec3(0, 0, 1), 1.17);
    sphere3.mCol = vec3(1, 0, 0);
    sphere3.mRadius = 0.125;

    // Raycasting & marching & rendering.
    for (int i = 0; i < 64 && sdfPlane(camera, far) > 0.5f; ++i)
    {
        float shortestDist = sdfPlane(camera, far);
        shortestDist = min(shortestDist, sdfSphere(camera, sphere));
        if (shortestDist < 0.01)
        {
            vec3 normal = GetNormalSphere(camera, rayDir, sphere);
            vec3 qLambert = 
                clamp(dot(dirLight.mDir, normal) * sphere.mCol * dirLight.mCol, 0., 1.) 
              * 0.75 + 0.25;
            result = qLambert;
            break;
        }

        shortestDist = min(shortestDist, sdfSphere(camera, sphere2));
        if (shortestDist < 0.01)
        {
            vec3 normal = GetNormalSphere(camera, rayDir, sphere2);
            vec3 qLambert = 
                clamp(dot(dirLight.mDir, normal) * sphere2.mCol * dirLight.mCol, 0., 1.) 
              * 0.75 + 0.25;
            result = qLambert;
            break;
        }

        shortestDist = min(shortestDist, sdfSphere(camera, sphere3));
        if (shortestDist < 0.01)
        {
            vec3 normal = GetNormalSphere(camera, rayDir, sphere3);
            vec3 qLambert = 
                clamp(dot(dirLight.mDir, normal) * sphere3.mCol * dirLight.mCol, 0., 1.) 
              * 0.75 + 0.25;
            result = qLambert;
            break;
        }

        shortestDist = min(shortestDist, sdfPlane(camera, plane));
        if (shortestDist < 0.01)
        {
            vec3 normal = GetNormalPlane(camera, rayDir, plane);
            vec3 qLambert = 
                clamp(dot(dirLight.mDir, normal) * plane.mCol * dirLight.mCol, 0., 1.) 
              * 0.75 + 0.25f;
            result = qLambert;
            break;
        }

        camera += shortestDist * rayDir;
    }

    fragColor = vec4(result, 1.0);
}

//++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
// SmartNoise Demo by Bapho
// https://github.com/Bapho/reshade-shaders/blob/master/Shaders/SmartNoise.fx
//++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++

float noise = 2.0;

//----------------------------------------------------------------------
precision lowp float;
float PHI = 1.61803398874989484820459 * 00000.1; // Golden Ratio   
float PI  = 3.14159265358979323846264 * 00000.1; // PI
float SQ2 = 1.41421356237309504880169 * 10000.0; // Square Root of Two


float gold_noise(in vec2 coordinate, in float seed){
    return fract(tan(distance(coordinate*(seed+PHI), vec2(PHI, PI)))*SQ2);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float mouse;
    if (iMouse.x <= 0.0 || iMouse.x >= iResolution.x){
		mouse = iResolution.x / 2.0;
    } else {
        mouse = iMouse.x;
    }

    if (mouse > fragCoord.x - 4.0 && mouse < fragCoord.x + 4.0){
        fragColor = vec4(0.4, 0.0, 0.0, 1.0);
    } else {

        vec2 uv = fragCoord/iResolution.xy;

        vec3 color;
        if (fragCoord.x < iResolution.x * 0.05
            || fragCoord.x > iResolution.x * 0.95){
            color = vec3(0.0);

        } else if (fragCoord.y < iResolution.y * 0.15){
        	color = texture(iChannel1, uv).rgb;

        } else if (fragCoord.y < iResolution.y * 0.85){
            color = texture(iChannel0, uv).rgb;

        } else {
            color = 0.5 + 0.25*cos(iTime*0.5+uv.xyx+vec3(0,4,8));
            //color = vec3(0.0, 0.8, 0.2);
        }

        if ((mouse <= 0.0 && fragCoord.x < iResolution.x / 2.0) 
				|| mouse > fragCoord.x){

            float luminance = (0.2126 * color.r) 
                + (0.7152 * color.g) + (0.0722 * color.b);

            float uniquePos = (iResolution.x * fragCoord.y) + fragCoord.x;

            float amount = noise * 0.08;

            // adjusting "noise contrast"
            if (luminance < 0.5){
                amount *= (luminance / 0.5);
            } else {
                amount *= ((1.0 - luminance) / 0.5);
            }

            // reddish pixels will get less noise
            float redDiff = color.r - ((color.g + color.b) / 2.0);
            if (redDiff > 0.0){
                amount *= (1.0 - (redDiff * 0.5));
            }

            // a very low unique seed will lead to slow noise
            // pattern changes on slow moving color gradients
            float uniqueSeed = ((luminance * iResolution.y) + uniquePos) * 0.00000001;

            // using a fictive coordinate as a workaround to fix a pattern bug
			vec2 coordinate = vec2(fragCoord.x, fragCoord.y * 1.001253543);

            // average noise luminance to subtract
            float sub = (0.5 * amount);

            // "noise clipping"
            if (luminance - sub < 0.0){
                amount *= (luminance / sub);
                sub *= (luminance / sub);
            } else if (luminance + sub > 1.0){
                if (luminance > sub){
                    amount *= (sub / luminance);
                    sub *= (sub / luminance);
                } else {
                    amount *= (luminance / sub);
                    sub *= (luminance / sub);
                }
            }

            // calculating and adding/subtracting the golden noise
            float ran = gold_noise(coordinate, uniqueSeed);
            float add = clamp(ran * amount, 0.0, 1.0);
            color += (add - sub);
        }

        fragColor = vec4(color, 1.0);
    }
}

// originally adapted from http://www.peda.com/grafeq/gallery/margarita.html

#define PI 3.14159265359
#define SCALE 60.0

float f(float a, vec2 p)
{
      float cf = 2.0 - cos(0.1 * iTime);
      float d = (length(p) - 3.5 * atan(p.y, p.x) + sin(cf * p.x) + cos(cf * p.y))/(a * PI);

      return smoothstep(0.0, 0.2, 0.5 * cos(PI * (2.0 * d - iTime)));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
        vec2 aspect = iResolution.xy / iResolution.y;
        vec2 uv = ( fragCoord.xy / iResolution.y ) - 0.5 * aspect;
        uv *= SCALE;

        vec3 col = mix(vec3(0.29, 0.22, 0.48), vec3(0.88, 0.03, 0.11), f(7.0, uv));
        col = mix(vec3(0.9, 0.87, 0.63), col, f(1.0, uv));

        col *= 1.0 - 0.1 * length(fragCoord/iResolution.xy - 0.5); // vignetting
        fragColor = vec4( col, 1.0 );
}

bool tick(int i, int s){
    return (i-s+6)%6 == 0;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = ( fragCoord - iResolution.xy ) / iResolution.y; 
    vec2 mouse = iMouse.xy/iResolution.xy;
    uv+=mouse*2.;
    float t = iTime * 5.0;
    //t += sin(t)+1.0;
    float res = 5., speed = 0.0137, offset = t * .1;
    offset *= ceil(uv.y * res) *.25 + 1.5;
    float flip =  round( fract(uv.y * .5 * res ));
    uv.x += (flip-.5) * offset;
    float pw = res*1.5/iResolution.y;

    vec2 u = uv * res; 
    int i = int(ceil(u.x) + ceil(u.y));
    u = fract(u);
    float k = 0.,
        a = u.y + u.x, b = u.y - u.x,
        c = length(u - .5);

    if(tick(i, 2)){
        float g = step(.0, u.y - .5);
        k += smoothstep(.5 - pw, .5, c) + step(.8, b + .2);
        k += smoothstep(.15, .15 - pw, c+g);
        k += smoothstep(.05, .05 - pw,  length(vec2(abs(u.x - .5), u.y - .5) - .1));
    }else if(tick(i, 3)){
        k += step(1. - abs(b), .7) - step(.5 - u.y, 0.) + step(.9 - b, .1);
    }else if(tick(i, 5) || tick(i , 4)){
        k += step(.3, abs(b)) - step(1.3 - u.x, .4);        
    }

    if(tick(i, 4)){
        k-= step(abs(1. - b), .6);
    }else if(tick(i, 0)){
        k += step( abs(b + 1.), .5);
    }else if(tick(i, 1) ){
        k += 1.;
    }

    if(flip == 1.) k = 1. - k;
    vec3 front = vec3(1., .1, .3), back = vec3(.2, .1, .2);
    fragColor = vec4(mix(front, back, clamp(k, 0., 1.)), 1.);
}

// Fuzz-flakes by Matt McLin

// random func based on one found here: https://thebookofshaders.com/10/
// @patriciogv - 2015, http://patriciogonzalezvivo.com
float rand (float seed) 
{
    return fract(sin(dot(vec2(seed, seed*0.5926535897),
                         vec2(12.9898,78.233)))*43758.5453123);
}

float flake(in vec2 uv, in vec2 pos, float speed, float size, float wind)
{
    uv.x *= 1.6;
    vec2 drop;
    drop.y = 1.0 - fract(((iTime+pos.y)*speed) / iResolution.y);
    drop.x = (pos.x * 1.6) + sin(drop.y*wind)*0.1;
    float d = distance(uv, drop);
    d = clamp(d, 0.0, 0.28);
    if (d < size) return 1.0;
    if (d > (size+0.02)) return 0.0;
    return mix(1.0,0.0, 50.0*(d - size));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    vec3 col = vec3(0.3,0.1,0.7) - 0.2*cos(iTime+uv.xyx);

    for (float i=0.0; i<200.0; i += 1.0) {
        vec3 flakecol = vec3(0.3+0.3*rand(i*0.9),0.7+0.3*rand(i*0.3),0.8+0.2*rand(i*0.6));
        vec2 pos = vec2(rand(i), rand(i*0.887787)*1.6 + 0.1);
        float speed = 150.0 + 100.0*rand(i*0.728734);
        float size = 0.005 + 0.02*rand(i*0.1415926);
        float wind = 4.0*rand(i*0.314159);
        col = mix(col, flakecol, flake(uv, pos, speed, size, wind));
    }

    fragColor = vec4(col,1.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    uv.y = 1.0 - uv.y + 0.5;

    vec2 n = normalize(uv + uv - vec2(1.0));


	float amt = sin(iTime)*0.25 + 0.25;
    float feather = 0.01;
    float base = 0.5;


    float grad = smoothstep(base + amt, base + amt + feather, n.y);
    grad = smoothstep(0.0, feather*10.0, grad);
    fragColor = vec4(grad);
}

#define PI 3.14159265359
#define PI2 1.5707963268

void pR(inout vec2 p, float a) {
    p = cos(a)*p + sin(a)*vec2(p.y, -p.x);
}

vec2 pRi(vec2 p, float a) {
    pR(p, a);
    return p;
}

vec3 lerp3(vec3 a, vec3 b, float t)
{
    return a * t + b * (1.0-t);
}

#define saturate(x) clamp(x, 0., 1.)

float vmax(vec2 v) {
    return max(v.x, v.y);
}

float vmax(vec3 v) {
    return max(max(v.x, v.y), v.z);
}

float vmin(vec3 v) {
    return min(min(v.x, v.y), v.z);
}

float vmin(vec2 v) {
    return min(v.x, v.y);
}

float fBox(vec3 p, vec3 b) {
    vec3 d = abs(p) - b;
    return length(max(d, vec3(0))) + vmax(min(d, vec3(0)));
}

float fCorner2(vec2 p) {
    return length(max(p, vec2(0))) + vmax(min(p, vec2(0)));
}

float fDisc(vec3 p, float r) {
    float l = length(p.xz) - r;
    return l < 0. ? abs(p.y) : length(vec2(p.y, l));
}


float fHalfCapsule(vec3 p, float r) {
    return mix(length(p.xz) - r, length(p) - r, step(0., p.y));
}


// IQ https://www.shadertoy.com/view/Xds3zN
float sdRoundCone( in vec3 p, in float r1, float r2, float h )
{
    vec2 q = vec2( length(p.xz), p.y );

    float b = (r1-r2)/h;
    float a = sqrt(1.0-b*b);
    float k = dot(q,vec2(-b,a));

    if( k < 0.0 ) return length(q) - r1;
    if( k > a*h ) return length(q-vec2(0.0,h)) - r2;

    return dot(q, vec2(a,b) ) - r1;
}

float smin2(float a, float b, float r) {
    vec2 u = max(vec2(r - a,r - b), vec2(0));
    return max(r, min (a, b)) - length(u);
}

float smax2(float a, float b, float r) {
    vec2 u = max(vec2(r + a,r + b), vec2(0));
    return min(-r, max (a, b)) + length(u);
}

float smin(float a, float b, float k){
    float f = clamp(0.5 + 0.5 * ((a - b) / k), 0., 1.);
    return (1. - f) * a + f  * b - f * (1. - f) * k;
}

float smax(float a, float b, float k) {
    return -smin(-a, -b, k);
}

float smin3(float a, float b, float k){
    return min(
        smin(a, b, k),
        smin2(a, b, k)
    );
}

float smax3(float a, float b, float k){
    return max(
        smax(a, b, k),
        smax2(a, b, k)
    );
}


float ellip(vec3 p, vec3 s) {
    float r = vmin(s);
    p *= r / s;
    return length(p) - r;
}

float ellip(vec2 p, vec2 s) {
    float r = vmin(s);
    p *= r / s;
    return length(p) - r;
}

bool isEye = false;


float mHead(vec3 p) {

    pR(p.yz, -.1);
    p.y -= .11;

    vec3 pa = p;
    p.x = abs(p.x);
    vec3 pp = p;

    float d = 1e12;

    // skull back
    p += vec3(0,-.135,.09);
    d = ellip(p, vec3(.395, .385, .395));

    // skull base
    p = pp;
    p += vec3(0,-.135,.09) + vec3(0,.1,.07);
    d = smin(d, ellip(p, vec3(.38, .36, .35)), .05);

    // forehead
    p = pp;
    p += vec3(0,-.145,-.175);
    d = smin(d, ellip(p, vec3(.315, .3, .33)), .18);

    p = pp;
    pR(p.yz, -.5);
    float bb = fBox(p, vec3(.5,.67,.7));
    d = smax(d, bb, .2);

    // face base
    p = pp;
    p += vec3(0,.25,-.13);
    d = smin(d, length(p) - .28, .1);

    // behind ear
    p = pp;
    p += vec3(-.15,.13,.06);
    d = smin(d, ellip(p, vec3(.15,.15,.15)), .15);

    p = pp;
    p += vec3(-.07,.18,.1);
    d = smin(d, length(p) - .2, .18);

    // cheek base
    p = pp;
    p += vec3(-.2,.12,-.14);
    d = smin(d, ellip(p, vec3(.15,.22,.2) * .8), .15);

    // jaw base
    p = pp;
    p += vec3(0,.475,-.16);
    pR(p.yz, .8);
    d = smin(d, ellip(p, vec3(.19,.1,.2)), .1);

    // brow
    p = pp;
    p += vec3(0,-.0,-.18);
    vec3 bp = p;
    float brow = fHalfCapsule(p * vec3(.65,1,.9), .27);
    brow = length(p) - .36;
    p.x -= .37;
    brow = smax(brow, dot(p, normalize(vec3(1,.2,-.2))), .2);
    p = bp;
    brow = smax(brow, dot(p, normalize(vec3(0,.6,1))) - .43, .25);
    p = bp;
    pR(p.yz, -.5);
    float peak = -p.y - .165;
    peak += smoothstep(.0, .2, p.x) * .01;
    peak -= smoothstep(.12, .29, p.x) * .025;
    brow = smax(brow, peak, .07);
    p = bp;
    pR(p.yz, .5);
    brow = smax(brow, -p.y - .06, .15);
    d = smin(d, brow, .06);

    // nose
    p = pp;
    p += vec3(0,.03,-.45);
    pR(p.yz, 3.);
    d = smin(d, sdRoundCone(p, .008, .05, .18), .1);

    p = pp;
    p += vec3(0,.06,-.47);
    pR(p.yz, 2.77);
    d = smin(d, sdRoundCone(p, .005, .04, .225), .05);

    // jaw

    p = pp;
    vec3 jo = vec3(-.25,.4,-.07);
    p = pp + jo;
    float jaw = dot(p, normalize(vec3(1,-.2,-.05))) - .069;
    jaw = smax(jaw, dot(p, normalize(vec3(.5,-.25,.35))) - .13, .12);
    jaw = smax(jaw, dot(p, normalize(vec3(-.0,-1.,-.8))) - .12, .15);
    jaw = smax(jaw, dot(p, normalize(vec3(.98,-1.,.15))) - .13, .08);
    jaw = smax(jaw, dot(p, normalize(vec3(.6,-.2,-.45))) - .19, .15);
    jaw = smax(jaw, dot(p, normalize(vec3(.5,.1,-.5))) - .26, .15);
    jaw = smax(jaw, dot(p, normalize(vec3(1,.2,-.3))) - .22, .15);

    p = pp;
    p += vec3(0,.63,-.2);
    pR(p.yz, .15);
    float cr = .5;
    jaw = smax(jaw, length(p.xy - vec2(0,cr)) - cr, .05);

    p = pp + jo;
    jaw = smax(jaw, dot(p, normalize(vec3(0,-.4,1))) - .35, .1);
    jaw = smax(jaw, dot(p, normalize(vec3(0,1.5,2))) - .3, .2);
    jaw = max(jaw, length(pp + vec3(0,.6,-.3)) - .7);

    p = pa;
    p += vec3(.2,.5,-.1);
    float jb = length(p);
    jb = smoothstep(.0, .4, jb);
    float js = mix(0., -.005, jb);
    jb = mix(.01, .04, jb);

    d = smin(d, jaw - js, jb);

    // chin
    p = pp;
    p += vec3(0,.585,-.395);
    p.x *= .7;
    d = smin(d, ellip(p, vec3(.028,.028,.028)*1.2), .15);

    // cheek

    p = pp;
    p += vec3(-.2,.2,-.28);
    pR(p.xz, .5);
    pR(p.yz, .4);
    float ch = ellip(p, vec3(.1,.1,.12)*1.05);
    d = smin(d, ch, .1);

    p = pp;
    p += vec3(-.26,.02,-.1);
    pR(p.xz, .13);
    pR(p.yz, .5);
    float temple = ellip(p, vec3(.1,.1,.15));
    temple = smax(temple, p.x - .07, .1);
    d = smin(d, temple, .1);

    p = pp;
    p += vec3(.0,.2,-.32);
    ch = ellip(p, vec3(.1,.08,.1));
    d = smin(d, ch, .1);

    p = pp;
    p += vec3(-.17,.31,-.17);
    ch = ellip(p, vec3(.1));
    d = smin(d, ch, .1);

    // mouth base
    p = pp;
    p += vec3(-.0,.29,-.29);
    pR(p.yz, -.3);
    d = smin(d, ellip(p, vec3(.13,.15,.1)), .18);

    p = pp;
    p += vec3(0,.37,-.4);
    d = smin(d, ellip(p, vec3(.03,.03,.02) * .5), .1);

    p = pp;
    p += vec3(-.09,.37,-.31);
    d = smin(d, ellip(p, vec3(.04)), .18);

    // bottom lip
    p = pp;
    p += vec3(0,.455,-.455);
    p.z += smoothstep(.0, .2, p.x) * .05;
    float lb = mix(.035, .03, smoothstep(.05, .15, length(p)));
    vec3 ls = vec3(.055,.028,.022) * 1.25;
    float w = .192;
    vec2 pl2 = vec2(p.x, length(p.yz * vec2(.79,1)));
    float bottomlip = length(pl2 + vec2(0,w-ls.z)) - w;
    bottomlip = smax(bottomlip, length(pl2 - vec2(0,w-ls.z)) - w, .055);
    d = smin(d, bottomlip, lb);

    // top lip
    p = pp;
    p += vec3(0,.38,-.45);
    pR(p.xz, -.3);
    ls = vec3(.065,.03,.05);
    w = ls.x * (-log(ls.y/ls.x) + 1.);
    vec3 pl = p * vec3(.78,1,1);
    float toplip = length(pl + vec3(0,w-ls.y,0)) - w;
    toplip = smax(toplip, length(pl - vec3(0,w-ls.y,0)) - w, .065);
    p = pp;
    p += vec3(0,.33,-.45);
    pR(p.yz, .7);
    float cut;
    cut = dot(p, normalize(vec3(.5,.25,0))) - .056;
    float dip = smin(
        dot(p, normalize(vec3(-.5,.5,0))) + .005,
        dot(p, normalize(vec3(.5,.5,0))) + .005,
        .025
    );
    cut = smax(cut, dip, .04);
    cut = smax(cut, p.x - .1, .05);
    toplip = smax(toplip, cut, .02);

    d = smin(d, toplip, .07);

    // seam
    p = pp;
    p += vec3(0,.425,-.44);
    lb = length(p);
    float lr = mix(.04, .02, smoothstep(.05, .12, lb));
    pR(p.yz, .1);
    p.y -= smoothstep(0., .03, p.x) * .002;
    p.y += smoothstep(.03, .1, p.x) * .007;
    p.z -= .133;
    float seam = fDisc(p, .2);
    seam = smax(seam, -d - .015, .01); // fix inside shape
    d = mix(d, smax(d, -seam, lr), .65);

    // nostrils base
    p = pp;
    p += vec3(0,.3,-.43);
    d = smin(d, length(p) - .05, .07);

    // nostrils
    p = pp;
    p += vec3(0,.27,-.52);
    pR(p.yz, .2);
    float nostrils = ellip(p, vec3(.055,.05,.06));

    p = pp;
    p += vec3(-.043,.28,-.48);
    pR(p.xy, .15);
    p.z *= .8;
    nostrils = smin(nostrils, sdRoundCone(p, .042, .0, .12), .02);

    d = smin(d, nostrils, .02);

    p = pp;
    p += vec3(-.033,.3,-.515);
    pR(p.xz, .5);
    d = smax(d, -ellip(p, vec3(.011,.03,.025)), .015);

    // return d;

    // eyelids
    p = pp;
    p += vec3(-.16,.07,-.34);
    float eyelids = ellip(p, vec3(.08,.1,.1));

    p = pp;
    p += vec3(-.16,.09,-.35);
    float eyelids2 = ellip(p, vec3(.09,.1,.07));

    // edge top
    p = pp;
    p += vec3(-.173,.148,-.43);
    p.x *= .97;
    float et = length(p.xy) - .09;

    // edge bottom
    p = pp;
    p += vec3(-.168,.105,-.43);
    p.x *= .9;
    float eb = dot(p, normalize(vec3(-.1,-1,-.2))) + .001;
    eb = smin(eb, dot(p, normalize(vec3(-.3,-1,0))) - .006, .01);
    eb = smax(eb, dot(p, normalize(vec3(.5,-1,-.5))) - .018, .05);

    float edge = max(max(eb, et), -d);

    d = smin(d, eyelids, .01);
    d = smin(d, eyelids2, .03);
    d = smax(d, -edge, .005);

    // eyeball
    p = pp;
    p += vec3(-.165,.0715,-.346);
    float eyeball = length(p) - .088;
	isEye = eyeball < d;
    d = min(d, eyeball);

    // tear duct
    p = pp;
    p += vec3(-.075,.1,-.37);
    d = min(d, length(p) - .05);


 	// ear
    p = pp;
    p += vec3(-.405,.12,.10);
    pR(p.xy, -.12);
    pR(p.xz, .35);
    pR(p.yz, -.3);
    vec3 pe = p;

    // base
    float ear = p.s + smoothstep(-.05, .1, p.y) * .015 - .005;
    float earback = -ear - mix(.001, .025, smoothstep(.3, -.2, p.y));

    // inner
    pR(p.xz, -.5);
    float iear = ellip(p.zy - vec2(.01,-.03), vec2(.045,.05));
    iear = smin(iear, length(p.zy - vec2(.04,-.09)) - .02, .09);
    float ridge = iear;
    iear = smin(iear, length(p.zy - vec2(.1,-.03)) - .06, .07);
    ear = smax2(ear, -iear, .04);
    earback = smin(earback, iear - .04, .02);

    // ridge
    p = pe;
    pR(p.xz, .2);
    ridge = ellip(p.zy - vec2(.01,-.03), vec2(.045,.055));
    ridge = smin3(ridge, -pRi(p.zy, .2).x - .01, .015);
    ridge = smax3(ridge, -ellip(p.zy - vec2(-.01,.1), vec2(.12,.08)), .02);

    float ridger = .01;

    ridge = max(-ridge, ridge - ridger);

    ridge = smax2(ridge, abs(p.x) - ridger/2., ridger/2.);

    ear = smin(ear, ridge, .045);

    p = pe;

    // outline
    float outline = ellip(pRi(p.yz, .2), vec2(.12,.09));
    outline = smin(outline, ellip(p.yz + vec2(.155,-.02), vec2(.035, .03)), .14);

    // edge
    float eedge = p.x + smoothstep(.2, -.4, p.y) * .06 - .03;

    float edgeo = ellip(pRi(p.yz, .1), vec2(.095,.065));
    edgeo = smin(edgeo, length(p.zy - vec2(0,-.1)) - .03, .1);
    float edgeoin = smax(abs(pRi(p.zy, .15).y + .035) - .01, -p.z-.01, .01);
    edgeo = smax(edgeo, -edgeoin, .05);

    float eedent = smoothstep(-.05, .05, -p.z) * smoothstep(.06, 0., fCorner2(vec2(-p.z, p.y)));
    eedent += smoothstep(.1, -.1, -p.z) * .2;
    eedent += smoothstep(.1, -.1, p.y) * smoothstep(-.03, .0, p.z) * .3;
    eedent = min(eedent, 1.);

    eedge += eedent * .06;

    eedge = smax(eedge, -edgeo, .01);
    ear = smin(ear, eedge, .01);
    ear = max(ear, earback);

    ear = smax2(ear, outline, .015);

    d = smin(d, ear, .015);

    // targus
    p = pp;
    p += vec3(-.34,.2,.02);
    d = smin2(d, ellip(p, vec3(.015,.025,.015)), .035);
    p = pp;
    p += vec3(-.37,.18,.03);
    pR(p.xz, .5);
    pR(p.yz, -.4);
    d = smin(d, ellip(p, vec3(.01,.03,.015)), .015);

    return d;
}
float sstep(float t) {
	return sin(t * PI - PI / 2.) * .5 + .5;
}
float sphere(vec3 x, vec3 pos, float r)
{
    return length(x - pos) - r;
}
float bulb(vec3 pos)
{
    vec3 x = pos;
    float p = 2.;
    float dz = 1.0;
    float r = 0.0;
    for(int i = 0; i < 10; ++i)
    {
        r = length(x);
        if (r > 4.5)
            break;
        float rp = pow(r, p);
        float phi = p*atan(x.y, x.x);
		float theta = p*acos(x.z/r);
        dz = pow(r, p - 1.0) * p * dz + 1.0;

        x = rp * vec3(sin(theta)*cos(phi), sin(theta)*sin(phi), cos(theta));  
        x += pos;
    }
    return 0.5*log(r)*r/dz;
}
float map(vec3 p) {

    float scale = 0.8;
    float s = .2;
    float ry = mix(sstep(sstep(sstep(mod(iTime * s, 1.)))), mod(iTime * s, 1.), .3) * PI * 2.;
    float rx = sin(iTime * .33) * .2;

    if (iMouse.z > 0. && iMouse.w > 0.) {
        ry = (.5 - iMouse.x / iResolution.x) * PI * 2.;
    	rx = (.5 - iMouse.y / iResolution.y) * PI / 2.;
        scale = 0.8;
    }

    pR(p.yz, rx);
    pR(p.xz, ry);
    p /= scale;

    //float s1 = sphere(p, vec3(0,1.0,0), 0.2);

    vec3 bp = p;
    pR(bp.xy, PI2);
    pR(bp.xz, -0.31);
    bp -= vec3(0.3, 0, -0.6);
   	float lf = min(bulb(bp), mHead(p*0.75));
   	return lf * scale;
}
vec3 normal (vec3 p)
{
 const float eps = 0.0001;

 return normalize
 ( vec3
 	( map(p + vec3(eps, 0, 0) ) - map(p - vec3(eps, 0, 0)),
 	  map(p + vec3(0, eps, 0) ) - map(p - vec3(0, eps, 0)),
	  map(p + vec3(0, 0, eps) ) - map(p - vec3(0, 0, eps))
 	)
 );
}
float march(vec3 pos, vec3 dir)
{
    float cd = 0.0, fd = 0.0;
    vec3 x;
    for(int i = 0; i < 200; ++i)
    {
        x = pos + dir * fd;
        cd = map(x);
        fd += cd;
        if(cd < 0.001)
            return fd;
        if(fd > 1000.0)
            return 0.0;
    }
    pos = normalize(x);
    return 0.0;
}
float getao (vec3 pos, vec3 normal)
{
	return clamp(map(pos+normal*0.01)/0.01, 0.0, 1.0);
}
vec3 rayDirection(float fieldOfView, vec2 size, vec2 fragCoord) {
    vec2 xy = fragCoord - size / 2.0;
    float z = size.y / tan(radians(fieldOfView) / 2.0);
    return normalize(vec3(xy, -z));
}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    vec3 dir = rayDirection(45.0, iResolution.xy, fragCoord);
    vec3 eye = vec3(0.0, 0.0, 5.0);
    float l = march(eye, dir);
    vec3 n = vec3(0);
    vec3 c = vec3(0);
    if(l > 0.0)
    {
        vec3 p = eye + dir * (l-0.005);
    	n = normal(p);
        float lam = dot(n, vec3(0.707, 0.707, 0.707));
        float ao = getao(p, n);
    	c = vec3(max(lam, 0.3));
        c *= lerp3(vec3(0.6, 0.6, 0.9), vec3(0.9, 0.6, 0.4), abs(dot(vec3(0,1.,0), n))) * ao;
    }
    else
        c = lerp3(vec3(0.6, 0.6, 0.9), vec3(0.9, 0.6, 0.4), abs(dot(vec3(0,1.,0), dir)));
    // Output to screen
    fragColor = vec4(c, 1.0);
}

// Les formes - https://iquilezles.org/www/articles/distfunctions/distfunctions.htm
float direction = 1.0;

vec3 rotatingColor (float time) {
    float pi = 3.141592;
    float demiPi = pi / 2.0;

    vec3 color = vec3(0.0);
    vec3 index = vec3(mod(time, pi + demiPi) - demiPi,
                      mod(time + demiPi, pi + demiPi) - demiPi,
                      mod(time + pi, pi + demiPi) - demiPi);//Goes from -1 to 2

    if (index.x < demiPi)
    color.x = abs(cos(index.x));

    if (index.y < demiPi)
    color.z = abs(cos(index.y));

    if (index.z < demiPi)
    color.y = abs(cos(index.z));

	return color;
}


