#define pi 3.14159265359
float sdOctahedron( in vec3 p, in float s)
{
    p = abs(p);
    float m = p.x+p.y+p.z-s;
    vec3 q;
         if( 3.0*p.x < m ) q = p.xyz;
    else if( 3.0*p.y < m ) q = p.yzx;
    else if( 3.0*p.z < m ) q = p.zxy;
    else return m*0.57735027;

    float k = clamp(0.5*(q.z-q.y+s),0.0,s); 
    return length(vec3(q.x,q.y-s+k,q.z-k)); 
}
float opTwist(in vec3 p, float twist, float sc)
{
    float c = cos(twist*p.y);
    float s = sin(twist*p.y);
    mat2  m = mat2(c,-s,s,c);
    vec3  q = vec3(m*p.xz,p.y);
    return sdOctahedron(q, sc);
}
vec3 reflplain(vec3 p, vec4 n)
{
    vec3 ac = (n.xyz * n.w);
	if(dot(p, ac) >= 0.0)
		 return vec3(0);
	vec3 a = p - ac;
	float dist = dot(a, n.xyz);
	vec3 pp = p + (n.xyz - 2.0*dist*n.xyz);
	return pp;
}
vec3 reflsphere(vec3 p, vec4 n)
{
	vec3 a = p - n.xyz;
	float dist = length(a);
   	return p - (a * (n.w/dist));
}
vec3 refltriangle(vec3 p, vec3 a, vec3 b, vec3 c)
{
    vec3 n = -normalize(cross(b - a, c - a)); 
	float dist = dot(p - a, n);
    if(dist >= 0.0)
		 return p;
	vec3 pp = p + (n - dist*n);
	if (dot(n, cross(b - a, a - pp)) > 0.0 && 
    dot(n, cross(c - b, b - pp)) > 0.0 && 
    dot(n, cross(a - c, c - pp)) > 0.0)
		return p + (n - 2.0*dist*n);
	return vec3(0);
}
float dot2( in vec3 v ) { return dot(v,v); }
float udTriangle( vec3 p, vec3 a, vec3 b, vec3 c )
{
    vec3 ba = b - a; vec3 pa = p - a;
    vec3 cb = c - b; vec3 pb = p - b;
    vec3 ac = a - c; vec3 pc = p - c;
    vec3 nor = cross( ba, ac );

    return sqrt(
    (sign(dot(cross(ba,nor),pa)) +
     sign(dot(cross(cb,nor),pb)) +
     sign(dot(cross(ac,nor),pc))<2.0)
     ?
     min( min(
     dot2(ba*clamp(dot(ba,pa)/dot2(ba),0.0,1.0)-pa),
     dot2(cb*clamp(dot(cb,pb)/dot2(cb),0.0,1.0)-pb) ),
     dot2(ac*clamp(dot(ac,pc)/dot2(ac),0.0,1.0)-pc) )
     :
     dot(nor,pa)*dot(nor,pa)/dot2(nor) );
}
