}
#define PI_2 1.57079632679
#define PI 3.14159265359
float fsinf(float x)
{
	float s = sign(mod((x - PI_2) * 0.5, PI) - PI_2);
	x = (mod(x - PI_2, PI) - PI_2) * 0.0123456790123;
	x = 4.0*x*x*x - 3.0*x;
	x = 4.0*x*x*x - 3.0*x;
	x = 4.0*x*x*x - 3.0*x;
	x = 4.0*x*x*x - 3.0*x;
	return x * s;
}
float wave(vec3 p, vec3 location)
{
    p = p - location;
    float prefl = sqrt(dot(p, p)) * 10.0; 
    return fsinf(prefl)/prefl;
}
float reflections(vec3 p, vec4 n)
{
    float b4 = dot(p, n.xyz) + n.w;
    vec3 rp = p;
    float dt = 20.*sin(iTime*0.5);
    p.y -= wave(p, vec3(0,0,dt));
    rp.xz = reflsphere(p, vec4(0.0, -2.0, 5.0, 1.0)).xz;
    if(rp.xz != vec2(0))
    	p.y -= wave(rp, vec3(0,0,dt));
    rp.xz = reflplain(p, vec4(0.0, 0.0, 1.0, 1.0)).xz;
    if(rp.xz != vec2(0))
    	p.y -= wave(rp, vec3(0,0,dt));
    float cdt = 5.0 * cos(iTime * 0.5);
    rp.xz = refltriangle(p, vec3(-2.5, -2.5, cdt), vec3(0, 2.5, cdt), vec3(2.5, -2.5, cdt)).xz;
    if(rp.xz != vec2(0))
    	p.y -= wave(rp, vec3(0,0,dt));
    p.y -= wave(p, vec3(0,0,dt));
    return (dot(p, n.xyz) + n.w);
}
float rplain(vec3 p, vec4 n)
{
    return abs(dot(p,n.xyz) + n.w);
}
float map(vec3 position)
{
    vec3 sp = position - vec3(3.14159265359, 0, 0);
    float r = reflections(position, vec4(0,1,0, 2.0));
    float s = sphere(position, vec3(0, -2., 5.0), 1.0);
	float o = sdOctahedron(position, 0.2);
    float d = -cos(iTime * 2.0);
    float dt = 5.0 * cos(iTime * 0.5);
    float t = udTriangle(position, vec3(-2.5, -2.5, dt), vec3(0, 2.5, dt), vec3(2.5, -2.5, dt));
    float a = rplain(position, vec4(0.0, 0.0, 1.0, 0.0));
    if(d < 0.0)
    	return min(min(min(s, r), o), t);
    else
		return min(min(min(min(s, r), o), a), t);
}
vec3 normal (vec3 p)
{
 const float eps = 0.0001;

 return normalize
 ( vec3
 	( map(p + vec3(eps, 0, 0) ) - map(p - vec3(eps, 0, 0)),
 	  map(p + vec3(0, eps, 0) ) - map(p - vec3(0, eps, 0)),
	  map(p + vec3(0, 0, eps) ) - map(p - vec3(0, 0, eps))
 	)
 );
}
float march(vec3 pos, vec3 dir)
{
    float cd = 0.0, fd = 0.0;
    for(int i = 0; i < 1000; ++i)
    {
        cd = map(pos + dir * fd);
        fd += cd;
        if(fd > 100.0)
            return 0.0;
        if(cd < 0.0001)
            return fd;
    }
    return 0.0;
}
float getao (vec3 pos, vec3 normal)
{
	return clamp(map(pos+normal*0.2)/0.2, 0.0, 1.0);
}
vec3 rayDirection(float fieldOfView, vec2 size, vec2 fragCoord) {
    vec2 xy = fragCoord - size / 2.0;
    float z = size.y / tan(radians(fieldOfView) / 2.0);
    return normalize(vec3(xy, -z));
}
mat4 viewMatrix(vec3 eye, vec3 center, vec3 up) {
    // Based on gluLookAt man page
    vec3 f = normalize(center - eye);
    vec3 s = normalize(cross(f, up));
    vec3 u = cross(s, f);
    return mat4(
        vec4(s, 0.0),
        vec4(u, 0.0),
        vec4(-f, 0.0),
        vec4(0.0, 0.0, 0.0, 1)
    );
}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec3 viewDir = rayDirection(45.0, iResolution.xy, fragCoord);
	vec3 eye = vec3(30.0, 10.0, 30.0);
    mat4 viewToWorld = viewMatrix(eye, vec3(0.0, 0.0, 0.0), vec3(0.0, 1.0, 0.0));
    vec3 worldDir = (viewToWorld * vec4(viewDir, 0.0)).xyz;
    float l = march(eye, worldDir);
    vec3 n = vec3(0);
    vec3 c = vec3(0);
    if(l != 0.0)
    {
        vec3 p = eye + worldDir * (l - 0.005);
    	n = normal(p);
        float ao = getao(p, n);
    	c = ao * (vec3(min(max(dot(n, vec3(0.707, 0.707, 0.707)),0.0),0.9)) + vec3(0.15));
    }
    // Output to screen
    fragColor = vec4(c,1.0);
}

float DistLine(vec3 camera, vec3 cameraDistToPixel, vec3 point) {
	return length(cross(point-camera, cameraDistToPixel))/length(cameraDistToPixel);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    // make origin in center of the screen
    uv -= .5;

    // adjusting screen to aspect ratio
    uv.x *= iResolution.x/iResolution.y;

    vec3 camera = vec3(.0, .0, -2.0);
    vec3 cameraDistToPixel = vec3(uv.x, uv.y, 0.0) - camera;

    float time = iTime;

    vec3 point = vec3(sin(time), .0, 3.*cos(time)+2.);
    float distanceToPoint = DistLine(camera, cameraDistToPixel, point);
    if (distanceToPoint > .1) {
    	distanceToPoint = 0.0;
    } else {
    	distanceToPoint = 1.0;
    }
    // Time varying pixel color
    vec3 col = 0.5 + 0.5*cos(iTime+uv.xyx+vec3(0,2,4));

    // Output to screen
    fragColor = vec4(distanceToPoint, col);
}

#define PI 3.14159265359

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
        vec2 aspect = iResolution.xy / iResolution.y;
        vec2 p = ( 20.0 * fragCoord.xy / iResolution.y ) - 10.0 * aspect;

        float s = 0.0, t = 0.1 * iTime;

        for( int k = 1; k < 9; k++)
        {
            // adapted from Fabrice Neyret's "smoothfloor", https://www.shadertoy.com/view/4t3SD7
            float q = abs(mod(0.5 * t + 5.5, 12.0) - 6.0) - 3.5;
            float mm = floor(q);

            mm += smoothstep(0.0, 1.0, 10.0 * (q - mm) - 9.0) + 7.0;

            float th = PI * float(k)/mm;
            s += cos((2.5 + 1.5 * cos(2.0 * t)) * dot(p, vec2(cos(th), -sin(th))) + 4.5 * t) * smoothstep(-0.05, 0.0, mm - float(k));
        }

        s = ((int(floor(s)) & 1) == 1) ? 1.0 - fract(s): fract(s);
        vec3 col = 0.5 + 0.5 * tanh(1.0 - cos(PI * (2.0 * s + vec3(3, 1, -1)/3.0)));

        fragColor = vec4( col, 1.0 );
}

