float wave(vec2 st, float c, float s) {
    float f = smoothstep(c - s, c, st.y) * smoothstep(c, c + s, st.y);
	return 1. - clamp(f, 0., 1.);
}

float waves(vec2 st, float i) {
	float off = -.04 + i * .075;
    float wave1 = wave(
        st + vec2(0., -off + noise(st * 5. + iTime * .6) * .05),
        sin((st.x + iTime * .15 + .00) * 20.) * .01,
        .01
    );
    float wave2 = wave(
        st + vec2(0., off + noise(st * 5. + iTime * .6) * .05),
        sin((st.x + iTime * .15 + .15) * 20.) * .01,
        .01
    );
    return wave1 * (1. - wave2);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy - .5;

    vec3 col = vec3(0.);

    for(int i = 0; i < 16; i++) {
        vec3 c = hsv2rgb(iTime * .15 + float(i) * .06, .9, .8) * .105;
        float w = waves(uv, float(i));
        col += c * w;
    }

    fragColor = vec4(col,1.0);
}

