    return v*r;
}

vec3 calculateBezierPoint(bezier bz, float t)
{
  float oneMt = 1.0 - t;

  return bz.p0 * pow(oneMt,3.0) +
     3.0 *bz.p1*t*pow(oneMt,2.0) +
       3.0*bz.p2*t*t*oneMt +
         bz.p3*t*t*t;
}

float sdf_sphere(vec3 p, vec3 orig, float rad)
{
  return distance(p, orig) - rad;
}

float sdf_torus(vec3 p, vec3 orig, vec2 size)
{
  vec3 pt = p-orig;
  vec2 q = vec2(length(pt.xz)-size.x,pt.y);
  return length(q)-size.y;
}

float sdf_union(float d1, float d2)
{
  return min(d1,d2);
}

vec3 sdf_torus_normal(vec3 p, vec3 o, vec2 s)
{
  vec3 xeps = vec3(EPS, 0.0, 0.0);
  vec3 yeps = vec3(0.0, EPS, 0.0);
  vec3 zeps = vec3(0.0, 0.0, EPS);
  vec3 n = vec3(sdf_torus(p+xeps, o, s) - sdf_torus(p-xeps, o, s),
    sdf_torus(p+yeps, o, s) - sdf_torus(p - yeps, o, s),
    sdf_torus(p+zeps, o, s) - sdf_torus(p- zeps, o, s));
  return normalize(n);
}


vec2 render(vec2 uv, vec3 orig, vec3 dir)
{
  float gradient = clamp(0.0,1.0, sin(uv.y));
  vec3 p = orig;

vec3 light = vec3(5.0*sin(iTime), 5.0*cos(iTime),0.0);
  int step = 0;
  float totalDist = 0.0;
  while (step < marchSteps && totalDist < marchDist)
  {
    float d = sdf_torus(p, vec3(0.0), vec2(2.0, 1.0));
    //float d = sdf_sphere(p, vec3(0.0), 2.0);
    if (d < EPS)
    {
      vec3 n = sdf_torus_normal(p, vec3(0.0), vec2(2.0, 1.0));
      return vec2(dot(n, p-light)*0.2 + 0.2,totalDist+d);
    }
    p += dir * d;
    step++;
    totalDist += d;
  }

  return vec2(gradient,0.0);
}

vec4 renderAsDot(vec2 uv, vec2 center, float angle, vec3 orig, vec3 vec)
{
  float cosA = cos(angle);
  float sinA = sin(angle);
  mat2 rot = mat2(cosA, -sinA, sinA, cosA);
  uv -= center;
  vec2 rdrV = render(uv, orig, vec);
  float curSize = mix(minDotSize, maxDotSize, rdrV.x);
  uv = uv * rot;
  vec2 uvMod = mod(uv, dotSpacing);
  float size = minDotSize;
  return vec4(sat(vec3(-(length(uvMod-(dotSpacing/2.0))-curSize)/size)*5.),rdrV.y);
}

vec4 renderBezier(vec2 uv, vec3 orig, vec3 dir)
{
  bezier bz;
  vec3 bezierPts[bezierSphCnt];
  float distSph[bezierSphCnt];
  vec3 p = orig;
  vec3 off = vec3(2.0,0.0,0.0);
  float cosA = cos(-0.3);
  float sinA = sin(-0.3);
  float exce = 1.9;
  mat2 rot = mat2(cosA, -sinA, sinA, cosA);
  bz.p0 = off + vec3(sin(iTime), cos(iTime), 0.0)*exce;
  bz.p1 = off + vec3(sin(iTime+1.0), cos(iTime+1.0), 0.0)*exce;
  bz.p2 = off + vec3(sin(iTime+2.0), cos(iTime+2.0), 0.0)*exce;
  bz.p3 = off + vec3(sin(iTime+3.0), cos(iTime+3.0), 0.0)*exce;

  vec2 p02 = bz.p0.xz * rot;
  vec2 p12 = bz.p1.xz * rot;
  bz.p0 = vec3(p02.x, bz.p0.y, p02.y);
  for (int iBz = 0; iBz < bezierSphCnt; ++iBz)
    bezierPts[iBz] = calculateBezierPoint(bz, float(iBz) / float(bezierSphCnt));

  int step = 0;
  float totalDist = 0.0;
  while (step < marchSteps && totalDist < marchDist)
  {
    float nearest = -1.0;
    int idxHit = -1;
    for (int iBz = 0; iBz < bezierSphCnt; ++iBz)
    {
      float d = sdf_sphere(p, bezierPts[iBz], 0.2);
      if (idxHit == -1 || (d < nearest))
      {
        nearest = d;
        idxHit = iBz;
      }
    }
    if (nearest < EPS)
    {
    //  vec3 n = sdf_torus_normal(p, vec3(0.0), vec2(2.0, 1.0));
      return vec4(bezierCol,totalDist+nearest);//dot(n, p-light)*0.2 + 0.2;
    }
    p += dir * nearest;
    step++;
    totalDist += nearest;
  }
  return vec4(0.0);
}

vec3 renderAnim(vec2 uv, vec2 center, vec3 orig, vec3 vec)
{
  float theta = 3.1415927 * sin(0.1*iTime);
  float way = (sin(iTime)-sin(iTime+0.1))*0.1;
  vec4 outCol = renderAsDot(uv,center,theta, orig, vec);

  vec4 bzCol = renderBezier(uv - center, orig, vec);
  if (outCol.w < EPS)
    return bzCol.xyz;
  if(bzCol.w < EPS || outCol.w < bzCol.w)
     return outCol.xyz;
  return bzCol.xyz;
}

vec3 negate(vec3 c)
{
