void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
  vec2 uv = fragCoord.xy / iResolution.xy;
  float ratio = iResolution.x/iResolution.y;
  uv = vec2(uv.x * ratio, uv.y);


  vec3 origin =vec3(0.0, 10.0, 0.0);
  vec3 vec = vec3(1.0, 0.0, 0.0);
  vec3 rightVec = vec3(0.0,0.0,-1.0);
  vec3 upVec = vec3(0.0,1.0,0.0);

  float yaw = 3.1415927/2.0;
  float pitch = 0.0;

  float cosYaw = cos(yaw);
  float sinYaw = sin(yaw);
  float cosPitch = cos(pitch);
  float sinPitch = sin(pitch);

  mat3 rotY = mat3(cosPitch,  0.0, sinPitch,
                   0.0, 1.0, 0.0,
                   -sinPitch,  0.0, cosPitch);
  mat3 rotZ = mat3(cosYaw, -sinYaw, 0.0,
                   sinYaw, cosYaw, 0.0,
                   0.0,  0.0, 1.0);
  vec2 center = vec2(0.5*ratio, 0.5);
  vec2 cuv = uv -center;
    cuv *= sin(iTime)*0.5+1.;
    cuv = rot(cuv, -iTime*0.5);
  vec = rotZ * (rotY * (vec + rightVec * cuv.x + upVec * cuv.y));
  vec = normalize(vec);


  vec3 col = renderAnim(uv,center, origin, vec);
    col += vec3(0.22,0.12,0.42)*pow(1.-length(cuv),0.5);
  fragColor = vec4((col), 1.0);
}

