//------------------------------------------------------------------------
// Modelling 
//------------------------------------------------------------------------

float sdSphere( vec3 p, float s )
{
  return length(p)-s;
}

float sdEllipsoid(const in  vec3 p, const in vec3 r)
{
  return (length(p / r) - 1.0) * min(min(r.x, r.y), r.z);
}

float sdCappedCylinder( vec3 p, vec2 h )
{
  vec2 d = abs(vec2(length(p.xz),p.y)) - h;
  return min(max(d.x,d.y),0.0) + length(max(d,0.0));
}

float dot2( in vec2 v )
{
    return dot(v,v);
}

float sdCappedCone( in vec3 p, in float h, in float r1, in float r2 )
{
    vec2 q = vec2( length(p.xz), p.y );
    vec2 k1 = vec2(r2,h);
    vec2 k2 = vec2(r2-r1,2.0*h);
    vec2 ca = vec2(q.x-min(q.x,(q.y < 0.0)?r1:r2), abs(q.y)-h);
    vec2 cb = q - k1 + k2*clamp( dot(k1-q,k2)/dot2(k2), 0.0, 1.0 );
    float s = (cb.x < 0.0 && ca.y < 0.0) ? -1.0 : 1.0;
    return s*sqrt( min(dot2(ca),dot2(cb)) );
}

float sdCapsule( vec3 p, vec3 a, vec3 b, float r )
{
    vec3 pa = p - a, ba = b - a;
    float h = clamp( dot(pa,ba)/dot(ba,ba), 0.0, 1.0 );
    return length( pa - ba*h ) - r;
}

mat2 rot( float th ){ vec2 a = sin(vec2(1.5707963, 0) + th); return mat2(a, -a.y, a.x); }

float smin( float a, float b, float k )
{
    float h = clamp( 0.5+0.5*(b-a)/k, 0.0, 1.0 );
    return mix( b, a, h ) - k*h*(1.0-h);
}

vec2 opUnionRound(const in vec2 a, const in vec2 b, const in float r)
{
    vec2 res = vec2(smin(a.x,b.x,r),(a.x<b.x) ? a.y : b.y);
    return res;
}

vec2 opS( vec2 d1, vec2 d2 )
{ 
    return (-d1.x>d2.x) ? vec2(-d1.x, d1.y): d2;
}

float TimerInOut(vec4 v)
{
    return smoothstep(v.y,v.y+v.w,v.x) - smoothstep(v.z-v.w,v.z,v.x);
}

float onion( in float d, in float h )
{
    return abs(d)-h;
}

// model
vec2 pot(vec2 res, vec3 p)
{
    float rimh = 0.05;
    float poth = 1.7;
   	float d2 = sdCappedCylinder(p+vec3(0.0,rimh,0.0),vec2(2.1,rimh))-0.05;
   	float d3 = sdCappedCone(p+vec3(0.0,poth,0.0), poth, 1.6,2.1);
    d2 = onion(d2-0.8,0.02);
    d3 = onion(d3-0.8,0.02);
    d2 = smin(d2,d3,0.15);
    d2 = max( d2, p.y+0.05 );
	vec2 res1 = vec2(d2,3.0);
    // dirt
   	float d4 = sdCappedCylinder(p+vec3(0.0,0.4,0.0),vec2(2.65,0.02));
    vec2 res2 = vec2(d4,1.0);
	res1 = opUnionRound(res1,res2,0.6);     
 	res = opUnionRound(res,res1,0.15);  
    return res;
}

float mouthblend = 0.0;
float blink = 0.0;

vec2 sdCactusXZ(vec3 p, float w, float h, float offset)
{
    float d1 = sdEllipsoid(p,vec3(w,h,w));
	d1+=abs(sin(atan(p.x/p.z)*8.0)*(offset*(h-abs(p.y))));
    return vec2(d1,2.0);
}
vec2 sdCactusXY(vec3 p, float w, float h, float offset)
{
    float d1 = sdEllipsoid(p,vec3(h,w,w));
	d1+=abs(sin(atan(p.z/p.y)*8.0)*(offset*(h-abs(p.x))));
    return vec2(d1,2.0);
}

vec2 Arm(vec3 p)
{
	vec2 a1 = sdCactusXY(p-vec3(1.4,0.2,0.0),0.35,1.0,0.0125);
	vec2 a2 = sdCactusXZ(p-vec3(2.1,1.0,0.0),0.35,1.0,0.0125);
    return opUnionRound(a1,a2, 0.15);
}


vec2 doModel( vec3 p )
{
    p-=vec3(0.0,0.7,0.0);

    // body
	vec2 res = sdCactusXZ(p,1.3,2.6, 0.0225);
    // arms
 	res = opUnionRound(Arm(p),res, 0.15);    
    vec3 p2 = p;
    p2.x = -p2.x;
 	res = opUnionRound(Arm(p2-vec3(0.0,0.7,0.0)),res, 0.15);

    // mouth
    vec3 q = p;
    q.x = abs(q.x);
    q.yz *= rot(-pow(q.x, 2.8));
    q.z -= 1.25;
    vec2 mouth1 = vec2(sdCapsule(q, vec3(0.35, 0.0, 0.0), vec3(-0.35, 0.0, 0.0), .025)-0.115, 4.0);

    mouth1.x = mix(sdSphere(q,0.25),mouth1.x,mouthblend);
    res = opS(mouth1,res);

	// eyes    
    p.x = abs(p.x);
    float d2 = sdSphere(p+vec3(-0.3,-1.0,-1.1),0.2);
    res = opUnionRound(vec2(d2,5.0),res, 0.025);
    // pot
	res = pot(res,p+vec3(0.0,1.1,0.0));
    return res;
}


//------------------------------------------------------------------------
// Material 
//
// Defines the material (colors, shading, pattern, texturing) of the model
// at every point based on its position and normal.
//------------------------------------------------------------------------
// c = colour index (added by del for some materials)
// c.a == specular val fudged in...

vec4 EyeTest(vec3 n)
{
    n = normalize( n );
    n.zy *= rot(sin(iTime*1.15)*0.3);
    vec2 uv = vec2(atan(n.z,n.x) / TAU, asin(n.y) / TAU);
    uv.x -= 0.25;
    float d1 = length(uv)-0.02;	
    float d2 = smoothstep(0.0,0.02, d1-0.05);
    d1 = smoothstep(0.0,0.02, d1);
    vec3 col = vec3(0.05,0.15,0.4)*d1;
    col = mix(col,vec3(0.24,0.24,0.24),d2);
    return blink > 0.0 ? vec4(col.xyz,2.0) : vec4(0.03,0.2,0.05,0.25);
}

float hash(vec3 p)
{
    p  = fract( p*0.3183099+.1 );
	p *= 17.0;
    return fract( p.x*p.y*p.z*(p.x+p.y+p.z) );
}

float noise( in vec3 x )
{
    vec3 p = floor(x);
    vec3 f = fract(x);
    f = f*f*(3.0-2.0*f);

    return mix(mix(mix( hash(p+vec3(0,0,0)), 
                        hash(p+vec3(1,0,0)),f.x),
                   mix( hash(p+vec3(0,1,0)), 
                        hash(p+vec3(1,1,0)),f.x),f.y),
               mix(mix( hash(p+vec3(0,0,1)), 
                        hash(p+vec3(1,0,1)),f.x),
                   mix( hash(p+vec3(0,1,1)), 
                        hash(p+vec3(1,1,1)),f.x),f.y),f.z);
}
const mat3 m = mat3( 0.00,  0.80,  0.60,
                    -0.80,  0.36, -0.48,
                    -0.60, -0.48,  0.64 );


vec4 doMaterial( in vec3 pos, in vec3 nor,float c )
{
    if (c<=2.0)
    {
		vec3 q = pos*12.0;
		float f  = 0.5*noise( q ); q = m*q;
		f += 0.2500*noise( q ); q = m*q;
		f += 0.1250*noise( q ); q = m*q;
		f += 0.0625*noise( q ); q = m*q;        
        if (c<=1.0)
			return vec4(0.08, 0.05, 0.03,0.1)*f;	// dirt
        f=0.5+(f*0.4);
        return vec4(0.03,0.2,0.05,0.5)*f;			// cactus
    }
    else if (c<=3.0)
    {
		return vec4(0.2, 0.06, 0.03,0.4);	// pot
    }
    else if (c<=4.0)
    {
		return vec4(0.1, 0.08, 0.03, 0.8);	// mouth
    }

    return EyeTest(nor);
}

//------------------------------------------------------------------------
// Lighting
//------------------------------------------------------------------------
float calcSoftshadow( in vec3 ro, in vec3 rd );

vec3 doLighting( in vec3 pos, in vec3 nor, in vec3 rd, in float dis, in vec4 mat )
{
    vec3 lin = vec3(0.0);

    // key light
    //-----------------------------
    vec3  lig = normalize(vec3(0.7,0.875,0.89));		// dir
    float dif = max(dot(nor,lig),0.0);
    float sha = 0.0;
    if( dif>0.01 )
        sha=calcSoftshadow( pos+0.01*nor, lig );
    lin += dif*vec3(4.00,4.00,4.00)*sha;

	float spec = pow(dif, 160.0) *mat.a;

    // ambient light
    //-----------------------------
    lin += vec3(0.50,0.50,0.50);

    // surface-light interacion
    //-----------------------------
    vec3 col = mat.xyz*lin;
    col+=spec;

    // fog    
    //-----------------------------
	col *= exp(-0.002*dis*dis);

    return col;
}

vec2 calcIntersection( in vec3 ro, in vec3 rd )
{
	const float maxd = 20.0;           // max trace distance
	const float precis = 0.001;        // precission of the intersection
    float h = precis*2.0;
    float t = 0.0;
    vec2 res = vec2(-1.0,0.0);
    float c = 0.0;

    for( int i=0; i<60; i++ )
    {
        if( h<precis||t>maxd ) break;
        vec2 res2 = doModel( ro+rd*t );
	    h = res2.x;
        c = res2.y;

        t += h*0.95;
    }

    if( t<maxd )
    {
        res.x = t;
        res.y = c;
    }
    return res;
}

vec3 calcNormal( in vec3 pos )
{
    const float eps = 0.002;             // precision of the normal computation

    const vec3 v1 = vec3( 1.0,-1.0,-1.0);
    const vec3 v2 = vec3(-1.0,-1.0, 1.0);
    const vec3 v3 = vec3(-1.0, 1.0,-1.0);
    const vec3 v4 = vec3( 1.0, 1.0, 1.0);

	return normalize( v1*doModel( pos + v1*eps ).x + 
					  v2*doModel( pos + v2*eps ).x + 
					  v3*doModel( pos + v3*eps ).x + 
					  v4*doModel( pos + v4*eps ).x );
}

float calcSoftshadow( in vec3 ro, in vec3 rd )
{
    float res = 1.0;
    float t = 0.0005;                 // selfintersection avoidance distance
	float h = 1.0;
    for( int i=0; i<40; i++ )         // 40 is the max numnber of raymarching steps
    {
        h = doModel(ro + rd*t).x;
        res = min( res, 64.0*h/t );   // 64 is the hardness of the shadows
		t += clamp( h, 0.02, 2.0 );   // limit the max and min stepping distances
    }
    return clamp(res,0.0,1.0);
}

mat3 calcLookAtMatrix( in vec3 ro, in vec3 ta, in float roll )
{
    vec3 ww = normalize( ta - ro );
    vec3 uu = normalize( cross(ww,vec3(sin(roll),cos(roll),0.0) ) );
    vec3 vv = normalize( cross(uu,ww));
    return mat3( uu, vv, ww );
}

vec3 background(vec2 pos)
{
    pos.x = abs(pos.x);
    float d = length(pos*pos);
    pos+=vec2(0.5);
	float vv = pos.y*pos.y;
	vv+=sin(exp(1.0-pos.x));
	float v = sin(sin(pos.x*16.0)+(vv+iTime*.1) * 42.0)+0.95;
    v*=0.9-d*d;

    v = clamp(v,0.1,1.0);

	return vec3( v*1.1,0.05+v*.6, 0.2+v*0.45);    
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 p = (-iResolution.xy + 2.0*fragCoord.xy)/iResolution.y;
    vec2 m = iMouse.xy/iResolution.xy;

    // anims
    blink = step(sin(iTime * 2.8 + cos(iTime * 2.0) * 2.0), 0.98);
	mouthblend = TimerInOut(vec4(mod(iTime,4.5),0.0,3.7,0.3));

    // camera movement
    vec3 ro, ta;
    doCamera( ro, ta, iTime, m );

    // camera matrix
    mat3 camMat = calcLookAtMatrix( ro, ta, sin(iTime)*0.25 );

	// create view ray
	vec3 rd = normalize( camMat * vec3(p.xy,2.0) ); // 2.0 is the lens length

    //-----------------------------------------------------
	// render
    //-----------------------------------------------------
  	//vec3 col = mix( vec3(0.2, 0.2, 0.5), vec3(0.5, 0.7, 1.0), fragCoord.y / iResolution.y );
    vec3 col = background((fragCoord.xy - 0.5 * iResolution.xy) / iResolution.y);

	// raymarch
    vec2 res = calcIntersection( ro, rd ); 
    float t = res.x;
    if( t>-0.5 )
    {
        // geometry
        vec3 pos = ro + t*rd;
        vec3 nor = calcNormal(pos);

        // materials
        vec4 mat = doMaterial( pos, nor, res.y );

        col = doLighting( pos, nor, rd, t, mat );
	}

	//-----------------------------------------------------
	// postprocessing
    //-----------------------------------------------------
    // gamma
	col = pow( clamp(col,0.0,1.0), vec3(0.4545) );

    fragColor = vec4( col, 1.0 );
}

#define PI  3.141592
#define TAU 6.283184


vec2 cs(float a){
    return vec2(cos(a),sin(a));
}

vec2 rot(vec2 v, float a) {
    vec2 c = cs(a);
    return v * mat2x2(c.x, -c.y, c.y, c.x);
}

vec2 sp(float t){
    return vec2(floor(t), fract(t));
}

float rand(vec2 co){
  return fract(sin(dot(co.xy ,vec2(12.9898,78.233))) * 43758.5453);
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord*2.-iResolution.xy)/iResolution.y;
	fragColor.b=0.;
    for(float i = 0. ; i < 6. ; i++) {
	    vec2 t = sp(iTime*.15 - i*.1);
	    vec4 c = vec4(
	        rand(vec2(t.x, i+0.))*2.0-1.0,
	        rand(vec2(t.x, i+1.))*2.0-1.0,
	        rand(vec2(t.x, i+2.))*0.5+0.5,
            rand(vec2(t.x, i+3.))*2.0-1.0
	    );

        c.w = mod(t.x, 2.)*2.-1.;

	    uv -= c.xy;
        if(length(uv)<c.z) {
	        uv = rot(uv, smoothstep(0.,1.,t.y)*TAU*sign(c.w));
        } else if(length(uv)<c.z+.01) {
            fragColor.b+=sin(t.y*PI);
        }
        uv += c.xy;
    }

    fragColor = texture(iChannel0, uv)+fragColor.b;
    //fragColor.g =1.0-20.*rand(floor(uv*150.0));
    //fragColor = max(fragColor, vec4(fragColor.g));
    return;
}

