// Limited the number of reflection bounces to 20 for safety.
// Update: Fixed shadow march logic. I was trying to march from the light to the surface instead of from the surface towards the light

#define NUM_LIGHTS 1
#define AMBIENT_LIGHT 1.61
#define NUM_SPHERES	6

vec3 colours[9] = vec3[](vec3(0.2,0.4,0.6), vec3(0.0,0.6,0.13), vec3(0.5,0.5,0.55), vec3(0.8,0.7,0.2), vec3(0.18,0.64,0.38), vec3(0.75,0.51,0.1), vec3(0.95,0.41,0.51), vec3(0.3,0.4,0.8), vec3(0.2,0.6,0.6));
vec3 speculars[9] = vec3[](vec3(0.9,0.9,0.9),vec3(0.9,0.9,0.9),vec3(0.9,0.9,0.9), vec3(0.9,0.9,0.9), vec3(0.9,0.9,0.9), vec3(0.9,0.9,0.9), vec3(0.9,0.9,0.9), vec3(0.9,0.9,0.9), vec3(0.9,0.9,0.9));
float shine[9] = float[](10.0,10.0,10.0,10.0,10.0,10.0,10.0,10.0,10.0);
float reflectivity[9] = float[](0.4,0.3,0.85,0.65,0.65,0.65,0.75,0.75,0.5);

vec3 intensities[9] = vec3[](vec3(0.9,0.9,0.9),vec3(0.9,0.9,0.9),vec3(0.8,0.8,0.8), vec3(0.4,0.4,0.4), vec3(0.1,0.4,0.8), vec3(0.25,0.51,0.1), vec3(0.35,0.81,0.01), vec3(0.1,0.3,0.4), vec3(0.9,0.9,0.9));

// adapted sdf functions by iq
float sdfSphere(vec3 p, vec3 t, float r)
{
	return length(p-t)-r;   
}
float sdfBox( vec3 p, vec3 t, vec3 b )
{
  vec3 d = abs(p - t) - b;
  return length(max(d,0.0))
         + min(max(d.x,max(d.y,d.z)),0.0); // remove this line for an only partially signed sdf 
}
float sdfPlane( vec3 p, vec3 t, vec4 n )
{
  // n must be normalized
  return dot(p-t,n.xyz) + n.w;
}
float sdfTorus( vec3 p, vec3 tr, vec2 t )
{
	p = p-tr;
  vec2 q = vec2(length(p.xz)-t.x,p.y);
  return length(q)-t.y;
}
float opUnion( float d1, float d2 ) {  return min(d1,d2); }

float opSub( float d1, float d2 ) { return max(-d1,d2); }

float opInter( float d1, float d2 ) { return max(d1,d2); }
////////

// objects map
float distanceToClosest(vec3 p, inout int pick)
{
    float def = 500.0;
    float d = sdfPlane(p,vec3(0.0,-2.0,0.0),normalize(vec4(0.0,1.0,0.0,1.0)));
    pick = 1;

    if (d > def)
    {
      d = def;
      pick = 0;
    }

    float so = opUnion( sdfBox(p, vec3(0.0), vec3(4.0,6.0,1.0)), sdfBox(p, vec3(0.0), vec3(1.0,6.0,4.0)) );
    if (so < d)
    {
        pick = 2;
        d = so;
    }

    float offset = 0.0;
    float offset2 = 0.0;
    float offsetinc = 6.28318 / float(NUM_SPHERES);

	for (int s = 0; s < NUM_SPHERES; s ++)
    {
        so = sdfSphere(p,vec3(6.5 * cos(iTime+offset),0.75*cos(iTime*offset),6.5 * sin(iTime+offset)),1.0);
		offset += offsetinc;    
    	if (so < d)
    	{
        	pick = int(mod(float(s),float(6))+3.0);
        	d = so;
    	}

        so = sdfSphere(p,vec3(8.5 * cos(-iTime+offset2),0.95*cos(-iTime*offset2 * 0.33),8.5 * sin(-iTime+offset2)),1.2);
		offset2 += offsetinc;    
    	if (so < d)
    	{
        	pick = int(mod(float(s),float(6))+3.0);
        	d = so;
    	}

    }

    return d;

}

vec3 estimateNormal(vec3 p, inout int pick)
{
    const float EPSILON = 0.001;
 return normalize(vec3(
        distanceToClosest(vec3(p.x + EPSILON, p.y, p.z),pick) - distanceToClosest(vec3(p.x - EPSILON, p.y, p.z),pick),
        distanceToClosest(vec3(p.x, p.y + EPSILON, p.z),pick) - distanceToClosest(vec3(p.x, p.y - EPSILON, p.z),pick),
        distanceToClosest(vec3(p.x, p.y, p.z  + EPSILON),pick) - distanceToClosest(vec3(p.x, p.y, p.z - EPSILON),pick)
    ));   
}

vec3 getLightPosition(int l)
{
	vec3 lps[6] = vec3[](vec3(-8.0 * cos(iTime * 0.25),3.5,-8.0 * sin(iTime * 0.25)),
				vec3(-5.80 * sin(iTime*0.5),-5.5*cos(iTime*0.5)+4.95 ,5.5* cos(iTime * 0.5)),
                vec3(5.80 * sin(iTime*0.5),-5.5*cos(iTime*0.5)+4.95 ,-5.5* cos(iTime * 1.3)),
                vec3(-5.80 * sin(iTime*0.5),-5.5*cos(iTime*0.5)+4.95 ,-5.5* cos(iTime * 2.7)),
                vec3(-5.80 * sin(iTime*0.5),-5.5*cos(iTime*0.5)+4.95 ,-5.5* cos(iTime * 4.5)),
                vec3(-5.80 * sin(iTime*0.5),-5.5*cos(iTime*0.5)+4.95 ,-5.5* cos(iTime * 0.5)));
    if (l > 5)
    {
        return lps[0];
    }

    return lps[l];
}
// soft shadows from iq's tutorial
float shadowMarch( vec3 lightOrigin, vec3 surfacePoint )
{
    vec3 direction = normalize(lightOrigin-surfacePoint);
    float blendFactor = 128.18;
    int pickTarget = 0;
  	float res = 1.0;
    float ph = 1e20;
    float end = distance(surfacePoint,lightOrigin);
    for( float s=0.01; s < end; )
    {
        vec3 marchStep = surfacePoint + direction * s;
        float distNow = distanceToClosest(marchStep, pickTarget);
        if( distNow<0.00085)
            return 0.0;
        float y = distNow*distNow/(2.0*ph);
        float d = sqrt(distNow*distNow-y*y);
        res = min( res, blendFactor*d/max(0.0,s-y) );
        ph = distNow;
        s += distNow;
    }
    return res; 
}
vec3 lightSurfacePoint(vec3 eye, vec3 surfacePoint, vec3 surfaceNormal, float ambientLight, int materialPick)
{
	vec3 surfaceColour = vec3(0.0);
    float shadow = 1.0;

    for (int l = 0; l < NUM_LIGHTS; l ++)
    {
        vec3 lightPos = getLightPosition(l);
        vec3 N = surfaceNormal;
    	vec3 L = normalize(lightPos - surfacePoint);
    	vec3 V = normalize(eye - surfacePoint);
    	vec3 R = normalize(reflect(-L, N));

    	float dotLN = dot(L, N);
    	float dotRV = dot(R, V);
    	vec3 colour = colours[materialPick] * 0.01;
    	if (dotLN < 0.0) 
		{
        	// Light not visible from this point on the surface
        	colour =  colours[materialPick] * 0.01;
    	} 
    	else if (dotRV < 0.0) 
		{
        // Light reflection in opposite direction as viewer, apply only diffuse
        // component
        	colour = intensities[l] * (colours[materialPick] * dotLN);
            shadow = min(shadow, shadowMarch(lightPos, surfacePoint));
    	}
        else
        {
    		colour = intensities[l] * (colours[materialPick] * dotLN + speculars[materialPick] * pow(dotRV, shine[materialPick]));
            shadow = min(shadow, shadowMarch(lightPos, surfacePoint));
        }

        surfaceColour += colour;//phongLight((colours[materialPick]* shadow *ambientLight), lightSpec[l], 10.7, getLightPosition(l), lightIntensity[l], surfacePoint, normal, eye);
    }
	return surfaceColour * ambientLight * shadow;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // pixel coordinates (from -1 to 1)
    vec2 uv = (fragCoord/iResolution.xy)* 2.0 - 1.0;
	uv.x *= iResolution.x/iResolution.y;

    // camera setup and ray cast
    float zoom = 1.0;
    vec3 lookAt = vec3(0.0,0.5,0.0);
//    vec3 camOrigin = vec3(10.0 * cos(iTime * 0.1),6.5 * cos(iTime * 0.5)+4.2,-10.0 * sin(iTime * 0.1));
    vec3 camOrigin = vec3(10.0 * cos(iTime * 0.1),6.5 * cos(iTime * 0.5)+4.2,-10.0 * sin(iTime * 0.1));
	vec3 forwardVec = normalize(lookAt - camOrigin);
    vec3 rightVec = normalize(cross(vec3(0.0,1.0,0.0),forwardVec));
    vec3 upVec = cross(forwardVec,rightVec);

    vec3 centerVec = camOrigin + forwardVec * zoom;
    vec3 intersectVec = centerVec + uv.x * rightVec + uv.y * upVec;
    vec3 rayDirection = normalize(intersectVec - camOrigin);

    // config and work variables
    vec3 surfacePoint = vec3(0.0);
    vec3 col = vec3(0.1,0.3,0.44);

	float marchDistance = 0.0f;
    vec3 marchVec = camOrigin;
    int pickTarget = 0;
    vec3 surfaceNormal = vec3(0.0);

    const float MAX_DISTANCE = 500.0;
    const float CLOSE_ENOUGH = 0.001;

    vec3 marchStep = vec3(0.0);
   	float distNow = 0.0;

    vec3 colAccum = vec3(0.0);
	float bounceCount = 0.0;
	float refMixVal = 0.35; // don't start with a solid mix
//initialize with sky colour
    pickTarget = 0;
	colAccum =  colours[pickTarget] - rayDirection.y * 0.5;

	for (marchDistance = 0.0; marchDistance < MAX_DISTANCE;)
	{
		marchStep = marchVec + rayDirection * marchDistance;
	    distNow = distanceToClosest(marchStep, pickTarget);

		marchDistance += distNow;        
		if (distNow <= CLOSE_ENOUGH)
       	{
			// hit something!
   			surfaceNormal = estimateNormal(marchStep, pickTarget);
			// calculate colour accumulation
			colAccum = mix(colAccum,lightSurfacePoint(marchVec , marchStep, surfaceNormal, AMBIENT_LIGHT, pickTarget),
					clamp(reflectivity[pickTarget] + refMixVal + bounceCount * 0.1,0.0,1.0));
					//mix(reflectivity[pickTarget], clamp(0.0,1.0,refMixVal + bounceCount * 0.1), 0.5) );
            rayDirection = normalize(reflect((rayDirection ), surfaceNormal));
            bounceCount += 1.0;
            marchDistance = 0.01;
			// move camera origin to reflection location
			marchVec = marchStep; // + rayDirection * marchDistance;
		}

        if (marchDistance >= MAX_DISTANCE || bounceCount >= 20.0)
        {
          // sky colour
                int lastPick = pickTarget;
        		pickTarget = 0;
				colAccum = mix(colAccum,colours[pickTarget] - rayDirection.y * 0.5,
							clamp(reflectivity[lastPick]+bounceCount*0.1,0.0,1.0));

            	break;   
        }

	}

    col = colAccum;
    fragColor = vec4(col,1.0);
}

#define PI 3.14159265359
mat2 rotate2d(float _angle){
    return mat2(cos(_angle),-sin(_angle),
                sin(_angle),cos(_angle));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.x;
    uv.y+=0.2;
    float t = 3.7 + iTime*0.002;  
    float p = 20.0;

    vec2 m = iMouse.xy / iResolution.xy;
    m+=0.5;

    float angle = atan(m.y-m.x);

    uv -= vec2(0.5);
    uv = rotate2d( .35*PI ) * uv;
    uv += vec2(0.5);
    uv*=p;
   float e = t*floor(mod(uv.x,p));
   float f = t*floor(mod(uv.y,p));
    uv = rotate2d( sin(angle+e-f*1.5)*PI ) * uv;
    uv += vec2(0.15);
//    float k = floor(fract(uv.x-uv.y)*2.0);
    float k = (sin(uv.x*uv.y*1.3))*2.5;
    fragColor = vec4(k);
}

/// Ray_tracer v1

#define ITER 100
#define BILLBOARD_SIZE 5.0
#define NUMBER_OF_REFLECTIONS 3

struct ray_t{

    // origin of ray
    vec3 o;
    // direction of ray
    vec3 d;

};

struct sphere_t{

    // position in room
    vec3 pos;
    // color 
    vec3 col;
    // radius
    float r;
    // reflection value
    float ref;

};


sphere_t sphere[5];

/*
mat3 rotY (float ang) {
    return mat3(cos(ang) ,0.0, sin(ang),
                0.0, 1.0, 0.0,
                -sin(ang), 0.0, cos(ang));
}
*/


// returns the distance to the radius of the shpere
// if it is <0, then we are inside of that shere
float distance_from_sphere(vec3 pos, sphere_t sphere) {
 	return length(pos - sphere.pos) - sphere.r;  
}   

vec3 trace(ray_t r, vec3 cam) {

    vec3 current_position, current_color = vec3(0);
    float way_done = 0.0, dist, temp_dist; 
    int nearest_sphereN, refelctionN = 0;
    sphere_t s;


    for (int i=0; i<ITER; i++) {

        current_position = r.o + r.d * way_done;

        // find nearest sphere index and distance to it
        dist = 10000000.0;
        nearest_sphereN = 0;

        for (int j=0; j<sphere.length(); j++) {

            temp_dist = distance_from_sphere(current_position, sphere[j]);

            if (temp_dist < dist) {               
                dist = temp_dist;
                nearest_sphereN = j;                
            }
         }

         way_done += dist;

         // reflect
         if (dist < 0.001) {             


             current_color += sphere[nearest_sphereN].col*sphere[nearest_sphereN].ref;


             if (refelctionN++ >= NUMBER_OF_REFLECTIONS) break;

             //if (refelctionN > 1) current_color *= (current_color + oldCol) * 0.5;

             current_position = r.o + r.d * (way_done);

             way_done = 0.3;
             vec3 dir = reflect(r.d, normalize(current_position-sphere[nearest_sphereN].pos));
             r = ray_t (current_position, dir);
         }

    }

    if (current_color != vec3(0.0)) {

        vec3 norm = normalize(current_position-sphere[nearest_sphereN].pos);
        float shading = dot(normalize(vec3(-1.0, 1.0, 0.0)), norm);

        float shading1 = current_position.y > 0. ? 0.5 : 0.1; 

        return current_color*0.5 + shading * 0.25 + shading1*0.25;
    }else{
        current_color = vec3(0.7, 0.7, 0.7) * smoothstep(-0.5, 0.5, r.o.y + r.d.y*5.0 - cam.y);   
    }

    return current_color;  

}    

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    // set FOV (-0.3 to  0.3) and create uv
    vec2 uv = fragCoord/iResolution.xy *0.3 -0.15;

    // remove distortion 
    // (-0.53 -> X -> 0.53)
    // (-0.3  -> Y -> 0.3)
	uv.x *= (iResolution.x/iResolution.y);

    // we cast rays from here
    vec3 cam = vec3(0.0,sin(iTime) * 1.5, -10.0);

    // sending direction
    vec3 dir = vec3(uv*BILLBOARD_SIZE, 1.0);

    // Spheres in Room
    sphere[0] = sphere_t(vec3(0.0), vec3(0.0, 0.0, 0.0), 2.0, .1);
    sphere[1] = sphere_t(vec3(5.0 * sin(iTime),1.0,3.0 * cos(iTime)), vec3(1.0, 1.0, 0.0), 1.0, 1.);
    sphere[2] = sphere_t(vec3(0.0, -5.0+cam.y, 5.0), vec3(0.0, 1.0, 1.0), abs(2.0 * sin(iTime)), 1.);
    sphere[3] = sphere_t(vec3(8.0, 0.0+cam.y, 8.0), vec3(1.0, 1.0, 1.0), 5.0, 1.0);
    sphere[4] = sphere_t(vec3(-5.0, 5.0+cam.y, 3.0), vec3(1.0, 0., 0.0), 2.0, 1.0);  

    // create a ray & send it  
    ray_t r = ray_t (cam, dir);

    vec3 col = trace(r, cam);

    // Output to screen
    fragColor = vec4(col,1.0);
}

