float waveI(in vec3 p)
{
    float w = dot(p.xz, vec2(0.707, 0.707)) + iTime * 0.8;
   	float f = -abs(sin(w * 0.2)) * 0.5;

    w = dot(p.xz, normalize(vec2(-1.0, 0.3))) + iTime;
    f += -(sin(w + 4.33)) * 0.1;

    w = dot(p.xz, normalize(vec2(0.5, -1.5))) + iTime * 0.5;
    f += -abs(cos(w * 0.8)) * 0.3;

    f += textureLod(iChannel0, p.xz + iTime * 0.2, 8.5).x * 0.5;

    return f * (40.0 - p.z) / 40.0;
}

float wave(in vec3 p)
{
    float w = waveI(p);
    w += waveI(p + vec3(0.0, 0.0, 0.1));
    w += waveI(p + vec3(0.0, 0.0, -0.1));

    return w / 3.0;
}

float cof_sdf(in vec3 p)
{
    return p.y + 0.5- wave(p);
}

vec3 cof_norm(in vec3 p)
{
    const float e = 0.01;
    vec3 n = vec3(
        cof_sdf(p + vec3(e, 0.0, 0.0)) - cof_sdf(p - vec3(e, 0.0, 0.0)),
        cof_sdf(p + vec3(0.0, e, 0.0)) - cof_sdf(p - vec3(0.0, e, 0.0)),
        cof_sdf(p + vec3(0.0, 0.0, e)) - cof_sdf(p - vec3(0.0, 0.0, e)));
    n += textureLod(iChannel0, (p.xz - iTime * 0.1) * 0.1, 0.0).xyz * 0.25;
    return normalize(n);
}

float sphere(in vec3 p, float r)
{
    return length(p) - r;
}

float box(in vec3 p, in vec3 b)
{
    vec3 d = abs(p) - b;
	return length(max(d,0.0))
         + min(max(d.x,max(d.y,d.z)),0.0);
}

float torus( vec3 p, vec2 t )
{
  vec2 q = vec2(length(p.xy)-t.x,p.z);
  return length(q)-t.y;
}

float smoothUnion( float d1, float d2, float k )
{
    float h = clamp( 0.5 + 0.5*(d2-d1)/k, 0.0, 1.0 );
    return mix( d2, d1, h ) - k*h*(1.0-h);
}

vec3 rotate(in vec3 p, in float a)
{
    mat3x3 m = mat3x3(vec3(cos(a), -sin(a), 0.0), vec3(sin(a), cos(a), 0.0), vec3(0.0, 0.0, 1.0));
    return m * p;
}

vec3 cup_c = vec3(0.0, -0.1, 2.0);
float cup_sdf(in vec3 p)
{
    vec3 c = cup_c;

    float w1 = wave(c + vec3(0.0, 0.0, -0.2));
    float w2 = wave(c + vec3(0.0, 0.0, 0.0));
    float w3 = wave(c + vec3(0.0, 0.0, -0.1));
    c.y += (w1 + w2 + w3) / 3.0;

    p = rotate(p, 3.1415 / 16.0 + sin(iTime) * 0.1);
    p -= c;


    float o = max(-box(p - vec3(0.0, 1.2, 0.0), vec3(5.0, 1.0, 5.0)), sphere(p, 0.9));
    o = max(-sphere(p, 0.8), o);
    o = smoothUnion(o, torus(p - vec3(1.0, -0.2, 0.0), vec2(0.3, 0.075)), 0.1);

    return o;
}

float cup_mask(in vec3 p)
{
    vec3 c = cup_c;

    float w1 = wave(c + vec3(0.0, 0.0, -0.2));
    float w2 = wave(c + vec3(0.0, 0.0, 0.0));
    float w3 = wave(c + vec3(0.0, 0.0, -0.1));
    c.y += (w1 + w2 + w3) / 3.0;

    p = rotate(p, 3.1415 / 16.0 + sin(iTime) * 0.1);
    p -= c;

    return sphere(p, 0.8);
}

vec3 cup_norm(in vec3 p)
{
    const float e = 0.01;
    vec3 n = vec3(
        cup_sdf(p + vec3(e, 0.0, 0.0)) - cup_sdf(p - vec3(e, 0.0, 0.0)),
        cup_sdf(p + vec3(0.0, e, 0.0)) - cup_sdf(p - vec3(0.0, e, 0.0)),
        cup_sdf(p + vec3(0.0, 0.0, e)) - cup_sdf(p - vec3(0.0, 0.0, e)));
    return normalize(n);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy*2.0 - 1.0;

    vec3 o = vec3(0.0, 2.0, -1.5),
        f = normalize(cup_c - o),
        r = vec3(1.0, 0.0, 0.0),
        u = normalize(cross(f, r));
    float a = iResolution.y / iResolution.x;

    fragColor = vec4(0.0);

    vec3 d = normalize(f + u * uv.y * a + r * uv.x);

    vec3 l = normalize(vec3(0.707, 2.0, 0.0));

    fragColor = vec4(1.0) * 0.8;

    float cp = 100000.0;
    {
        float s = 0.0;
        for(int i = 0; i < 32; i++)
        {
            vec3 p = o + d * s;
            float m = cup_sdf(p);
            if(m < 0.005)
            {
                vec3 n = cup_norm(p);
                vec4 c = min(vec4(1.2) * (textureLod(iChannel1, n.xy, 2.0).r + 0.4), vec4(1.0));
                fragColor = c * max(dot(n, l), 0.2);
                cp = s;
                break;
            }
            s += m;
        }
    }

    {
        float s = 0.0;
        for(int i = 0; i < 128; i++)
        {
            vec3 p = o + d * s;
            float m = cof_sdf(p);
            if(m < 0.05)
            {
                if(s > cp || cup_mask(p) < 0.05)
                {
                    return;
                }

                float cup = cup_sdf(p);

                vec3 c = vec3(0.18, 0.12, 0.05) * 0.5;
                vec3 n = cof_norm(p);
                vec3 d = c * dot(n, l);
                vec3 sp = c * pow(max(dot(n, normalize(l + -d)), 0.0), 10.0) * 10.0;

                float cupAmb = (1.0 - min(cup * 5.0, 1.0));
                d += cupAmb * 3.0 * c;
                d += c * max(s - 20.0, 0.0) * 0.75;

                fragColor = vec4(d + sp, 1.0);
                break;
            }
            s += m;

            if(s > 30.0)
            {
                vec3 n = normalize(d);
                vec4 c = min(vec4(1.2) * (textureLod(iChannel1, n.xy, 0.0).r + 0.4), vec4(2.0));
                fragColor = c * max(dot(n, l), 0.2) * 1.3;
                return;
            }
        }
    }

    fragColor = pow(fragColor, vec4(1.4));
}

#define N_DELTA 0.015625
float rand(vec3 n) { 
    return fract(sin(dot(n, vec3(95.43583, 93.323197, 94.993431))) * 65536.32);
}

float perlin2(vec3 n)
{
    vec3 base = floor(n / N_DELTA) * N_DELTA;
    vec3 dd = vec3(N_DELTA, 0.0, 01.0);
    float
        tl = rand(base + dd.yyy),
        tr = rand(base + dd.xyy),
        bl = rand(base + dd.yxy),
        br = rand(base + dd.xxy);
    vec3 p = (n - base) / dd.xxx;
    float t = mix(tl, tr, p.x);
    float b = mix(bl, br, p.x);
    return mix(t, b, p.y);
}

float perlin3(vec3 n)
{
    vec3 base = vec3(n.x, n.y, floor(n.z / N_DELTA) * N_DELTA);
    vec3 dd = vec3(N_DELTA, 0.0, 0.0);
    vec3 p = (n - base) / dd.xxx;
    float front = perlin2(base + dd.yyy);
    float back = perlin2(base + dd.yyx);
    return mix(front, back, p.z);
}

float fbm(vec3 n)
{
    float total = 0.0;
    float m1 = 1.0;
    float m2 = 0.1;
    for (int i = 0; i < 6; i++)
    {
        total += perlin3(n * m1) * m2;
        m2 *= 2.1;
        m1 *= 0.6;
    }
    return total;
}

float cloudAtmosphere(vec2 uv)
{
    float n1 = fbm(vec3(uv * 2.0, 0.0));
    float n2 = fbm(vec3(uv,  1.0) + n1 * 0.05);   
    float n3 = fbm(vec3(uv, 2.0) + n2 * 0.3);
    return n3;
}

float nebula1(vec3 uv)
{
    float n1 = fbm(uv * 2.9 - 1000.0);
    float n2 = fbm(uv + n1 * 0.05);   
    return n2;
}

float nebula2(vec3 uv)
{
    float n1 = fbm(uv * 1.3 + 115.0);
    float n2 = fbm(uv + n1 * 0.35);   
    return fbm(uv + n2 * 0.17);
}

float nebula3(vec3 uv)
{
    float n1 = fbm(uv * 5.0);
    float n2 = fbm(uv + n1 * 0.15);   
    return n2;
}

vec3 nebula(vec3 uv)
{
    uv *= 10.0;
	return nebula1(uv * 0.5) * vec3(-5.0, 1.1, 7.0) -
        	nebula2(uv * 0.4) * vec3(-3.5, 1.0, 8.0) -
        	nebula3(uv * 0.6) * vec3(0.0, 0.0, -1.0);

}

float altitude(vec3 pos)
{
    return (cloudAtmosphere(pos.xz * 0.03) + 2.0) * 24.0;
}

float map(vec3 pos, vec3 rd)
{
    return (pos.y - altitude(pos)) * 0.7;
}

vec3 calcNormal(vec3 pos)
{
    vec3 dd = vec3(0.01, 0.0, 1.0);
    vec3 n = vec3(0.0, 1.0, 0.0);
    return normalize(vec3(map(pos + dd.xyy, n) - map(pos - dd.xyy, n),
                          map(pos + dd.yxy, n) - map(pos - dd.yxy, n),
                          map(pos + dd.yyx, n) - map(pos - dd.yyx, n)));

}

