// Fake/approximated soft shadow for a capsule.


// Other capsule functions:
//
// Capsule intersection: https://www.shadertoy.com/view/Xt3SzX
// Capsule bounding box: https://www.shadertoy.com/view/3s2SRV
// Capsule distance:     https://www.shadertoy.com/view/Xds3zN
// Capsule occlusion:    https://www.shadertoy.com/view/llGyzG

// Other soft-shadow functions:
//
// Sphere:    https://www.shadertoy.com/view/4d2XWV
// Ellipsoid: https://www.shadertoy.com/view/llsSzn
// Box:       https://www.shadertoy.com/view/WslGz4
// Capsule:   https://www.shadertoy.com/view/MlGczG

#define AA 3

//================================================================

// fakesoft shadow occlusion
float capShadow( in vec3 ro, in vec3 rd, in vec3 a, in vec3 b, in float r, in float k )
{
    vec3 ba =  b - a;
	vec3 oa = ro - a;

    // closest distance between ray and segment
#if 1
    // naive way to solve the 2x2 system of equations
	float oad  = dot( oa, rd );
	float dba  = dot( rd, ba );
	float baba = dot( ba, ba );
	float oaba = dot( oa, ba );
	vec2 th = vec2( -oad*baba + dba*oaba, oaba - oad*dba ) / (baba - dba*dba);
#else
    // fizzer's way to solve the 2x2 system of equations
    vec3 th = inverse(mat3(-rd,ba,cross(rd,ba))) * oa;
#endif	


	th.x = max(   th.x, 0.0001 );
	th.y = clamp( th.y, 0.0, 1.0 );

	vec3  p =  a + ba*th.y;
	vec3  q = ro + rd*th.x;
    float d = length( p-q )-r;

    // fake shadow
    float s = clamp( k*d/th.x+0.5, 0.0, 1.0 );
    return s*s*(3.0-2.0*s);
}


//================================================================
// intersect capsule
float capIntersect( in vec3 ro, in vec3 rd, in vec3 pa, in vec3 pb, in float r )
{
    vec3  ba = pb - pa;
    vec3  oa = ro - pa;

    float baba = dot(ba,ba);
    float bard = dot(ba,rd);
    float baoa = dot(ba,oa);
    float rdoa = dot(rd,oa);
    float oaoa = dot(oa,oa);

    float a = baba      - bard*bard;
    float b = baba*rdoa - baoa*bard;
    float c = baba*oaoa - baoa*baoa - r*r*baba;
    float h = b*b - a*c;
    if( h>=0.0 )
    {
        float t = (-b-sqrt(h))/a;

        float y = baoa + t*bard;

        // body
        if( y>0.0 && y<baba ) return t;

        // caps
        vec3 oc = (y<=0.0) ? oa : ro - pb;
        b = dot(rd,oc);
        c = dot(oc,oc) - r*r;
        h = b*b - c;
        if( h>0.0 )
        {
            return -b - sqrt(h);
        }
    }
    return -1.0;
}

// compute normal
vec3 capNormal( in vec3 pos, in vec3 a, in vec3 b, in float r )
{
    vec3  ba = b - a;
    vec3  pa = pos - a;
    float h = clamp(dot(pa,ba)/dot(ba,ba),0.0,1.0);
    return (pa - h*ba)/r;
}


// fake occlusion
float capOcclusion( in vec3 p, in vec3 n, in vec3 a, in vec3 b, in float r )
{
    vec3  ba = b - a, pa = p - a;
    float h = clamp(dot(pa,ba)/dot(ba,ba),0.0,1.0);
    vec3  d = pa - h*ba;
    float l = length(d);
    float o = 1.0 - max(0.0,dot(-d,n))*r*r/(l*l*l);
    return sqrt(o*o*o);
}



void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
     // camera movement	
	float an = 0.5*iTime;
	vec3 ro = vec3( 1.0*cos(an), 0.4, 1.0*sin(an) );
    vec3 ta = vec3( 0.0, 0.0, 0.0 );


    vec3 tot = vec3(0.0);
#if AA>1
	#define ZERO min(iFrame,0)
    for( int m=ZERO; m<AA; m++ )
    for( int n=ZERO; n<AA; n++ )
    {
        vec2 o = vec2(float(m),float(n)) / float(AA) - 0.5;
        vec2 p = (2.0*(fragCoord+o)-iResolution.xy)/iResolution.y;
#else    
        vec2 p = (2.0*fragCoord-iResolution.xy)/iResolution.y;
#endif

        // camera matrix
        vec3 ww = normalize( ta - ro );
        vec3 uu = normalize( cross(ww,vec3(0.0,1.0,0.0) ) );
        vec3 vv = normalize( cross(uu,ww));
        // create view ray
        vec3 rd = normalize( p.x*uu + p.y*vv + 1.5*ww );

        const vec3  capA = vec3(-0.3,-0.1,-0.1);
        const vec3  capB = vec3(0.3,0.1,0.4);
        const float capR = 0.2;

        vec3 col = vec3(0.0);

        const vec3 lig = normalize(vec3(-0.8,0.8,0.2));

        float tmin = 1e20;
        float sha = 1.0;
        float occ = 1.0;
        vec3 nor;

        // plane (floor)
        {
            float t = (-0.3-ro.y)/rd.y;
            if( t>0.0 && t<tmin )
            {
                tmin = t;
                vec3 pos = ro + t*rd;
                nor = vec3(0.0,1.0,0.0);
                // fake soft shadow!
                sha = capShadow( pos+0.001*nor, lig, capA, capB, capR, 4.0 ); 
                // fake occlusion 
                occ = capOcclusion( pos, nor, capA, capB, capR ); 
            }
        }

        // capsule
        {
            float t = capIntersect( ro, rd, capA, capB, capR );
            if( t>0.0 && t<tmin )
            {
                tmin = t;
                vec3 pos = ro + t*rd;
                nor = capNormal(pos, capA, capB, capR );
                occ = 0.5 + 0.5*nor.y;
                sha = 1.0;
            }
        }

        // lighting
        if( tmin<1e19 )
        {
            float dif = clamp( dot(nor,lig), 0.0, 1.0 )*sha;
            float amb = 1.0*occ;
            col =  vec3(0.2,0.3,0.4)*amb;
            col += vec3(0.7,0.6,0.5)*dif*0.8;
        }

        tot += sqrt( col );
#if AA>1
    }
    tot /= float(AA*AA);
#endif

	fragColor = vec4( tot, 1.0 );
}

float get_foam_for_position(in vec2 pos)
{
    // foam perturbed coordinates
    vec2 foam_uv = pos*8.0;

    float pos_noise = abs(sin(pos.x*1.0)*sin(pos.y*1.0));

    float fx = pos.y;
    float fy = pos.x;

    foam_uv.x += cos(fx*20.0 + iTime*1.0)*0.17;
    foam_uv.y += sin(fy*20.0 + iTime*1.0)*0.17;

    // process blob texture to produce foam-like pattern
    float foam = texture(iChannel0, foam_uv).x;
    foam = 1.0 - foam;
    foam = min( foam*1.2, 1.0 );
    foam = pow( foam, 4.0 );
    foam = min( foam*1.5, 1.0 );

    return foam;
}

vec4 get_water_surface(in vec2 pos, in float sdf)
{
    // coastal pulse wave
    float wave_pulse = sdf + iTime * 0.2;
    wave_pulse = fract(wave_pulse);

    // construct wave shape
    float wave_front = pow(wave_pulse, 64.0);
    wave_front = min((wave_pulse - 0.95) * 20.0, 1.0);
    float wave_back = pow(1.0 - wave_pulse, 8.0);
    //wave_back = min( 1.0, ((1.0 - wave_pulse) - 0.6) * 4.0 );

    float wave_height = max(wave_front, wave_back);

    float wave_fadeoff = 1.0 - min(1.0, max((sdf - 0.6)*5.0, 0.0));

	float foam = get_foam_for_position(pos);

    float wave = max( 0.0, wave_height*1.5 - 1.0 + foam );
    wave *= wave_fadeoff;

    vec3 water = vec3(0.1, 0.3, 0.8);
    vec3 wave_foam = vec3(wave, wave, wave);

    vec3 color = water + wave_foam;
    return vec4(color, wave_height);
}


float df(float x, float y)
{
    return 1.0 - x - y;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 pos = fragCoord/iResolution.xy;
    vec4 color = get_water_surface(pos, df(pos.x, pos.y));
    fragColor = vec4(color.xyz, 1.0);
}

#define ISJ 1

# define SQR(x) ((x) * (x))
const vec4 bitEnc = vec4(1. ,255. ,65025. ,16581375.);
const vec4 bitDec = 1. / bitEnc;

vec4 myt(float ij)
{
 float a;
 float r;
 float g;
 float b;

 a = ij - mod(ij, 65536. * 256.);
 r = (ij - a) - mod(ij, 65536.);
 g = (ij - a - r) - mod(ij, 256.);
 b = (ij - a - r - g);
 return (vec4(r / 16581375., g / 65535., b / 255., 1.));
}

