#define MSAA 4

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
 float zoom = iResolution.y * 0.4;
 int maxk = 50;
 float o1 = -0.25;
 float o2 = 0.25;
 vec2 msaa[4];

 msaa[0] = vec2( o1,  o2);
 msaa[1] = vec2( o2, -o1);
 msaa[2] = vec2(-o1, -o2);
 msaa[3] = vec2(-o2,  o1);
 vec3 color = vec3(0.);

 int k = 0;
 vec2 ct; 
 vec2 c;
 vec2 z;
 vec2 zsq;
 for (int i = 0; i < MSAA; ++i)
 {
  ct = iResolution.xy / 2. / zoom;
  c = cidef(fragCoord - msaa[i], ct, zoom);
  z =(zidef(fragCoord - msaa[i], c, ct, zoom));
  zsq = z * z;
  k = 0;
  while (k < maxk)
  {
   z = vec2(zsq.x, (SQR((z.x) + (z.y)) - zsq.x)) - zsq.y + c;
   zsq = z * z;
   if (zsq.x + zsq.y > 4.)
    break;
   k++;
  }
  if (k >= maxk)
   color += vec3(0.);
  else
