// Display a 3D Hilbert curve (https://en.wikipedia.org/wiki/Hilbert_curve)

// colors

#define BG vec3(0.18, 0.28, 0.23)
#define COL vec3(0.12, 0.56, 1.0)

// Skilling's algorithm, https://doi.org/10.1063/1.1751381 and https://doi.org/10.1063/1.1751382
// see also http://www.inference.org.uk/bayesys/test/hilbert.c and compare with https://www.shadertoy.com/view/3tl3zl

vec3 hilbert( in int k, in int s )
{
	int bb = 1 << s, b = bb;
    ivec3 t = ivec3(k ^ (k >> 1)), hp = ivec3(0);

    for( int j = s - 1; j >= 0; j-- )
    {
        b >>= 1;
        hp += (t >> (2 * j + ivec3(2, 1, 0))) & b;
    }  

    for( int p = 2; p < bb; p <<= 1 )
    {
        int q = p - 1;

        if( (hp.z & p) != 0 ) hp.x ^= q;
        else hp.xz ^= (hp.x ^ hp.z) & q;

        if( (hp.y & p) != 0 ) hp.x ^= q;
        else hp.xy ^= (hp.x ^ hp.y) & q;

        if( (hp.x & p) != 0 ) hp.x ^= q;
    }

    return 2.0 * (vec3(hp)/float(bb - 1)) - 1.0;
}

// line segment distance

float segment(vec2 p, vec2 a,vec2 b) { 
    p -= a, b -= a;
	return length(p - b * clamp(dot(p, b) / dot(b, b), 0.0, 1.0));
}

// rotation matrix

#define rot(a) mat2(cos(a), -sin(a), sin(a), cos(a))

// 3D -> 2D projection

vec2 proj( in float p, in float c, in vec3 P )
{
    float q = -p * sqrt(1.0 - c * c);

    return mat3x2(-p, q, 0.0, c, p, q) * P;
}

// 3D curve drawing, adapted from https://www.shadertoy.com/view/4lyyWw by Fabrice Neyret

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float ep = 5.0/iResolution.y;
    vec2 aspect = iResolution.xy / iResolution.y;
    vec2 uv = ( fragCoord.xy / iResolution.y ) - 0.5 * aspect;
    uv *= 3.2;

    int s = (int(floor(0.1 * iTime)) % 4) + 1; // iteration stage of Hilbert curve
    int n = 1 << (3 * s); // Hilbert curve points
    vec3 P, Pn;
    vec2 pb, p;
    float d = 100.0, dt;

    for (int i = 0; i < n; i++)
    {
        P = hilbert(i, s);

        P.xz *= rot(2.0 * iTime); // rotation
        p = proj(sqrt(0.5), 0.9, P); // screen projection

        if (i > 0)
        {
            dt = segment(uv, pb, p) * ((5.0 - P.z)/9.0); // draw segment with thickening factor
            if (dt < d) { d = dt; Pn = P; } // keep nearest
        }

        pb = p;
    }

    float da = 0.5 + 0.5 * mix(0.8, 1.0, Pn.y); // darkening at the bottom
    fragColor = vec4(mix(BG, mix(vec3(0.0), COL, da), smoothstep(ep, 0.0, d)), 1.0);
}

// Mollweide-projected Newton-Raphson fractal

// some constants
#define PI 3.14159265359

// from Fabrice Neyret, 
#define cis(a) vec2( cos(a), sin(a) )
#define cmul(A, B) ( mat2( A, -(A).y, (A).x ) * (B) )
#define cinv(Z) ( vec2( (Z).x, -(Z).y ) / dot(Z, Z) ) 
#define cdiv(A, B) cmul( A, cinv(B) )

// modified DLMF coloring, adapted from https://www.shadertoy.com/view/WtXGWN
vec3 smooth_dlmf( float h )
{
    vec3 rgb = clamp( vec3(1., -1., -1.) * abs((vec3(8., 4., 8.) * mod(h, 1.0) - vec3(4.5, 1.5, 5.5))) + vec3(-1.5, 1.5, 2.5), 0.0, 1.0 );
	return rgb * rgb * rgb/(1.0 - 3.0 * rgb * (1.0 - rgb));
}

// exponentiation using binary powering
vec2 cpow( in vec2 z, in int n )
{
    if (n == 0) return vec2(1.0, 0.0);
    if (n == 1) return z;

    if (n < 0) z = cinv(z);
    n = abs(n);

    vec2 w = vec2(1.0, 0.0);

    for(int k = 0; k < 11; k++) { // should cover up to n == 1000

        if ((n & 1) == 1) w = cmul(w, z);

        z = cmul(z, z);        
        n >>= 1;

        if (n < 2) break;
    }

    return cmul(z, w);
}

// Newton-Raphson fractal for z^n - 1
vec3 NewtonFractal(int n, vec2 z) {

    vec2 ld = vec2(0.0);
    float k = 0.0;

    for(int i = 0; i < 20; i++) {
        ld = cdiv(cpow(z, n) - vec2(1.0, 0.0), float(n) * cpow(z, n - 1));
        if (dot(ld, ld) < 0.001) break;
        z = z - ld;
        k += 1.0;
    }

    // multiplier highlights the positions of the roots
    return pow((k + 0.1)/20.0, 0.18) * smooth_dlmf(0.1 + 0.5 * atan(z.y, z.x)/PI);

}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
