        vec2 uv = ( fragCoord.xy / iResolution.y ) - 0.5 * aspect;
        uv *= 2.3;

        vec3 col = vec3(0.0);

        float ee = 1.0 - length(uv/vec2(2.0, 1.0));
        if ( ee > 0.0)
        {
            float s2 = sqrt((1.0 - uv.y) * (1.0 + uv.y)), as = 2.0 * (uv.y * s2 + asin(uv.y));
            vec2 zz = sqrt((PI + as)/(PI - as)) * cis(0.2 * iTime - 0.5 * PI * uv.x/s2); // Mollweide variable

            col = NewtonFractal(4, zz);
        }

        col = mix(vec3(0.19, 0.28, 0.23), col, smoothstep(0.0, 4.0/iResolution.y, ee));
        fragColor = vec4( col, 1.0 );
}

float random(vec2 st) {
	return fract(sin(dot(st, vec2(15.13123, 45.56251))) * 45666.12) *2.-1.;
}

float noise2(vec2 st) {
	vec2 i = floor(st);
	vec2 f = fract(st);
	// Create grid to spread out our noise
	float a = random(i);
	float b = random(i + vec2(1.0, 0.0));
	float c = random(i + vec2(0.0, 1.0));
	float d = random(i + vec2(1.0, 1.0));
	// Create our hermite cubic interpolation
	vec2 h = f * f * (3.0 - 2.0 * f);
	// Do our bilinear interpolation:
	return 	mix(a, b, h.x) + 
			(c - a) * h.y * (1.0 - h.x) +
			(d - b) * h.x * h.y;
}
float fbm(vec3 p){
	float amp = 2.0;
	float fbm = 0.;
	float scale = 2.0;
	for(int i = 0; i < 5; i++){
		fbm += noise2(p.xy) * amp;
		p *= scale;
		amp *= 0.5;
	}
	return fbm/12.;
}


