int mandel(float x0, float y0, int its){
    float x = 0.0;
    float y = 0.0;
    float x_tmp;

    int it = 0;
    while (x*x+y*y<4.0 && it<its){
        x_tmp = x*x-y*y+x0;
        y = 2.0*x*y+y0;
        x = x_tmp;
        it++;
    }
    return it;
}

vec3 setup(vec2 uv, int its){
    float x = uv.x;
    float y = uv.y;

    x = x*3.5-2.5;
    y = y*2.0-1.0;

    vec2 center = vec2(-0.7615720,-0.0847596);
    float time_bias = tanh(min(31.0,iTime)/5.0);

	x = time_bias*center.x+(1.0-time_bias)*x;
    y = time_bias*center.y+(1.0-time_bias)*y;

    int res = mandel(x, y, its);
    if (res==its) {
        return vec3(0.6,0.2,0.2);
    }
    //float col_r = (float(res%51))/255.0;
    //float col_g = (float(res%255))/255.0;
    float col_g = (sin(float(res/3))+1.0)/2.0;
    float col_b = (sin(float(res/37))+1.0)/2.0;
    //float col_b = (float((res/9)%255))/255.0;
    return vec3(0,col_g,col_b);
}

