}
float map(vec3 p) {
    //p = mod(p+0.5*repeat_offset, repeat_offset)-0.5*repeat_offset;
    //time-varying parameters (maybe replace with some inputs, or remove)
    float final_offset;
    if (iMouse.w < 1.)
        final_offset = offsetdiff*delay_sin(iTime*0.5+1.) + ring_offset;
    else
        final_offset = (iMouse.y/iResolution.y-0.5)*3.+2.;
    float final_ratio = ratio/final_offset;

    float ringdiff = (max_rings-min_rings)*0.5;
    float ring_count = (max_rings+min_rings)*0.5;
    float final_ringcount;
    if (iMouse.z < 1.)
    	final_ringcount = ringdiff*delay_sin(iTime*0.5)+ring_count;
    else
    	final_ringcount = ringdiff*(iMouse.x/iResolution.x-0.5)*2. + ring_count;
    float sector = 2.*PI/(final_ringcount);
    float outerrad = base_radius;
    float innerrad = outerrad*final_ratio;
    vec2 h = sdTorus(p, vec2(outerrad, innerrad));
    int i;
    float currindent = indent;
    vec2 minh = h;

    for (i=0; i<levels; i++) {

        //mod polar coordinates
        float theta = mod(abs(h.y), sector)-sector/2.;

        //new cartesian coords
        float s = length(p.zx);
        p.z = cos(theta)*s - outerrad;
        p.x = sin(theta)*s;
        p = p.zxy;

        //new torus
        outerrad = innerrad*final_offset;        
        innerrad = outerrad*final_ratio;
        h = sdTorus(p, vec2(outerrad, innerrad));

        minh.x = max(minh.x, currindent-h.x);
        if (h.x < minh.x) {
            minh = h;
        }

        currindent = currindent * final_ratio * final_offset;
    }
    return minh.x;
}

vec4 gradient(in vec3 pos) {
    vec3 offset = vec3(-gradient_eps, 0.0, gradient_eps);
    float dx0 = map(pos+offset.xyy);
    float dxf = map(pos+offset.zyy);
    float dy0 = map(pos+offset.yxy);
    float dyf = map(pos+offset.yzy);
    float dz0 = map(pos+offset.yyx);
    float dzf = map(pos+offset.yyz);
    float ddd = map(pos);
    return vec4(normalize(vec3(dxf - dx0, dyf - dy0, dzf - dz0)), dx0+dxf+dy0+dyf+dz0+dzf-6.*ddd);
}

vec2 raymarch(vec3 pos, vec3 dir) {
    int i;
    float d = 0.;
    float dist;
    for (i=0; i<iters; i++) {
        dist = map(pos+d*dir);
        d += dist;
        if (dist < tol) {
            return vec2(d, 2.);
        } else if (dist > maxdist) {
            break;
        }
    }
	d = (plane_height-pos.y) / dir.y;
    return vec2(d, step(-d, 0.)*step(length((pos+d*dir).zx), 50.));
}

//softer soft shadows
//see https://www.shadertoy.com/view/4tBcz3
float shadowtrace(vec3 pos, vec3 dir) {
    int i;
    float d = shadow_eps;
    float dist = map(pos+d*dir);
    float fac = 1.0;
    for (i=0; i<shadow_iters; i++) {
        d += max(0.01, dist);
        dist = map(pos+d*dir);
        fac = min(fac, dist * shadow_sharpness / d);
    }
    return mix(mix(0.5, 0., -fac), mix(0.5, 1., fac), step(fac, 0.));
}

vec3 skycol(vec3 rd) {
    return vec3(0.6, 0.7, 0.8)*(1.+pow(max(dot(rd, light_dir), 0.), 2.)) + pow(max(0.,dot(rd, light_dir)), 5.);
}

float schlick(vec3 rd, vec3 n) {
    return 1.-(R0+(1.-R0)*pow(max(dot(n.xyz, -rd), 0.), 5.0));
}

vec3 material(vec3 ro, vec3 rd, vec4 n, vec2 record) {
    if (record.y > 1.5) {
        float edgefac = abs(n.w*laplace_factor);
        vec3 color = 1.-viridis_quintic(edgefac).yxz*0.5;
        float fac = max(ambient, dot(light_dir, n.xyz));
        //float ao = min(1.,ao_min+(record.z > ao_radius ? 1. : record.z/(ao_radius)));
        return fac*color;
    } else if (record.y > 0.5) {
        vec2 uv = (ro+rd*record.x).zx;
        uv = abs(mod(uv, 4.)-2.);
        float checker = abs(step(uv.x, 1.) - step(uv.y, 1.));
        return vec3(light_dir.y*(0.5+0.5*checker));
    } else {
        return skycol(rd);
    }
}

//materials with reflections
vec3 shade(vec3 ro, vec3 rd, vec4 n, vec2 record) {
    vec3 shadedcolor = material(ro, rd, n, record);
    if (record.y > 0.5) {
        float fac = shadowtrace(ro+rd*record.x, light_dir);
        shadedcolor *= max(ambient, fac);
    }
    if (record.y > 1.5) {
        int i;
        float final_albedo = reflection_albedo;
        for (i=0; i<reflections; i++) {
            if (record.y < 1.5) break;
            final_albedo *= schlick(rd, n.xyz);
            ro = ro+rd*record.x;
            rd = reflect(rd, n.xyz);
            ro += reflection_eps*rd;
            record = raymarch(ro, rd);
            n = gradient(ro+rd*record.x);
            shadedcolor += final_albedo * material(ro, rd, n, record);
        }
        //compute last reflections with just envmap
        if (record.y > 1.5) {
            final_albedo *= schlick(rd, n.xyz);
            shadedcolor += final_albedo * skycol(reflect(rd, n.xyz));
        }
    }
    return shadedcolor;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{    
    R0*=R0;
    //camera position
    float s = sin(iTime*0.5);
    float ww = iTime*0.2;
    vec3 ro = (3.-s)*vec3(cos(ww),0.5+0.5*s,sin(ww));
    vec3 w = normalize(vec3(0.,-1.5-s,0.)-ro);
    vec3 u = normalize(cross(w, vec3(0., 10., 0.)));
    vec3 v = cross(u, w);
    vec3 rd = normalize(w*fdist+(fragCoord.x/iResolution.x-0.5)*u+(fragCoord.y-iResolution.y/2.0)/iResolution.x*v);

    vec2 record = raymarch(ro, rd);
    vec4 n = gradient(ro+rd*record.x);
    vec3 shadedcolor = shade(ro, rd, n, record);

    fragColor = vec4(shadedcolor, 1.);
}

// common stuff
const vec3  fv3_1   = vec3(1.0, 1.0, 1.0);
const vec3  fv3_0   = vec3(0.0, 0.0, 0.0);
const vec3  fv3_x   = vec3(1.0, 0.0, 0.0);
const vec3  fv3_y   = vec3(0.0, 1.0, 0.0);
const vec3  fv3_z   = vec3(0.0, 0.0, 1.0);
const vec2  fv2_1   = vec2(1.0, 1.0);
const vec2  fv2_0   = vec2(0.0, 0.0);
const vec2  fv2_x   = vec2(1.0, 0.0);
const vec2  fv2_y   = vec2(0.0, 1.0);
const float PI      = 3.14159265359;
const float TAU     = PI * 2.0;

// less common
const float rmMaxSteps = 100.0;
const float rmMaxDist  = 180.0;
const float rmEpsilon  =   0.001;
const float grEpsilon  =   0.001;
#define SMOOTH_MANDEL 1

vec2 complexMul(in vec2 A, in vec2 B) {
  return vec2((A.x * B.x) - (A.y * B.y), (A.x * B.y) + (A.y * B.x));
}

struct POI {
  vec2  center;
  float range;
  float maxIter;
};
vec4 poiToVec4(in POI poi) {return vec4(poi.center, poi.range, poi.maxIter);}
POI vec4ToPOI(in vec4 v) {return POI(v.xy, v.z, v.w);}


float mandelEscapeIters(in vec2 C, in float maxIters) {
  vec2 Z = C;
  for (float n = 0.0; n < maxIters; n += 1.0) {
    Z  = complexMul(Z, Z) + C;
    if (dot(Z, Z) > 4.0) {
      return n;
    }
  }
  return maxIters;
}

// adapted from IQ
// http://iquilezles.org/www/articles/distancefractals/distancefractals.htm
float mandelDist(in vec2 c, float numIters)
{
  vec2  z = vec2(0.0, 0.0);
  vec2 dz = vec2(0.0, 0.0);

  float m2;
  for (float n = 0.0; n < numIters; ++n) {
    dz = 2.0 * complexMul(z, dz) + fv2_x;
    z  = complexMul(z, z) + c;

    m2 = dot(z, z);
    if (m2 > 1.0e10) {
      break;
    }
  }

  // distance estimation: G/|G'|
  return sqrt(m2 / dot(dz, dz)) * 0.5 * log(m2);
}

// based on https://www.shadertoy.com/view/Wtf3Df
vec3 getRayDirection(in vec3 ro, in vec3 lookAt, in vec2 uv, float zoom) {
  vec3 ol       = normalize(lookAt - ro);
  vec3 screenRt = cross(ol      , fv3_y); // world Up
  vec3 screenUp = cross(screenRt, ol   );
  vec3 rd       = normalize(uv.x * screenRt + uv.y * screenUp + ol * zoom);
  return rd;
}

mat2 rot2(float t) {
  float s = sin(t);
  float c = cos(t);
  return mat2(s, c, -c, s);
}

float sdfCircle2D(in vec2 p, in vec2 c, float r) {
  return length(p - c) - r;
}

float sdf(in vec3 p, out float bright) {
  float mi = 20.0;
  vec2 mp = p.xz;
  mp.x += 0.35;
  mp *= rot2(iTime * -0.2);
  mp *= 0.05;
  mp.x -= 0.25;  
  bright = 1.0;
  #if SMOOTH_MANDEL
  float iters        = mandelDist(mp, mi);
  bright = smoothstep(-0.001, 0.001, iters);
  iters = max(0.0, iters);
  iters = 1.0 / (iters + 0.1);
  float mandelHeight = 0.4 * mi;
  #else
  float iters        = mandelEscapeIters(mp, mi);
  float mandelHeight =  2.0;
  #endif

  // altitude above plane
  float dist = p.y;

  // some waves
  dist += sin(p.x * 0.1 + iTime) * 2.0;

  // the mandelbrot set
  dist -= iters/mi * mandelHeight;

  /*
  // some pillars
  const float pillarDist = 60.0;
  const float pillarRad  = 10.0;
  dist = min(dist, sdfCircle2D(p.xz, pillarDist * vec2(-1.0, -1.0), pillarRad));
  dist = min(dist, sdfCircle2D(p.xz, pillarDist * vec2(-1.0,  1.0), pillarRad));
  dist = min(dist, sdfCircle2D(p.xz, pillarDist * vec2( 1.0,  1.0), pillarRad));
  dist = min(dist, sdfCircle2D(p.xz, pillarDist * vec2( 1.0, -1.0), pillarRad));
  */

  return dist;
}

// from http://jamie-wong.com/2016/07/15/ray-marching-signed-distance-functions
vec3 estimateNormal(vec3 p) {
  const float e = grEpsilon;
  float unused;
  return normalize(vec3(
    sdf(vec3(p.x + e, p.y    , p.z     ), unused) - sdf(vec3(p.x - e, p.y    , p.z    ), unused),
    sdf(vec3(p.x    , p.y + e, p.z     ), unused) - sdf(vec3(p.x    , p.y - e, p.z    ), unused),
    sdf(vec3(p.x    , p.y    , p.z  + e), unused) - sdf(vec3(p.x    , p.y    , p.z - e), unused)
  ));
}


vec3 march(in vec3 p, in vec3 rd, out float numSteps, out float bright) {
  float distTotal = 0.0;
  for (numSteps = 0.0; numSteps < rmMaxSteps; ++numSteps) {
    float d = sdf(p, bright);
    if ((d < rmEpsilon) || (distTotal > rmMaxDist)) {
      return p;
    }
    p += rd * d;
    distTotal += d;
  }
  return p;
}

void mainImage(out vec4 RGBA, in vec2 XY)
{
  RGBA.a   = 1.0;

  float smallWay = min(iResolution.x, iResolution.y);
  vec2  uv = (XY * 2.0 - fv2_1 * iResolution.xy)/smallWay;
  float t  = iTime * TAU * 0.01;
  vec3  ro = vec3( vec2(cos(t), sin(t)) * 40.0, 10.0).xzy;
  vec3  la = vec3( 0.0, 0.0,  0.0);
  const float zoom = 3.2;
  vec3  rd = getRayDirection(ro, la, uv, zoom);

  float numSteps;
  float sdfBright;
  vec3 surf = march(ro, rd, numSteps, sdfBright);
  float dist = length(ro - surf);

  const float checkSize = 20.0;
  // float bright = float((mod(surf.x, checkSize * 2.0) > checkSize) ^^ (mod(surf.z, checkSize * 2.0) > checkSize));
  float bright = 1.0;
  bright = bright * 0.05 + 0.95;
 // bright = 0.95;

  // shading
  bright *= max(0.3, dot(fv3_x, estimateNormal(surf)));

  vec3 rgb = vec3(bright);

  rgb.rg *= sdfBright * 0.7 + 0.3;
  rgb.b  *= sdfBright * 0.3 + 0.7;


  // fog
  rgb *= 1.0 + surf.y * 0.2 - 0.5;
  rgb = mix(rgb, vec3(0.5), clamp(dist/rmMaxDist - 0.1, 0.0, 1.0));

  // gamma
  rgb = pow(rgb, vec3(0.4545));

  // ray steps
  // RGBA.r += numSteps / rmMaxSteps;

  RGBA.rgb = rgb;

}


// grimoire bindings
// out vec4 fragColor; void main() { mainImage(fragColor, gl_FragCoord.xy); }

/*
Shader coded live on twitch (https://www.twitch.tv/nusan_fx)
You can lower the MARCH_STEPS if too slow
You can also try the color and shape options
The shader was made using Bonzomatic.
You can find the original shader here: http://lezanu.fr/LiveCode/JewelSurface.glsl
Inspired by https://www.shadertoy.com/view/ttl3R2
*/

#define MARCH_STEPS 100

#define COLOR_GEM 1
#define RED_GEM 0
#define CHROMATIC 0

#define SHAPE sphere
//#define SHAPE box
//#define SHAPE diamon
//#define SHAPE cylinder

#define time iTime

mat2 rot(float a) {
  float ca=cos(a);
  float sa=sin(a);
  return mat2(ca,sa,-sa,ca);  
}

float rnd1(float t) {
  return fract(sin(t*724.355)*685.655);
}

float bx(vec3 p) {
  p=abs(p);
  return max(p.x,max(p.y,p.z));
}

float smin(float a, float b, float h) {
  float k=clamp((a-b)/h*.5+.5,0.,1.);
  return mix(a,b,k)-k*(1.-k)*h;
}

float mat=1.;
float id=0.;
float map(vec3 p) {

  vec3 bp=p;

  float s=5.;
  float mm=10000.;
  id=0.;
  for(int i=0; i<4; ++i) {

    float t=rnd1(float(i)+12.7)*75.842 + 122.845;
    //t += time*(.1+i*.05);
    p.xy *= rot(t);
    p.yz *= rot(t*.7);

    id += dot(sign(p), vec3(1.72,3.84,12.94)*(float(i)+1.));
    p=abs(p);

    float mp=min(p.x, min(p.y,p.z));
    mm=min(mm,mp);

    p-=s;

    s*=0.7;
  }


  float d = mm;

  float dist=0.6;
  vec3 rp=(fract(p/dist-.5)-.5)*dist;
  float d3 = length(rp);

  float sphere = length(bp)-20.;
  float box = bx(bp)-14.;
  float diamon = box;
  bp.xy *= rot(3.141592*.25);
  bp.xz *= rot(3.141592*.25);
  diamon = max(diamon, bx(bp)-12.);

  float cylinder = max(length(bp.xz)-14., length(bp.xy)-18.);
  //shape = smin(length(bp+7 * sin(time*.5))-12, length(bp-7)-12, 12);

  float shape = SHAPE;

  float d4 = length(vec2(d, shape));
  d = d4-0.17;

  d3 = length(vec2(d3, d4))-0.39;



  d = min(d,d3);


  float d2 = abs(shape) - min(mm-.2, 1.0) * .7;



  mat=(d<d2)?1.:0.;

  d=min(d, d2);

  //d*=0.7;

  return d;
}

float curve(float t, float d) {
  float g=t/d;
  return mix(rnd1(floor(g)), rnd1(floor(g)+1.), pow(smoothstep(0.,1.,fract(g)), 10.));
}

void cam(inout vec3 p) {
  float t=time*.15 + curve(time+97.4, 2.5*4.)*2.;
  p.yz *= rot(sin(t)*.5);
  p.xz *= rot(sin(t*1.3)*.9);
}

vec3 rnd(float t) {
  return fract(sin(t*vec3(423.745,384.912,542.877))*725.945);
}

vec3 sky(vec3 r) {

  vec3 col=vec3(0.3);

  float dist=0.3;
  vec2 amask=abs(fract(r.yz/dist-.5)-.5)*dist;
  float mask=clamp(((1.3-(max(amask.x,amask.y))*10.))*2.,0.,1.);

  col = mix(col, vec3(7), pow(max(0.,-r.y),12.));
  col = mix(col, vec3(7), pow(max(0.,-r.z),12.));
  col = mix(col, mask * vec3(10), pow(abs(r.x),10.));

  return col * 1.7;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
  vec2 uv = vec2(fragCoord.x / iResolution.x, fragCoord.y / iResolution.y);
  uv -= 0.5;
  uv /= vec2(iResolution.y / iResolution.x, 1);

  #if CHROMATIC
  float fsize=1.0;
  float fringe = fract(floor(gl_FragCoord.y/fsize)*fsize/3.);
  vec3 fcol = (1.-abs(fringe*3.-vec3(0,1,2)))*3.;
  #else
  float fringe = 0.;
  vec3 fcol = vec3(1.0);
  #endif

  //uv *= 2.0/(1.0+length(uv));

  float delay=2.5;
  float zoom=curve(time, delay);
  vec3 s=vec3((curve(time+12.75, delay)-.5)*13., (curve(time+37.15, delay)-.5)*12.,-27.-zoom*10.);
  vec3 r=normalize(vec3(-uv, 0.9 - zoom * 0.4));

  cam(s);
  cam(r);

  vec3 col=vec3(0);
  vec2 off=vec2(0.01,0);

  vec3 p=s;
  float side=1.;
  vec3 prevhit=s;
  vec3 prod=vec3(1);
  for(int i=0; i<MARCH_STEPS; ++i) {
    float d=map(p)*side;
    if(abs(d)<0.001) {

      float curmat=mat;
      float curid=id;

      vec3 n=normalize(map(p)-vec3(map(p-off.xyy), map(p-off.yxy), map(p-off.yyx))) * side;

      vec3 rn = reflect(r,n);

      float fre=pow(1.-abs(dot(n,r)), 2.);

      if(curmat<0.5) {
        #if RED_GEM
        vec3 diff=vec3(1,0,0.2);
        #elif COLOR_GEM
        vec3 diff=rnd(curid);
        diff=1.-step(diff, vec3(0.8));
        if(dot(diff,vec3(1))<0.2) diff=vec3(1);
        #else
        vec3 diff=vec3(1);
        #endif

        vec3 depth = exp(-distance(prevhit,p) / diff*0.04);

        col += diff * sky(rn) * fre * prod;

        float ior=1.2 - fringe*0.1;
        vec3 ref = refract(r,n, side<0.0? ior : 1.0/ior);
        if(dot(ref,ref)>0.1) {

          r=ref;
        } else {

          r=rn;
        }
        prod *= depth;
        prod *= 1.-fre;

        side = -side;
        p-=n*0.1;
      } else {
        r=rn;

        prod *= pow(fre,0.5);
        //prod *= pow(1-fre,10);
        //prod *= 0.0;
        p+=n*.1;
        //break;
      }

      d = 0.1;
      prevhit = p;
      //break;
    }
    if(d>50.) break;
    p+=r*d;
  }

  col *= fcol;

  col += sky(r)*prod;

  //col *= 1.2-length(uv);

  col *= pow(col, vec3(0.4545));

  col=clamp(col, 0.,1.);
  col *= 1.2-length(uv)*1.1;

  fragColor = vec4(col, 1);
}

float map(float value, float min1, float max1, float min2, float max2) {
  return min2 + (value - min1) * (max2 - min2) / (max1 - min1);
}
vec3 hsb2rgb( in vec3 c ){
    vec3 rgb = clamp(abs(mod(c.x*6.0+vec3(0.0,4.0,2.0),
                             6.0)-3.0)-1.0,
                     0.0,
                     1.0 );
    rgb = rgb*rgb*(3.0-2.0*rgb);
    return c.z * mix(vec3(1.0), rgb, c.y);
}

vec3 getColor(float n) {
	vec3 col = vec3(0);
    float l = 1./log(2.);
    col.x = (1.-cos(l*n))/2.;
    col.y = (1.-cos(1./(3.*sqrt(2.))*l*n))/2.;
    col.z = (1.-cos(1./(7.*pow(3., 1./8.))*l*n))/2.;

    /*vec3 palette[4];
    palette[0] = vec3(0.);
    palette[1] = vec3(0., 0., 1.);
    palette[2] = vec3(1.);
    palette[3] = vec3(0., 1., 0.);

    return palette[n%4];
	*/
    return col;
}
void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    float size = pow(10., -iTime/3. + 0.5);
    vec2 pos = vec2(-0.743643887037151, 0.13182590420533);
    float limit = 800.;//(iMouse.y*iMouse.y);
    vec2 res = normalize(iResolution.xy);
    float ca = map(fragCoord.x, 0., iResolution.x, pos.x-size*res.x, pos.x+size*res.x);
    float cb = map(fragCoord.y, 0., iResolution.y, pos.y-size*res.y, pos.y+size*res.y);
    float a = 0.;//iMouse.x / iResolution.x * 2. - 1.;
    float b = 0.;//iMouse.y / iResolution.y * 2. - 1.;
    float n = 0.; 
    float log2 = log(2.);
    while(n < limit) {
        float aa = a*a - b*b;
        float bb = 2.*a*b;
        a = aa+ca;
        b = bb+cb;
        n++;
        if(a*a+b*b > 4.) { 
            n = n + 1. - log((log(a*a+b*b)/2.)/log2)/log2;
        	break;
        }


    }
	vec3 col;
    if(n < limit) {
        col = getColor(n);

    } else {
    	col = vec3(0.);
    }
    fragColor = vec4(col, 1.);

}

float tr = 50.0;
#define P(t,tt) vec3(cos(ti+t) * tr, sin(ti) * 0.1, sin(ti+tt) * tr);
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 p = (fragCoord.xy * 2.0 - iResolution.xy) / min(iResolution.x, iResolution.y);
    float ti = iTime * 2.;
    vec3 ro = P(0.,0.);
    vec3 ta = P(sin(ti), cos(ti));
    vec3 fo = normalize(ta-ro);
    vec3 ri = normalize(cross(vec3(cos(ti*0.5),sin(ti*0.5),0.), fo));
    vec3 up = normalize(cross(fo,ri));
    vec3 ray = mat3(ri,up,fo) * normalize(vec3(p, 1.5));

    float t = 0.0;
    vec3 col = vec3(0.);
    float a = 1.0;
    for(int i=0;i<300;i++) {
        vec3 pos = ro + ray * t;
    	float d = -length(vec2(length(pos.xz) - tr, pos.y)) + 5.0;
        if (d < 0.001) {
    		vec2 uv = vec2(atan(pos.z, pos.x), atan(pos.y, length(pos.xz) - tr)) / (acos(-1.)*2.) + 0.5;
            float c = smoothstep(0.05, 0.00, abs(fract(uv.y * 5.0) - 0.5));
            c = mix(c, 1.0, smoothstep(0.1, 0.00, abs(fract(uv.x * 10.0) - 0.5)));
            col += mix(vec3(c), mix(vec3(7., 4., 2.), vec3(2., 4., 7.), sin(ti) * 0.5 + 0.5), 1.0-exp(-t * 0.005)) * a;
            a *= 0.25;
            t = 0.002;
            ro = pos;
            ray = reflect(ray, normalize((normalize(vec3(pos.x, 0., pos.z)) * tr) - pos));
        }
        t += d;
    }
    fragColor = vec4(col,1.0);
}

// noise by IQ
float fbm( vec3 p, vec3 n )
{
	p *= 0.15;
    n = abs(n);
	float x = texture( iChannel3, p.yz ).x;
	float y = texture( iChannel3, p.zx ).x;
	float z = texture( iChannel3, p.xy ).x;

	return (x*n.x + y*n.y + z*n.z);
}

float hash( float n )
{
    return fract(sin(n)*43758.5453);
}

vec3 hash3( float n )
{
    return fract(sin(vec3(n,n+1.0,n+2.0))*vec3(43758.5453123,22578.1459123,19642.3490423));
}

vec3 random3f( vec3 p )
{
#if 1
	return textureLod( iChannel0, (p.xy + vec2(3.0,1.0)*p.z+0.5)/256.0, 0.0 ).xyz;
#else
    return fract(sin(vec3( dot(p,vec3(1.0,57.0,113.0)), 
                           dot(p,vec3(57.0,113.0,1.0)),
                           dot(p,vec3(113.0,1.0,57.0))))*43758.5453);
#endif	
}
float noise( in vec3 x )
{
    vec3 p = floor( x );
    vec3 f = fract( x );

    float res = 1.0;
    for( int k=-1; k<=1; k++ )
    for( int j=-1; j<=1; j++ )
    for( int i=-1; i<=1; i++ )
    {
        vec3 b = vec3( float(i), float(j), float(k) );
        vec3 r = vec3( b ) - f + random3f( p + b );
        float d = dot( r, r );
		res = min(d, res);			
    }

    return res;
}

//------------------------------------------------------------------------
// Camera
//
// Move the camera. In this case it's using time and the mouse position
// to orbitate the camera around the origin of the world (0,0,0), where
// the yellow sphere is.
//------------------------------------------------------------------------
void doCamera( out vec3 camPos, out vec3 camTar, in float time, in float mouseX )
{
    float zoom = 4.;
	camPos = vec3(0.,0.,iTime);
    camTar = camPos + vec3(0.0, 0.0, 5.0);
}


//------------------------------------------------------------------------
// Modelling 
//
// Defines the shapes (a sphere in this case) through a distance field, in
// this case it's a sphere of radius 1.
//------------------------------------------------------------------------

float doModel( vec3 p )
{
    float no = noise(p*2.);
    return pow(no, .7)*.6;
}

//------------------------------------------------------------------------
// Material 
//
// Defines the material (colors, shading, pattern, texturing) of the model
// at every point based on its position and normal. In this case, it simply
// returns a constant yellow color.
//------------------------------------------------------------------------
vec3 hueShift( vec3 color, float hueAdjust ){

    const vec3  kRGBToYPrime = vec3 (0.299, 0.587, 0.114);
    const vec3  kRGBToI      = vec3 (0.596, -0.275, -0.321);
    const vec3  kRGBToQ      = vec3 (0.212, -0.523, 0.311);

    const vec3  kYIQToR     = vec3 (1.0, 0.956, 0.621);
    const vec3  kYIQToG     = vec3 (1.0, -0.272, -0.647);
    const vec3  kYIQToB     = vec3 (1.0, -1.107, 1.704);

    float   YPrime  = dot (color, kRGBToYPrime);
    float   I       = dot (color, kRGBToI);
    float   Q       = dot (color, kRGBToQ);
    float   hue     = atan (Q, I);
    float   chroma  = sqrt (I * I + Q * Q);

    hue += hueAdjust;

    Q = chroma * sin (hue);
    I = chroma * cos (hue);

    vec3    yIQ   = vec3 (YPrime, I, Q);

    return vec3( dot (yIQ, kYIQToR), dot (yIQ, kYIQToG), dot (yIQ, kYIQToB) );
}

vec3 doMaterial( in vec3 pos, in vec3 nor )
{
    nor.x = 1.0;
    nor.y = 1.0;
    return vec3(.5, 0., 1.) * nor;
}

//------------------------------------------------------------------------
// Lighting
//------------------------------------------------------------------------

float calcIntersection( in vec3 ro, in vec3 rd )
{
	const float maxd = 10.0;           // max trace distance
	const float precis = 0.001;        // precission of the intersection
    float h = precis*2.0;
    float t = 0.0;
	float res = -1.0;
    for( int i=0; i<50; i++ )          // max number of raymarching iterations is 90
    {
        if( h<precis||t>maxd ) break;

	    h = doModel( ro+rd*t );
        t += h;
    }

    if( t<maxd ) res = t;
    return res;
}

vec3 calcNormal( in vec3 pos )
{
    const float eps = 0.002;             // precision of the normal computation

    const vec3 v1 = vec3( 1.0,-1.0,-1.0);
    const vec3 v2 = vec3(-1.0,-1.0, 1.0);
    const vec3 v3 = vec3(-1.0, 1.0,-1.0);
    const vec3 v4 = vec3( 1.0, 1.0, 1.0);

	return normalize( v1*doModel( pos + v1*eps ) + 
					  v2*doModel( pos + v2*eps ) + 
					  v3*doModel( pos + v3*eps ) + 
					  v4*doModel( pos + v4*eps ) );
}

mat3 calcLookAtMatrix( in vec3 ro, in vec3 ta, in float roll )
{
    vec3 ww = normalize( ta - ro );
    vec3 uu = normalize( cross(ww,vec3(sin(roll),cos(roll),0.0) ) );
    vec3 vv = normalize( cross(uu,ww));
    return mat3( uu, vv, ww );
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 p = (-iResolution.xy + 2.0*fragCoord.xy)/iResolution.y;
    vec2 m = iMouse.xy/iResolution.xy;

    //-----------------------------------------------------
    // camera
    //-----------------------------------------------------

    // camera movement
    vec3 ro, ta;
    doCamera( ro, ta, iTime, m.x );

    // camera matrix
    mat3 camMat = calcLookAtMatrix( ro, ta, 0.0 );  // 0.0 is the camera roll

	// create view ray
	vec3 rd = normalize( camMat * vec3(p.xy,.25));	// lens length

    //-----------------------------------------------------
	// render
    //-----------------------------------------------------

	vec3 col = vec3( 0.0, 0.0, 0.0); // background color

	// raymarch
    float t = calcIntersection( ro, rd );
    if( t>-0.5 )
    {
        // geometry
        vec3 pos = ro + t*rd;
        vec3 nor = calcNormal(pos);

        // materials
        vec3 mal = doMaterial( pos, nor );

        // lighting
        col = mal*2.5;
		col *= exp(-0.03*t*t);
	}

	//-----------------------------------------------------
	// postprocessing
    //-----------------------------------------------------
    // gamma
	col = pow( clamp(col,0.0,1.0), vec3(0.8) );
    col = hueShift(col, -length(col*1.1));
    col*= 2.;

    fragColor = vec4( col,1.0);
}

