// Parameters
const float branches = 3.;

#define PI 3.14159265

float rand(vec2 co){
    return fract(sin(dot(co.xy ,vec2(12.9898,78.233))) * 43758.5453);
}

float perlin(vec2 co) {
    vec2 q = floor(co);
    vec2 r = co - q;
    r = r * r * (3. - 2. * r);
    vec2 e = vec2(0.,1.);
    return mix(
    	mix(rand(q + e.xx), rand(q + e.yx), r.x),
        mix(rand(q + e.xy), rand(q + e.yy), r.x),
        r.y);
}

mat2 rot(float t) {
    float s = sin(t);
    float c = cos(t);
    return mat2(c, s, -s, c);
}


vec2 moda(vec2 uv, float n, out float idx) {
    float a = atan(-uv.y, -uv.x) + PI;
    float r = 2.*PI/n;
    idx = floor(a/r);
    a = mod(a, r);
    return vec2(cos(a), sin(a)) * length(uv);
}

vec2 elbow90(vec2 uv) {
    vec2 tuv = vec2(-uv.y+.295, uv.x);;
    float a = atan(uv.x, uv.y);
    vec2 ruv = vec2(2.*PI*a*.03, length(uv));
    return mix(tuv, mix(uv, ruv, step(0., uv.x)), step(0., uv.y));
}

vec2 elbow(vec2 uv, float angle) {
    vec2 tuv = rot(angle) * uv;
    vec2 thuv = rot(angle/2.) * uv;

    float a = atan(uv.x, uv.y);
    float r = length(uv);
    vec2 ruv = vec2(2.*PI*a*.03, length(uv));

    vec2 toff = vec2(+.19 * angle, 0.);
    return mix(mix(uv, ruv, step(0., uv.x)), mix(tuv + toff, ruv, step(tuv.x, 0.)), step(0., thuv.x));
}

float smin(float a, float b, float k) {
    float h = max(0., k - abs(b - a))/k;
    return min(a, b) - h*h*h*k*(1./6.);
}

float gate(float a, float b, float t) {
    return max(a-t, t-b);
}

float sinstep(float x)
{
    return (1. - cos(PI * x)) * .5;
}

vec4 worm5(vec2 uv, float len) {
    vec2 uv0 = uv;
    uv.x = -abs(uv.x - len/2.) + len/2.; // symmetry
    float head = length(uv*2.-1.) - 1.;
    float h = gate(.5, len-.5, uv.x);
    float v = gate(.0, 1., uv.y);
    float d = max(h, v);
    d = min(d, head*.5);
    vec3 color = vec3(1.-pow(sinstep(smoothstep(.5, len, uv0.x)), 1.));
    color *= vec3(.9,.92,.95);
    return vec4(color, d);
}

float fill(float d) {
    return smoothstep(.05, .0, d);
}

vec4 straitWorms(vec2 uv, float len, vec2 m, float n) {
    float margin = m.x;
    float ymargin = m.y;
    uv.y += ymargin*.3*.5;
    vec2 udx = vec2(1., floor(uv.y/.3/(1.+ymargin)));
    uv.x += udx.y*.7;
    float way = (fract(udx.y/2.)*2.-.5);
    uv.x += iTime * way;
    uv.x *= way*2.;
    vec2 guv = vec2(fract(uv.x * 3./(len+margin))*(len+margin)-margin*.5,fract(uv.y/.3/(1.+ymargin))*(1.+ymargin)-ymargin*.5);
    vec4 w = worm5(guv, len);
    //w.rgb *= vec3(1.,0.5,0.);
    w.a = max(w.a, gate(.0, .3*n, -uv.y/(1.+ymargin))*5.);
    return w;
}

vec4 secondElbowWorms(vec2 uv, float angle, float len, float margin) {
    uv += rot(angle*.5)*vec2(.39/sin(angle*.5),.0);
    uv += rot(angle)*vec2(.96/sin(angle),.0);
    vec2 euv = elbow(uv, PI-angle);
    euv.x += iTime;
    vec2 guv = vec2(fract(euv.x * 3./(len+margin))*(len+margin)-margin*.5,euv.y/.3);
    vec4 w = worm5(guv, len);
    return w;
}

vec4 compOver(vec4 under, vec4 over) {
    under.rgb = mix(under.rgb, over.rgb, fill(over.a));
    return under;
}

vec4 compAdd(vec4 under, vec4 over) {
    under.rgb += mix(vec3(0.), over.rgb, fill(over.a));
    return under;
}

vec4 line(vec2 uv, float angle, float d3, float margin) {
    vec2 diag = rot(PI-angle)*uv;
    float mdiag = (diag.y+margin*d3)*3.;
    vec4 w;
    w.rgb = vec3(.8,.6,.2);
    w.a = gate(margin*d3,margin*d3*2.,-uv.y)*5.;
    w.a = max(w.a, mdiag*2.)*1.5;
    return w;
}

float sat(float x) { return clamp(x, 0.,1.); }

float select(float id, float target) {
    return sat(1.-abs(id-target));
}

vec4 hex(vec2 uv)
{
    float odd = floor(uv.x*2.);
    float cid = floor(uv.x*2.+.5);
    uv.y += odd*sqrt(3.)/2.;
    uv.x = fract(uv.x*2.)*.5;

    vec2 uv2 = 2./3. * rot(PI/3.) * uv;
    vec2 uv3 = 2./3. * rot(PI/3.) * (vec2(-1., 1.) * uv + vec2(0., sqrt(3.)/3.));
    float gx = floor(uv2.x*2.);
    vec2 udx = vec2(floor(fract(uv2.x)*3.), floor(fract(uv3.x)*3.));
    float h = sat(udx.x - udx.y);
    float s2 = dot(mix(vec2(3., -1.), vec2(2., 1.), h), vec2(1., (udx.x + udx.y)));
    vec2 nuv = uv*sqrt(3.)/3.*2.;
    float sl = -1./sqrt(3.) * (select(udx.y, 1.)*2.-1.);
    nuv.y = fract(nuv.y+1./3.+.5+nuv.x*sl-sl*.5/sqrt(3.))-nuv.x*sl-.5+sl/3.45; // 3.45 is magick :/
    nuv.x = mix(-sqrt(3.)/3.+nuv.x, nuv.x, h);

    nuv = rot(s2*PI/3.+PI/6.) * nuv;
    return vec4(nuv, s2, cid);
}

float patternD8(vec2 uv) {
    float r1 = perlin(uv*1.+iTime);
    float r3 = (perlin(uv*1.+iTime*.1+r1) + perlin(uv*1.+vec2(-2.*iTime, 0.)))*.5;

    vec4 huv = hex(uv);
    uv = huv.xy;

    float r2 = rand(huv.ww);
    float circles = length((uv-vec2(.0,-.33))*vec2(1.2,1.))-mix(.02, .04, mod(huv.z,2.)) * mix(1.,3.,r1);
    float center = length(uv)-mix(.04, .06, r2);
    float lines = max((abs(fract(uv.x*6.)-.5)-.2)*2.5, abs(uv.y+.6)-.03 * mix(5.,.5,r3));

    float d = min(circles*2., center*2.);
    d = min(d, lines);

    return d*2.;
}


float pattern1(vec2 uv) {
    //return fill(patternD4(uv)) + fill(patternD4(uv)+.5)*.5;
    return fill(patternD8(uv));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord*2.-iResolution.xy)/min(iResolution.x, iResolution.y);
    vec2 uv0 = uv;

    float angle = 2.*PI/branches;
    float d3 = .3;

    // Frame
    float zoom = 5.;
    float ratio = iResolution.y/iResolution.x;
    float z = step(ratio, uv.x);
    uv -= vec2(-ratio*.5-.2,.2);
    uv *= 1.6;
    uv = rot(angle*.25) * uv;

    vec2 closeup = uv / zoom;
    closeup -= vec2(.1,0.);

    uv = mix(uv, closeup, z);
    zoom = mix(1., zoom, z);
    vec4 zm = vec4(1.,1.,1.,zoom);
    vec2 fuv = uv;

    float ridx = 0.;
    uv = moda(uv, branches, ridx);
    uv.xy = -uv.xy;

    float isFirst = sat(1. - mod(ridx, 3.));
    float isSecond = sat(1. - abs(mod(ridx, 3.) - 1.));
    float isThird = sat(1. - abs(mod(ridx, 3.) - 2.));

    mat2 mat = rot(PI-angle);
    uv += rot(angle*.5)*vec2(.35/sin(angle*.5),.0);
    vec2 euv = elbow(uv, PI-angle);

    // Symmetry
    vec2 vy = rot(angle*.5)*vec2(0.,1.);
    uv = mix(uv, uv - 2.*dot(uv, vy)*vy, isThird);

    float pattern = -patternD8(fuv*20.)/20.*zoom+.02*zoom;

    float mlen = 4.;
    float margin = .3;
    euv.x += iTime*.4;
    vec2 guv = vec2(fract(euv.x * 3./(mlen+margin))*(mlen+margin)-margin*.5,euv.y/.3);
    vec4 w = worm5(guv, mlen);
    w.rgb = pow(w.rgb, vec3(mix(2., .5, fill(pattern))));

    vec4 col = vec4(.01, .01, .03, 1.0);
    col = compAdd(col, w * zm);

    w = straitWorms(uv+vec2(.0,mix(0.,margin*d3*2., isThird)), mlen, vec2(margin), 2.);
    vec2 diag = rot(PI-angle)*uv;
    float mdiag = (diag.y+margin*d3)*3.;
    w.rgb *= mix(vec3(1.), vec3(.7,.75,.88), smoothstep(-.7, 0., mdiag));
    w.rgb = mix(w.rgb, vec3(.0), smoothstep(-.3, 0., mdiag));
    w.rgb = pow(w.rgb, vec3(mix(2., .5, fill(pattern))));
    w.a = max(w.a, mdiag);
    col = compAdd(col, w * zm);

    w = secondElbowWorms(uv+vec2(.21,.0), angle, mlen, margin);
    w.a = mix(1., w.a, isThird);
    w.rgb = pow(w.rgb, vec3(mix(2., .5, fill(pattern))));
    col = compAdd(col, w * zm);

    vec2 uv2 = uv;
    uv2 += mix(vec2(0.), rot(angle)*vec2(.8/sin(angle),.0), isSecond);
    w = line(uv2, angle, d3, margin);
    w.a = mix(w.a, 1., isFirst);
    w.rgb = pow(w.rgb, vec3(mix(1.5, 0.8, fill(pattern+.03))));
    col = compOver(col, w * zm);

    uv2 = uv;
    uv2 = uv2 - 2.*dot(uv2, vy)*vy;
    uv2.x += 1.11;
    w = line(uv2, angle, d3, margin);
    w.a = mix(1., w.a, isThird);
    w.rgb = pow(w.rgb, vec3(mix(1.5, 0.8, fill(pattern+.03))));
    col = compAdd(col, w * zm);

    col = mix(col, vec4(1.), step(ratio, uv0.x)*step(uv0.x, ratio + .04));

    fragColor = col;
}

