// a - retourner true si collision entre droite (orientée) et cercle
// b - retourner le point de collision
//////////////////////////////////////////////////////////////////
bool isCollision(vec2 p1, vec2 p2, vec2 center, float radius, out vec2 hitPoint) {
    vec2 d = normalize(p2-p1);
    vec2 n = vec2(-d.y, d.x);
    float distance = dot( (center - p1), n);

    float l = sqrt(radius * radius - distance * distance);
    hitPoint = center - distance * n - l * d;
    return abs(distance) <= radius ;  	 
}




