// PRIVATE
///////////////////////////////////////////////////
const float zNear = 1.0;
const float zFar  = 400.0;

bool segment(vec2 p1, vec2 p2, vec2 p) {

    vec2 v1 = p1-p;
    vec2 v2 = p2-p;    

    return length(cross(vec3(v1,0), vec3(v2,0))) < 0.002;
}

bool circle(vec2 origin, float radius, vec2 p) {
    return length(p-origin) < radius;
}


bool drawCross(vec2 p, vec2 center) {

    vec2 dv = p - center;
 	float hdist = abs(dv.x);   
 	float vdist = abs(dv.y);       

    return length(dv) < 0.02 && (hdist < 0.005 || vdist < 0.005);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float 	ratio 			= iResolution.x/iResolution.y;
    vec3 	viewportCoord 	= vec3(fragCoord.xy/iResolution.y - vec2(ratio/2.0, 0.5), zNear);

    const float radius = 0.20;
    const vec2  origin = vec2(0);

    vec2 p1 = vec2(0.2 , cos(iTime)/5.0);
    vec2 p2 = vec2(0.8, sin(iTime)/5.0);

    vec2 collisionPoint;
    bool collision = isCollision(p1, p2, origin, radius, collisionPoint);

    if( collision && drawCross(viewportCoord.xy, collisionPoint)) {
        fragColor = vec4(0,1,1,1);
    }
    else if(segment(p1, p2, viewportCoord.xy)) {
        if(collision) fragColor = vec4(1,0,0,1);
        else 		  fragColor = vec4(0,1,0,1);
    }
	else if(circle(origin, radius, viewportCoord.xy)) {
    	fragColor = vec4(1.0,0.5,0.0,1.0);
    }
    else {
		fragColor = vec4(0);        
    }
}

vec2 rotate2d(vec2 st, float a) {
    float c = cos(a);
    float s = sin(a);
    mat2 t = mat2(c,-s,s,c);
    return t*st;
}

bool ray_sphere_sect (vec3 ro, vec3 rd, vec3 sc, float r, out vec3 normal, out vec3 hp, out float dist){
    vec3 sr = sc - ro;
    float d = sqrt(pow(length(sr), 2.)-pow(r, 2.));
    vec3 cp = ro + d * rd;
    vec3 t = cp-sc;
    if (length(t) < r) {
        float lpt = sqrt(pow(r, 2.)-pow(length(t), 2.));
        vec3 p = ro + (d - lpt) * rd;
        hp = p;
        dist = length(hp - ro);
        normal = normalize(p-sc);
    }
    return length(t) < r;
}

bool ray_yplane_sect(vec3 ro, vec3 rd, float y, float x1, float x2, float z1, float z2, out vec3 hp, out float dist) {
    vec3 rb = vec3(0.0,y,0.0);
    vec3 bd=normalize(rb);
    float a = dot(bd, rd);
    float lrb = length(rb);
    float lrd = lrb/a;
    vec3 hitpoint = ro + lrd * rd;
    hp = hitpoint;
    dist = lrd;
    return hitpoint.x > x1 && hitpoint.x < x2 && hitpoint.z > z1 && hitpoint.z < z2;
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec3 st = vec3(fragCoord / iResolution.xy, 0.);
    st = st * 2.-1.;
    st.z = 0.;
    st.x*=iResolution.x/iResolution.y;

    vec3 c_light = vec3(3.,3.,-1.);
    vec3 c_light2 = vec3(-3.,2.,-1.);
    c_light = normalize(c_light);
    c_light2 = normalize(c_light2);
    st.xz = rotate2d(st.xz, -iTime*1.);
    vec3 camera = vec3(0,0,-1.5);
    camera.xz = rotate2d(camera.xz, -iTime*1.);
    vec3 ray = st-camera;
    ray = normalize(ray);
    vec3 ns,np;
    vec3 hps,hpp;
    float hds,hdp;
    bool rsr = ray_sphere_sect(camera, ray, vec3(0.),.35, ns, hps, hds);
    bool rpr = ray_yplane_sect(camera, ray, -.45 + .1*sin(iTime), -1., 1., -1.,1., hpp,hdp);
    vec3 temp, tempns,temp2,tempns2;
    float tempf,tempf2;
    vec3 light = normalize(c_light-hps);
    vec3 lightp = normalize(c_light-hpp);
    vec3 light2 = normalize(c_light2-hps);
    vec3 lightp2 = normalize(c_light2-hpp);
    vec3 camerav = normalize(camera - hps);
    vec3 camerapv = normalize(camera - hpp);

    float diff = clamp(dot(light, ns), 0.,1.) + .2;
    float diff2 = clamp(dot(lightp, vec3(0,1,0)), 0.,1.) + .1;
    float diff3 = clamp(dot(light2, ns), 0.,1.) + .2;
    float diff4 = clamp(dot(lightp2, vec3(0,1,0)), 0.,1.) + .1;

    float spec = pow(max(dot(reflect(-light, ns), camerav), .0), 64.);
    float spec2 = pow(max(dot(reflect(-lightp, vec3(0,1,0)), camerapv), .0), 64.);
    float spec3 = pow(max(dot(reflect(-light2, ns), camerav), .0), 64.);
    float spec4 = pow(max(dot(reflect(-lightp2, vec3(0,1,0)), camerapv), .0), 1024.);

    if (rsr && rpr) {
        if (hds < hdp) {
            fragColor = vec4(vec3(.4*diff + .4*diff3 + spec + spec3), 1.);
	    fragColor.rgb *= vec3(.99,.2,.2);
	} else {
	    fragColor = vec4(vec3(.4*diff2 + .4*diff4 + spec2 + spec4), 1.);
	    fragColor.rgb *= vec3(.0,.6,.99);
	}
    } else if (rsr) {
	fragColor = vec4(vec3(.4*diff + .4*diff3 + spec + spec3), 1.);
	fragColor.rgb *= vec3(.99,.2,.2);
    } else if (rpr) {
	bool shadow = ray_sphere_sect(hpp, lightp, vec3(0.), .25,tempns, temp, tempf);
	bool shadow2 = ray_sphere_sect(hpp, lightp2, vec3(0.), .25,tempns2, temp2, tempf2);
	tempf = 1./tempf;
	tempf = .15*tempf;
	tempf = pow(tempf, 3.);
	tempf2 = 1./tempf2;
	tempf2 = .15*tempf2;
	tempf2 = pow(tempf2, 3.);
	if (shadow) {
	    fragColor = vec4(vec3(.4*diff2 + .4*diff4 + spec2 + spec4), 1.);
	    fragColor.rgb *= vec3(.0,.6,.99);
	    fragColor.rgb -= tempf;
	    fragColor.rgb -= tempf2;
	} else {
	    fragColor = vec4(vec3(.4*diff2 + .4*diff4 + spec2 + spec4), 1.);
	    fragColor.rgb *= vec3(.0,.6,.99);
	}
    } else {
        fragColor = vec4(vec3(.4), 1.);
    }
}

