#define NUM_LIGHTS 1
#define MAX_REFLECTIONS 2
#define AMBIENT_LIGHT 1.61

vec3 colours[9] = vec3[](vec3(0.2,0.05,0.33), vec3(0.0,0.6,0.13), vec3(0.5,0.5,0.55), vec3(0.8,0.7,0.2), vec3(0.18,0.64,0.38), vec3(0.75,0.51,0.1), vec3(0.95,0.41,0.51), vec3(0.3,0.4,0.8), vec3(0.2,0.6,0.6));
vec3 speculars[9] = vec3[](vec3(0.9,0.9,0.9),vec3(0.9,0.9,0.9),vec3(0.9,0.9,0.9), vec3(0.9,0.9,0.9), vec3(0.9,0.9,0.9), vec3(0.9,0.9,0.9), vec3(0.9,0.9,0.9), vec3(0.9,0.9,0.9), vec3(0.9,0.9,0.9));
float shine[9] = float[](10.0,10.0,10.0,10.0,10.0,100.0,1.0,10.0,10.0);
float reflectivity[9] = float[](0.4,0.3,0.85,0.65,0.65,0.65,0.75,0.75,0.5);
vec3 intensities[9] = vec3[](vec3(0.9,0.9,0.9),vec3(0.9,0.9,0.9),vec3(0.8,0.8,0.8), vec3(0.4,0.4,0.4), vec3(0.1,0.4,0.8), vec3(0.25,0.51,0.1), vec3(0.35,0.81,0.01), vec3(0.1,0.3,0.4), vec3(0.9,0.9,0.9));

float hyperboloidSDF( vec3 p, vec3 q, float s)
{
//Hyperboloid eq
	// x2/a2 + y2/b2 - z2/c2 = 1
    // I swapped the z/y axis
	float g = pow(p.x,2.0)/ pow(q.x,2.0) - pow(p.y,2.0)/ pow(q.y,2.0) + pow(p.z,2.0)/ pow(q.z,2.0);
    return length(normalize(p) * g * s);
}

float sdfSphere(vec3 p, vec3 t, float r)
{
	return length(p-t)-r;   
}

// objects map
float distanceToClosest(vec3 p, inout int pick)
{
    float def = 500.0;

	float t = cos(iTime) * 0.05 + p.z;   
    float ev = length(p) * t;
	mat3 ry = mat3(cos(ev), 0.0, sin(ev),
                   0.0,1.0,0.0,
                   -sin(ev), 0.0, cos(ev));
	mat3 rz = mat3(cos(ev), sin(ev), 0.0,
                   -sin(ev), cos(ev), 0.0,
                   0.0,0.0,1.0);

    vec3 q = inverse(rz) * p;

    float d = hyperboloidSDF(q, vec3(2.0,2.0,2.0), sin(iTime*0.02)* 0.5);

    pick = 1;

    if (d > def)
    {
      d = def;
      pick = 0;
    }

	float s = sdfSphere(p, vec3(0.0,0.0,0.0), 0.5);
    if (s < d)
    {
     	d = s;
        pick = 2;
    }
    return d;
}

vec3 estimateNormal(vec3 p, inout int pick)
{
    const float EPSILON = 0.001;
 return normalize(vec3(
        distanceToClosest(vec3(p.x + EPSILON, p.y, p.z),pick) - distanceToClosest(vec3(p.x - EPSILON, p.y, p.z),pick),
        distanceToClosest(vec3(p.x, p.y + EPSILON, p.z),pick) - distanceToClosest(vec3(p.x, p.y - EPSILON, p.z),pick),
        distanceToClosest(vec3(p.x, p.y, p.z  + EPSILON),pick) - distanceToClosest(vec3(p.x, p.y, p.z - EPSILON),pick)
    ));   
}

