// soft shadows from iq's tutorial
float shadowMarch( vec3 lightOrigin, vec3 surfacePoint )
{
    vec3 direction = normalize(lightOrigin-surfacePoint);
    float blendFactor = 128.18;
    int pickTarget = 0;
  	float res = 1.0;
    float ph = 1e20;
    float end = distance(surfacePoint,lightOrigin);
    for( float s=0.01; s < end; )
    {
        vec3 marchStep = surfacePoint + direction * s;
        float distNow = distanceToClosest(marchStep, pickTarget);
        if( distNow<0.00085)
            return 0.0;
        float y = distNow*distNow/(2.0*ph);
        float d = sqrt(distNow*distNow-y*y);
        res = min( res, blendFactor*d/max(0.0,s-y) );
        ph = distNow;
        s += distNow;
    }
    return res; 
}
vec3 lightSurfacePoint(vec3 eye, vec3 surfacePoint, vec3 surfaceNormal, float ambientLight, int materialPick)
{
	vec3 surfaceColour = vec3(0.0);
    float shadow = 1.0;

    for (int l = 0; l < NUM_LIGHTS; l ++)
    {
        vec3 lightPos = getLightPosition(l);
        vec3 N = surfaceNormal;
    	vec3 L = normalize(lightPos - surfacePoint);
    	vec3 V = normalize(eye - surfacePoint);
    	vec3 R = normalize(reflect(-L, N));

    	float dotLN = dot(L, N);
    	float dotRV = dot(R, V);
    	vec3 colour = colours[materialPick] * 0.01;
    	if (dotLN < 0.0) 
		{
        	// Light not visible from this point on the surface
        	colour =  colours[materialPick] * 0.01;
    	} 
    	else if (dotRV < 0.0) 
		{
        // Light reflection in opposite direction as viewer, apply only diffuse
        // component
        	colour = intensities[l] * (colours[materialPick] * dotLN);
  //          shadow = min(shadow, shadowMarch(lightPos, -surfacePoint));
    	}
        else
        {
    		colour = intensities[l] * (colours[materialPick] * dotLN + speculars[materialPick] * pow(dotRV, shine[materialPick]));
    //        shadow = min(shadow, shadowMarch(lightPos, -surfacePoint));
        }

        surfaceColour += colour;//phongLight((colours[materialPick]* shadow *ambientLight), lightSpec[l], 10.7, getLightPosition(l), lightIntensity[l], surfacePoint, normal, eye);
    }
	return surfaceColour * ambientLight * shadow;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // pixel coordinates (from -1 to 1)
    vec2 uv = (fragCoord/iResolution.xy)* 2.0 - 1.0;
	uv.x *= iResolution.x/iResolution.y;

    // camera setup and ray cast
    float zoom = 1.0;
	float ext = iTime * 0.28;
    vec3 lookAt = vec3(0.0,0.0,0.0);
    vec3 camOrigin = vec3(0.0,0.0,-3.0 + (cos(ext) * 0.5));	
    vec3 forwardVec = normalize(lookAt - camOrigin);
    vec3 rightVec = normalize(cross(vec3(0.0,1.0,0.0),forwardVec));
    vec3 upVec = cross(forwardVec,rightVec);

    vec3 centerVec = camOrigin + forwardVec * zoom;
    vec3 intersectVec = centerVec + uv.x * rightVec + uv.y * upVec;
    vec3 rayDirection = normalize(intersectVec - camOrigin);

    // config and work variables
    int numReflections = MAX_REFLECTIONS;
    vec3 surfacePoint = vec3(0.0);
    vec3 col = vec3(0.1,0.3,0.44);

	float marchDistance = 0.0f;
    vec3 marchVec = camOrigin;
    int pickTarget = 0;
    vec3 surfaceNormal = vec3(0.0);

    const int MAX_STEPS = 96;
    const float MAX_DISTANCE = 96.0;
    const float CLOSE_ENOUGH = 0.0001;
	const int MAX_REFLECT_STEPS = 96;
	const float MAX_REFLECT_DISTANCE = 64.0;

    vec3 marchStep = vec3(0.0);
   	float distNow = 0.0;

    vec3 colAccum = vec3(0.0);

    for (int i = 0; i < MAX_STEPS; i ++)
    {
        marchStep = marchVec + rayDirection * marchDistance;
     	distNow = distanceToClosest(marchStep, pickTarget);

        if (distNow <= CLOSE_ENOUGH)
        {
            // hit something!
            surfacePoint = marchStep;
    		surfaceNormal = estimateNormal(surfacePoint, pickTarget);

            colAccum = lightSurfacePoint(camOrigin, surfacePoint, surfaceNormal, AMBIENT_LIGHT, pickTarget);

            vec3 lastDirection = rayDirection;
            vec3 lastSurfacePoint = surfacePoint;
            vec3 lastSurfaceNormal = surfaceNormal;

            for(int r = 0; r < numReflections; r ++)
            {

                vec3 reflectOrigin = lastSurfacePoint;
                vec3 reflectDirection = normalize(reflect((lastDirection), lastSurfaceNormal));
                float reflectDistance = 0.001;
    			vec3 reflectVec = reflectOrigin;
    			vec3 reflectNormal = vec3(0.0);

        		float reflectDist = 0.0;
                float refMixVal = 0.35;
		    	for (int k = 0; k < MAX_REFLECT_STEPS; k ++)
    			{
                    // march again from our new origin and accumulate colour from hit materials
        			reflectVec = reflectOrigin + reflectDirection * reflectDistance;
     				reflectDist = distanceToClosest(reflectVec, pickTarget);

        			if (reflectDist <= CLOSE_ENOUGH)
        			{
						// hit something else!
            			lastSurfacePoint = reflectVec;
    					lastSurfaceNormal = estimateNormal(lastSurfacePoint, pickTarget);
                        lastDirection = reflectDirection;
                        colAccum = mix (colAccum, lightSurfacePoint(reflectOrigin, lastSurfacePoint, lastSurfaceNormal, AMBIENT_LIGHT, pickTarget), mix(reflectivity[pickTarget],refMixVal,0.5));
                		refMixVal = min(0.95, refMixVal+0.1);

                        reflectDistance = 0.001;
                        reflectDist = 0.0;
                        reflectOrigin = lastSurfacePoint;
                		reflectDirection = normalize(reflect((lastDirection), lastSurfaceNormal));
                        break;

        			}
        			else
                    {
        				reflectDistance += reflectDist;   

        				if (reflectDistance >= MAX_REFLECT_DISTANCE)
        				{
            				// sky colour
            				 pickTarget = 0;
        					colAccum = mix(colAccum, colours[pickTarget] - reflectDirection.y * 0.5, mix(reflectivity[pickTarget],refMixVal,0.5));
                        	break;
        				}
                    }

    			}

            }

			break;         	
        }
        else
        {
         	marchDistance += distNow;   
        }
        if (marchDistance >= MAX_DISTANCE)
        {
            // sky colour
        	pickTarget = 0;
			colAccum =  colours[pickTarget] - rayDirection.y * 0.5;
            break;
        }
    }

    col = colAccum;

	vec3 fog = colours[0];
	float fogamt = 1.0 - exp(-marchDistance*0.01);

	col = mix(col,fog,clamp(fogamt,0.0,1.0));
    fragColor = vec4(col,1.0);
}

vec2 random2(vec2 st){
    st = vec2( dot(st,vec2(127.1,311.7)),
              dot(st,vec2(269.5,183.3)) );
    return -1.0 + 2.0*fract(sin(st)*43758.5453123);
}

// Value Noise by Inigo Quilez - iq/2013
// https://www.shadertoy.com/view/lsf3WH
float noise(vec2 st) {
    vec2 i = floor(st);
    vec2 f = fract(st);

    vec2 u = f*f*(3.0-2.0*f);

    return mix( mix( dot( random2(i + vec2(0.0,0.0) ), f - vec2(0.0,0.0) ),
                     dot( random2(i + vec2(1.0,0.0) ), f - vec2(1.0,0.0) ), u.x),
                mix( dot( random2(i + vec2(0.0,1.0) ), f - vec2(0.0,1.0) ),
                     dot( random2(i + vec2(1.0,1.0) ), f - vec2(1.0,1.0) ), u.x), u.y);
}

float circle(vec2 st, vec2 center, float radius) {
    return smoothstep(1., 1.-0.025, distance(st, center) / radius);
}

float ring(vec2 st, vec2 center, float radius) {
	return circle(st, center, radius) - circle(st, center, radius - 0.020);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    vec2 st = (2.*fragCoord.xy - iResolution.xy)/iResolution.y;
    vec3 color = vec3(0.0);

    float r = 0.67,
        a = atan(st.y, st.x),
        noiseA = a + iTime;

    vec2 nPos = vec2(cos(noiseA), sin(noiseA));

    float n = noise(nPos),
        n2 = noise(nPos + iTime);

    r += sin(a*10.) * n*.18;
    r += sin(a*30.) * n2*.08;

    float pct = ring(st, vec2(0.), r); 
    color = vec3(0.3, 0.9, 0.9) * pct + vec3(0.3, 0.9, 0.5) * pct * n * 2.;
    fragColor = vec4(color,1.0);
}

