{ return texelFetch(iChannel0, ivec2(floor(p*512.), 0.), 0).r;}
float box(vec3 p, vec3 b)
{
	vec3 d = abs(p)-b;
    return max(d.x,max(d.y,d.z));//+length(max(vec3(0.),d));
}
float portal(vec3 p, float a)
{
    p = abs(p);
    p.x -= 2.5;
    p.xy *= rot(a);
    return box(p, vec3(.25,4.,.25));
}
float at = 0.;
float mat = 0.;
float map(vec3 p)
{
    vec3 sp = p;
    float pl = p.y + 0.5;
 	float s = 7.;
    float r = length(p)*.07;//*(.6+.5*sin(iTime*.5));
    float rr = pow(r, 2.5);
    for(int i=0; i<3; i++)
    {
        p.xz *= rot(float(i)*.7);
        p.yz *= rot(float(i)*.3*iTime*.3);
        p = abs(p);
        p -= s*rr;
        s *= .65;
    }
    float b = portal(p, .8*rr)/(r*1.6);
    float c = length(p)-.4/r;
    at += .25/(.4+c);
    mat = c < b ? 1. : 0.;
