// Reference ... http://blog.ruslans.com/2015/01/raymarching-christmas-tree.html
//
const float PI = 3.1415926;

vec3 hsv(float h, float s, float v){
    vec4 t = vec4(1.0, 2.0 / 3.0, 1.0 / 3.0, 3.0);
    vec3 p = abs(fract(vec3(h) + t.xyz) * 6.0 - vec3(t.w));
    return v * mix(vec3(t.x), clamp(p - vec3(t.x), 0.0, 1.0), s);
}
mat2 rotate(in float r){
    float c = cos(r), s = sin(r);
    return mat2(c, -s, s, c);
}

float sdPlane(in vec3 p, in vec3 n, in float offset){
    return dot(p, n) - offset;
}
float sdSphere(in vec3 p, in float r){
    return length(p)-r;
}
float sdTorus(in vec3 p, in vec2 r){
    vec2 q = vec2(length(p.xz)-r.x, p.y);
    return length(q) - r.y;
}
float sdCylinder(in vec3 p, in float r){
    return length(p.xz) - r;
}
float sdCone(in vec3 p, in vec2 n){
    return dot(vec2(length(p.xz), p.y), n);
}

vec2 opU(in vec2 d1, in vec2 d2){
    return d1.x<d2.x ? d1 : d2;
}
vec2 opS(in vec2 d1, in vec2 d2){
    return vec2(
            (d1.x<-d2.x ? -d2.x : d1.x),
            d1.y
        );
}
vec2 opI(in vec2 d1, in vec2 d2){
    return vec2(
        (d1.x<d2.x ? d2.x : d1.x),
        d1.y
    );
}

vec3 opRep(in vec3 p, in vec3 c){
    return mod(p, c)-0.5*c;
}
vec2 opRepAngle(in vec2 p, in float n){
    float angle = 2.0*PI / n;
    float sector = floor(atan(p.x,p.y)/angle + 0.5);
    p *= rotate(sector * angle);
    return p;
}
vec3 opRepAngleS(in vec2 p, in float n){
    float angle = 2.0*PI / n;
    float sector = floor(atan(p.x,p.y)/angle + 0.5);
    p *= rotate(sector * angle);
    return vec3(p, mod(sector, n));
}

float star(in vec3 p, in float r){
    p.xy = opRepAngle(p.xy, 5.0);
    p = abs(p);
    vec3 n = vec3(0.5, 0.25, 0.8);
    return sdPlane(p, n, r);
}
float topper(in vec3 p){
    float s = star(p, 0.3);
    float c = max(sdCylinder(p, 0.2), sdPlane(abs(p-vec3(0., -0.5, 0.)), vec3(0.,1.,0.), 0.5));
    // return c;
    return min(s, c);
}
vec2 bauble(in vec3 p){
    float t = sdTorus(p.yxz-vec3(0.7, 0., 0.), vec2(0.1, 0.02));
    float c = max(sdCylinder(p, 0.08), sdPlane(abs(p-vec3(0., 0.55, 0.)), vec3(0., 1., 0.), 0.1));
    float s = sdSphere(p, 0.5);
    vec2 res = opU(
            vec2(min(t,c), 0.8),
            vec2(s, 1.0)
        );
    return res;
}
#define NEEDLE_LENGTH           0.5
#define NEEDLE_SPACING          0.2
#define NEEDLE_THICKNESS        0.02
#define NEEDLES_RADIAL_NUM      17.0
#define NEEDLE_BEND             0.99
#define NEEDLE_TWIST            1.6
#define NEEDLE_GAIN             1.5
float needles(in vec3 p){
    p.xy *= rotate(-length(p.xz)*NEEDLE_TWIST);    // 7. twist the needles
    p.xy = opRepAngle(p.xy, NEEDLES_RADIAL_NUM);         // 6. replicate the needles radially
    p.yz *= rotate(-NEEDLE_BEND);                  // 5. rotate the row of needles to align back with Z axis
    p.y -= p.z*NEEDLE_GAIN;                             // 4. skew the row of needles down along Y
    p.z = min(p.z, 0.0);                                // 3. remove the Z+ part
    p.z = opRep(p, vec3(NEEDLE_SPACING)).z;                  // 2. clone the needle along Z
    return max(
            sdCone(p-vec3(0., NEEDLE_LENGTH, 0.), vec2(NEEDLE_LENGTH, NEEDLE_THICKNESS)),
            -sdPlane(p, vec3(0.,1.,0.), 0.)
        );    // 1. A single needle (cone)
}
#define STEM_THICKNESS          0.05
vec2 branch(in vec3 p) {
    vec2 res = vec2(needles(p), 1.0);
    float s = sdCylinder(p.xzy, STEM_THICKNESS);
    s = max(s, sdPlane(p, vec3(0.0, 0.0, 1.0), 0.0));
    vec2 stem = vec2(s, 0.3);
    res = opU(res, stem);
    return res;
}
#define BRANCH_ANGLE            0.5
#define BRANCH_ANGLE_FADE  0.11
#define BRANCH_SPACING          1.3
#define BRANCH_NUM_MAX          9.0
#define BRANCH_NUM_FADE         2.2
#define BRANCH_CURVATURE      0.08

#define TREE_H                  4.0
#define TREE_R                  1.0
#define TRUNK_WIDTH             0.025
#define TREE2_ANGLE             0.4
#define TREE2_OFFSET            0.4
#define TREE2_SCALE             0.9

vec2 halfTree(vec3 p) {
    float section = floor(p.y/BRANCH_SPACING);
    float numBranches =  max(2.0, BRANCH_NUM_MAX - section*BRANCH_NUM_FADE);
    // 6. Revolve/clone around Y:
    p.xz = opRepAngle(p.xz, numBranches); 
    // 5. Offset to get the tree foundation:
    p.z -= TREE_R; 
    // 4. Rotate to get the tree slope:
    p.yz *= rotate(BRANCH_ANGLE); 
    // 3. Clone vertically:
    p.y = opRep(p, vec3(BRANCH_SPACING)).y; 
    // Offset to compensate for the bend:
    p.y -= BRANCH_SPACING*0.2; 
    // 2. Bend it to a parabolic shape:
    p.yz *= rotate(-length(p.yz)*BRANCH_CURVATURE + BRANCH_ANGLE + section*BRANCH_ANGLE_FADE); 
    // 1. A branch:
    vec2 b =  branch(p); 
    return b;
}
vec2 tree(vec3 p) {
    //  the first bunch of branches
    vec2 res = halfTree(p); 

    // the second bunch of branches (to hide the regularity)
    p.xz *= rotate(TREE2_ANGLE);
    p.y -= BRANCH_SPACING*TREE2_OFFSET;
    p /= TREE2_SCALE;
    vec2 t2 = halfTree(p);
    t2.x *= TREE2_SCALE;
    res = opU(res, t2);

    // trunk    
    vec2 tr = vec2(sdCone(p.xyz, vec2(TREE_H*2.0, TRUNK_WIDTH)), 0.);
    res = opU(res, tr);

    res = opI(res, vec2(sdSphere(p - vec3(0.0, TREE_H*0.5 + 1.0, 0.0), TREE_H + 0.8), 0.0));    
    return res;
}

vec2 map(in vec3 p){
    vec2 res = tree(p-vec3(0., -3.0, 0.));
    return res;
}

vec2 castRay(in vec3 ro, in vec3 rd){
    float minD = 0.0;
    float maxD = 20.0;

    float d = minD, m = -2.0;
    for(int i=0;i<100;i++){
        vec3 pos = ro + rd*d;
        vec2 tmp = map(pos);
        if(tmp.x<0.00001 || maxD<tmp.x) break;
        d += tmp.x*0.7;
        m = tmp.y;
    }
    if(maxD<d) m = -1.0;
    return vec2(d, m);
}

vec3 calcNormal(in vec3 p){
    vec2 e = vec2(1.0, -1.0)*0.0001;
    return normalize(vec3(
            e.xyy*map(p+e.xyy).x +
            e.yxy*map(p+e.yxy).x +
            e.yyx*map(p+e.yyx).x +
            e.xxx*map(p+e.xxx).x
        ));
}

vec3 render(in vec3 ro, in vec3 rd){
    vec2 res = castRay(ro, rd);
    float d = res.x;
    float m = res.y;

    vec3 pos = ro + rd*d;
    vec3 nor = calcNormal(pos);

    vec3 ld = normalize(vec3(1.0));

    vec3 color = vec3(0.);
    if(0.<=m)color = vec3(dot(ld, nor)) * hsv(m/5.0, 1.0, 1.0);

    color *= exp(-0.01*d);

    return color;
}

mat3 lookAt(in vec3 eye, in vec3 tar, in float r){
    vec3 cw = normalize(tar-eye);
    vec3 cp = vec3(sin(r), cos(r), 0.);
    vec3 cu = normalize(cross(cp, cw));
    vec3 cv = normalize(cross(cw, cu));
    return mat3(cu, cv, cw);
}


