float box(vec3 p, vec3 c)
{
    //p = mod(p, 2.5) - 2.5 * 0.5;
    vec3 q = abs(p)-c;
    return min(0.0, max(q.x, max(q.y, q.z))) + length(max(q,0.0));
}

float map(vec3 p)
{
	//p.x = mod(p.x, 2.5) - 2.5 * 0.5;
	float cube = box(p, vec3(0.6));

	float one = length(p + vec3(0.,-0.6,.0)) - 0.1;
	float two = min(length(p + vec3(0.6,-0.3,0.3)) - 0.1, 
			length(p + vec3(0.6,0.3,-0.3)) - 0.1);
	float three = min(length(p + vec3(0.0, 0.0,0.6)) - 0.1,
			  min(length(p + vec3(0.3, 0.3,0.6)) - 0.1,
			      length(p + vec3(-0.3, -0.3,0.6)) - 0.1));
	float four = min(length(p + vec3(0.3,-0.3,-0.6)) - 0.1,
			 min(length(p + vec3(-0.3,-0.3,-0.6)) - 0.1,
			 min(length(p + vec3(-0.3,0.3,-0.6)) - 0.1,
			     length(p + vec3(0.3,0.3,-0.6)) - 0.1)));

	float five = min(length(p + vec3(-0.6,-0.3,0.3)) - 0.1,
			 min(length(p + vec3(-0.6,-0.3,-0.3)) - 0.1,
			 min(length(p + vec3(-0.6,0.3,-0.3)) - 0.1,
			 min(length(p + vec3(-0.6,0.3,0.3)) - 0.1,
			     length(p + vec3(-0.6,0.0,0.0)) - 0.1))));

	float finalNumbers = min(one, min(two, min(three, min(four, five))));

	return max((length(p) - 1.0), max(-finalNumbers, cube));
}

vec3 minVec4(vec4 a, vec4 b) 
{
    return (a.a < b.a) ? a.rgb : b.rgb;
}

vec3 mapColor(vec3 p)
{
    //p.x = mod(p.x, 2.5) - 2.5 * 0.5;
	float cube = box(p, vec3(0.6));

	float one = length(p + vec3(0.,-0.6,.0)) - 0.1;
	float two = min(length(p + vec3(0.6,-0.3,0.3)) - 0.1, 
			length(p + vec3(0.6,0.3,-0.3)) - 0.1);
	float three = min(length(p + vec3(0.0, 0.0,0.6)) - 0.1,
			  min(length(p + vec3(0.3, 0.3,0.6)) - 0.1,
			      length(p + vec3(-0.3, -0.3,0.6)) - 0.1));
	float four = min(length(p + vec3(0.3,-0.3,-0.6)) - 0.1,
