	vec3 rd = normalize(vec3(uv, 2.0) + vec3(0.0,-1.0,0.0));
    //rd = normalize(vec3(uv,2.0));

	ro.xz = rotate(ro.xz,iTime * 0.1);
	p.xz = rotate(p.xz, iTime * 0.1);
	rd.xz = rotate(rd.xz, iTime * 0.1);

	float hit = 0.0;

	for(float i = 0.; i < 50.0; ++i)
	{
		float df = map(p);

		if(df < 0.001)
		{
			hit = 1.0;
			break;
		}

		p += df * rd;
	}
	vec3 n = normal(p);
	vec3 l = vec3(1.0,2.0,1.0);
	l.xz = rotate(l.xz, iTime);

	vec3 finalColor = mix(vec3(0.0,0.0,0.0), mapColor(p), vec3(light(n,l)));

	float fresnel = pow(1.-dot(n,-rd), 2.0);

	vec3 fresnelColor = vec3(1.0,1.0,1.0);

	finalColor = mix(finalColor, fresnelColor, fresnel);


	fragColor = vec4(finalColor, 1.0);
}

#define R iResolution.xy
mat2 r (float a) {
    float s = sin(a), c = cos(a);
	return mat2(c,-s,s,c);
}
vec4 ro (vec2 U) {
    vec3 v = vec3(2.*U,1);
    v.yz *= r (iTime);
    v.zx *= r (iTime);
    return vec4(v,(length(v.xy)-abs(v.z)));
}
void mainImage( out vec4 Q,vec2 U )
{
 	U = 2.*(U-0.5*R)/R.y;

    vec4 
        v = ro(U),
    	n = ro(U+vec2(0,1)/R),
    	e = ro(U+vec2(1,0)/R),
    	s = ro(U-vec2(0,1)/R),
    	w = ro(U-vec2(1,0)/R);
    vec3 g = normalize(vec3(e.w-w.w,n.w-s.w,.01));
    float h = dot(g,ro(vec2(1,0)).xyz);
    Q = .2*vec4(0.5+0.5*h*h*h)*smoothstep(0.,-0.1,v.w);
    Q.xz += smoothstep(.1,.05,abs(v.w));
    Q += smoothstep(.05,0.025,abs(v.x));
    Q += smoothstep(.05,0.025,abs(v.y));
    Q += smoothstep(.05,0.025,abs(v.z));
    Q.xy += smoothstep(.25,.2,length(v.xy-v.z*vec2(cos(3.*iTime),sin(3.*iTime))));
    Q.yz += smoothstep(.25,.2,length(v.xy-v.z*vec2(-cos(3.*iTime),-sin(3.*iTime))));
	Q *= exp(-length(U));



}

/*
	Anti-aliasing and some SDF functions adapted from
	Vaporwave Polyhedra by yx
	https://www.shadertoy.com/view/3d2XD1
*/

#define pi (acos(-1.))
#define tau 6.28318530717958647692
#define repeat(p,r) (mod(p,r)-r/2.)
#define NUM_SHAPES 4.

const float DURATION = .25;
const float REFLECTION_STRENGTH = 1.;
const float FRESNEL_STRENGTH = .5; // 0. - 1.

const float phi = (1.+sqrt(5.))*.5;

vec3 BLUE = vec3(0., 0., 1.);
vec3 WHITE = vec3(1.);
vec3 RED = vec3(1., 0. ,0.);
vec3 BLACK = vec3(0.);
vec3 GREEN = vec3(0., 1., 0.);

vec3 SKY_COLOR;
vec3 SHAPE_COLOR;
vec3 CAGE_COLOR;

int mat;
int matB;
vec3[3] colorScheme1;
vec3[3] colorScheme2;
vec3[3] colorScheme3;
vec3[3] colorScheme4;

// Order is always [sky, shape, cage]
void initColorSchemes() {
    colorScheme1 = vec3[](BLUE, RED, WHITE);
    colorScheme2 = vec3[](RED, WHITE, BLUE);
    colorScheme3 = vec3[](GREEN, BLACK, WHITE);
    colorScheme4 = vec3[](BLACK, WHITE, GREEN);
}

mat2 matRotation(float angle) {
    float c = cos(angle), s = sin(angle);
    return mat2(c, s, -s, c);
}

vec3 lerpVecs(vec3 a, vec3 b, float amount) {
  float x = mix(a.x, b.x, amount);
  float y = mix(a.y, b.y, amount);
  float z = mix(a.z, b.z, amount);
  return vec3(x,y,z);
}

float easeInOutQuad (float time, float start, float end, float duration) {
    if ((time/=duration/2.) < 1.) return end/2.*time*time + start;
    return -end/2. * ((--time)*(time-2.) - 1.) + start;
}

float sdTetrahedron(vec3 p, float r) {
    float x = abs(p.x+p.y)-p.z;
    float y = abs(p.x-p.y)+p.z;
    float m = max(x,y);
    return (m-r)/sqrt(3.);
}

float sdOctahedron(vec3 p, float r) {
    p = abs(p);
    float m = p.x+p.y+p.z-r;
    return m*0.57735027;
}

float sdIcosahedron(vec3 pos, float r) { 
    vec3 p = pos;
    p = abs(p/r);

    const float q = (sqrt(5.)+3.)/2.;
    const vec3 n1 = normalize(vec3(q,1,0));
    const vec3 n2 = vec3(sqrt(3.)/3.);

    float a = dot(p, n1.xyz);
    float b = dot(p, n1.zxy);
    float c = dot(p, n1.yzx);
    float d = dot(p, n2.xyz)-n1.x;
    return max(max(max(a,b),c)-n1.x,d)*r;
}

float sdDodecahedron(vec3 p, float r) {
    p = abs(p);
    p += phi*p.zxy;
    return (max(max(p.x,p.y),p.z)-r*phi) / sqrt(phi*phi+1.);
}


float sdRhombicTriacontahedron(vec3 p, float r)
{
    const float l = phi*2.;
    p = abs(p);
    float a = max(max(p.x,p.y),p.z);
    p += (p+p.yzx)*phi+p.zxy;
    return max(a, max(p.x,max(p.y,p.z))/l)-r;   
}

float sdTorus(vec3 p, vec2 t) {
    vec2 q = vec2(length(p.xz)-t.x, p.y);
    return length(q) - t.y;
}

float outerCage(vec3 p, float size) {
    return max(
        min(
			sdDodecahedron(p.zyx,1.03*size),
			sdIcosahedron(p,1.025*size)
        ),
		-sdRhombicTriacontahedron(p,1.019*size)
    );
}

float tetraCage(vec3 p, float size, float weight) {
    float a = max(
        -sdTetrahedron(p + vec3(0.,-weight,0.), size),
        sdTetrahedron(p, size)
    );

    float b = max(
        -sdTetrahedron(p + vec3(0.,weight,0.), size),
        sdTetrahedron(p, size)
    );

    float c = max(
         -sdTetrahedron(p + vec3(weight,0.,0.), size),
        sdTetrahedron(p, size)
    );

    float d = max(
         -sdTetrahedron(p + vec3(0.,0.,weight), size),
        sdTetrahedron(p, size)
    );

    float n = max(b, a);
    float o = max(c, d);

    return min(
        max(
            -sdTetrahedron(p + vec3(-weight, 0., 0.), size),
            min(n,o)
        ),
        n
    );
}

float octaCage(vec3 p, float size, float weight) {
    return max(
        max(
            sdOctahedron(p, size+weight),
            -sdTetrahedron(p, size)
        ),
        -sdTetrahedron(p*vec3(-1.), size)
    );
}

float icoCage(vec3 p, float size, float weight) {
    return max(
		sdIcosahedron(p,size*(1.+weight)),
        -sdRhombicTriacontahedron(p,size)
    );
}

float cage1(vec3 p, vec2 t) {
    return min(
        sdTorus(p, t*vec2(1., 2.)),
        sdTorus(p.yxz, t*vec2(1., 2.))
    );
}

float cage2(vec3 p, vec2 t) {
    vec3 tetraP = p;
    tetraP.xy *= matRotation(pi * 0.);
    tetraP.xz *= matRotation(pi * -.25);
	return tetraCage(tetraP, t.x, t.y);
}

float displacedCage2(vec3 p, float size, float width, float width2, float noise) {
    float displacement = (sin(10.*noise*p.x+iTime)*sin(10.*noise*p.y+iTime)*sin(10.*noise*p.z+iTime)*.02);
    return max(
        cage2(p, vec2(size, width))+displacement,
        cage2(p, vec2(size*1.001, width2))
    );
}

float cage3(vec3 p, float size, float weight) {
    return octaCage(p, size, weight);
}

float cage4(vec3 p, float size, float weight) {
    return icoCage(p, size, weight);
}

vec2 makeShapes(vec3 pos, float multiplier) {
    vec3 p = pos;

    vec2 pRot = vec2(iTime*.1,0)+1.0*tau*(iMouse.xy-iResolution.xy*.5)/iResolution.x;
        p.yz = cos(pRot.y)*p.yz + sin(pRot.y)*p.zy*vec2(1,-1);
        p.xz = cos(pRot.x)*p.xz + sin(pRot.x)*p.zx*vec2(1,-1);

    float baseSize = .7 * multiplier;
    float maxCageSize = baseSize * 2.;
    float baseCageSize = baseSize * 1.01;

    float finalShape;
    float finalCage;

    float sphere = (length(p)-baseSize);
    float shape1 = sphere;

    vec3 tetraP = p;
    tetraP.xy *= matRotation(pi * 0.);
    tetraP.xz *= matRotation(pi * -.25);
 	float shape2 = sdTetrahedron(tetraP, baseSize);

    float shape3 = sdOctahedron(p, baseSize*1.5);

    float shape4 = sdIcosahedron(p, baseSize);

    float timeBlock = mod(iTime*.5, NUM_SHAPES);


    float CAGE_DURATION = DURATION / 2.;

    float easedTime;
    float cageSize;
    float cageWidth;

    finalCage = cage1(p, vec2(5., -.1));
    float noise = texture(iChannel0, (pos.xz*.5)+iTime*.1, 0.).r;
    float minWidth = .000001*noise;
 	float maxWidth1 = .025*noise;


    if (timeBlock <= DURATION) {
        easedTime = easeInOutQuad(timeBlock, 0., 1., DURATION);
        SKY_COLOR = colorScheme1[0];
        SHAPE_COLOR = mix(colorScheme4[1], colorScheme1[1], easedTime);
        CAGE_COLOR =  colorScheme1[2];
        finalShape = mix(shape1, shape2, easedTime);

         if (timeBlock >= CAGE_DURATION) {
            float ease = easeInOutQuad(timeBlock-CAGE_DURATION, 0., 1., CAGE_DURATION);
            cageSize = mix(
                maxCageSize,
                baseCageSize,
                ease
            );
            cageWidth = mix(
                0.01,
                1.,
                ease
            );
            finalCage = displacedCage2(p, cageSize, .04, cageWidth, noise);
        }


    } else if (timeBlock <= 1.) {
        finalShape = shape2;
        finalCage = displacedCage2(p, baseCageSize, .04, 1., noise);
        SKY_COLOR = colorScheme1[0];
        SHAPE_COLOR = colorScheme1[1];
        CAGE_COLOR = colorScheme1[2];

        if (timeBlock >= 1.-CAGE_DURATION) {
            cageWidth = mix(
                maxCageSize,
                0.01,
                easeInOutQuad(timeBlock-(1.-CAGE_DURATION), 0., 1., CAGE_DURATION)
            );
            finalCage = displacedCage2(p, baseCageSize, .04, cageWidth, noise);
        }

    } else if (timeBlock <= 1.+DURATION) {
        easedTime = easeInOutQuad(timeBlock - 1., 0., 1., DURATION);
        finalShape = mix(shape2, shape3, easedTime); 
        SKY_COLOR = colorScheme2[0];
        SHAPE_COLOR = mix(colorScheme1[1], colorScheme2[1], easedTime);
        CAGE_COLOR =  colorScheme2[2];

        if (timeBlock >= 1.+CAGE_DURATION) {
            float ease = easeInOutQuad(timeBlock-(1.+CAGE_DURATION), 0., 1., CAGE_DURATION);
            cageSize = mix(
                maxCageSize*1.5,
                baseCageSize*1.5,
                ease
            );
            cageWidth = mix(
                minWidth,
                .075*noise,
                ease
            );
            finalCage = cage3(p, cageSize, cageWidth);
        }

    } else if (timeBlock <= 2.) {
        finalShape = shape3;
        finalCage = octaCage(p, baseCageSize*1.5, .075*noise);
        SKY_COLOR = colorScheme2[0];
        SHAPE_COLOR = colorScheme2[1];
        CAGE_COLOR = colorScheme2[2];


        if (timeBlock >= 2.-CAGE_DURATION) {
            cageWidth = mix(
                .075*noise,
                minWidth,
                easeInOutQuad(timeBlock-(2.-CAGE_DURATION), 0., 1., CAGE_DURATION)
            );
            finalCage = cage3(p, baseCageSize*1.5, cageWidth);
        }
    } else if (timeBlock <= 2.+DURATION) {
        easedTime = easeInOutQuad(timeBlock - 2., 0., 1., DURATION);
        finalShape = mix(shape3, shape4, easedTime);
        SKY_COLOR = colorScheme3[0];
        SHAPE_COLOR = mix(colorScheme2[1], colorScheme3[1], easedTime);
        CAGE_COLOR =  colorScheme3[2];


        if (timeBlock >= 2.+CAGE_DURATION) {
            float ease = easeInOutQuad(timeBlock-(2.+CAGE_DURATION), 0., 1., CAGE_DURATION);
            cageSize = mix(
                maxCageSize,
                baseCageSize,
                ease
            );
            cageWidth = mix(
                minWidth,
                .01*noise,
                ease
            );
            finalCage = cage4(p, cageSize, cageWidth);
        }

    } else if (timeBlock <= 3.) {
        finalShape = shape4;
        finalCage = cage4(p, baseCageSize, .01*noise);
        SKY_COLOR = colorScheme3[0];
        SHAPE_COLOR = colorScheme3[1];
        CAGE_COLOR = colorScheme3[2];

        if (timeBlock >= 3.-CAGE_DURATION) {
            cageWidth = mix(
                .01*noise,
                minWidth,
                easeInOutQuad(timeBlock-(3.-CAGE_DURATION), 0., 1., CAGE_DURATION)
            );
            finalCage = cage4(p, baseCageSize, cageWidth);
        }

    } else if (timeBlock <= 3.+DURATION) { 
        easedTime = easeInOutQuad(timeBlock - 3., 0., 1., DURATION);
        finalShape = mix(shape4, shape1, easedTime);
        SKY_COLOR = colorScheme4[0];
        SHAPE_COLOR = mix(colorScheme3[1], colorScheme4[1], easedTime);
        CAGE_COLOR =  colorScheme4[2];

        if (timeBlock >= 3.+CAGE_DURATION) {
            cageSize = mix(
                maxCageSize,
                baseCageSize,
                easeInOutQuad(timeBlock-(3.+CAGE_DURATION), 0., 1., CAGE_DURATION)
            );
            cageWidth = mix(
                minWidth,
                maxWidth1,
                easeInOutQuad(timeBlock-(3.+CAGE_DURATION), 0., 1., CAGE_DURATION)
            );
            finalCage = cage1(p, vec2(cageSize, cageWidth));
        }

    } else if (timeBlock <= 4.) {
        finalShape = shape1;
        finalCage = cage1(p, vec2(baseCageSize, maxWidth1));
        SKY_COLOR = colorScheme4[0];
        SHAPE_COLOR = colorScheme4[1];
        CAGE_COLOR = colorScheme4[2];

        if (timeBlock >= 4.-CAGE_DURATION) {
            cageWidth = mix(
                maxWidth1,
                minWidth,
                easeInOutQuad(timeBlock-(4.-CAGE_DURATION), 0., 1., CAGE_DURATION)
            );
            finalCage = cage1(p, vec2(baseCageSize, cageWidth));
        }

    }

    return vec2(finalShape, finalCage);
}

float scene(vec3 p) {      
    vec3 cP = p;

    vec2 pRot = vec2(0,iTime*.2);
    cP.yz = cos(pRot.y)*p.yz + sin(pRot.y)*p.zy*vec2(1,-1);

    float outerCage = outerCage(cP, 10.);

    vec2 shapes = makeShapes(p, 1.);
    float shape = shapes.x;

    float cage = shapes.y;

    float scene = min(shape, cage);
    scene = min(scene, outerCage);
    scene = min(scene, shape*1.5); // Glossy warp

    float cageDistance = abs(scene - cage);
    float outerCageDistance = abs(scene - outerCage);

    if (cageDistance <= .001) {
        mat = 1;
    } else if (outerCageDistance <= .001) {
        mat = 2;
    } else {
        mat = 0;
    }

    return scene;
}


vec3 trace(vec3 cam, vec3 dir) {    
    float hasBounced = 0.;
    float fresnel = 0.;

    vec3 accum = vec3(1.);
    for(int bounce=0;bounce<2;++bounce) {
        float t;
        float k;
        for(int i=0;i<100;++i)
        {
            k = scene(cam+dir*t);
            t += k;
            if (k < .001 || k > 10.)
                break;
        }

        vec3 h = cam+dir*t;
        vec2 o = vec2(.001, 0);
        vec3 n = normalize(vec3(
            scene(h+o.xyy)-scene(h-o.xyy),
            scene(h+o.yxy)-scene(h-o.yxy),
            scene(h+o.yyx)-scene(h-o.yyx)
        ));

        vec3 weightedReflection = lerpVecs(vec3(1.), accum, REFLECTION_STRENGTH);

        vec3 shapeSurfaceColor = mix(SKY_COLOR, SHAPE_COLOR*weightedReflection, hasBounced);

        if (k > 10.) {
            return SKY_COLOR + shapeSurfaceColor;    
        } 

        else if (mat == 1) {
            return CAGE_COLOR;
        }
        else if (mat == 2) {
            return mix(SKY_COLOR, SHAPE_COLOR, hasBounced);
        }

        else {
            fresnel = min(.7,pow(max(0.,1.-dot(-dir,n)),5.))/(1. - FRESNEL_STRENGTH);
            fresnel=mix(.04,1.,fresnel);
            cam=h+n*.01;
            dir=reflect(dir,n);
            accum *= fresnel;
            hasBounced = 1.;
        }
    }

   	return SKY_COLOR;
}

void mainImage(out vec4 out_color, vec2 fragCoord) {
    vec2 uv = fragCoord.xy / iResolution.xy-.5;
    uv.x *= iResolution.x / iResolution.y;

    out_color=vec4(0);
    initColorSchemes();

	//Anti-aliasing
    for(int i=0;i<4;++i){
        vec2 offset=vec2(i/2,i%2)*.5/iResolution.y;

        vec3 cam = vec3((uv+offset)*3.,-5.);
        vec3 dir = vec3(0,0,1);

        out_color.rgb += trace(cam,dir);
    }
    out_color *= .25;
    out_color *= 2.; // exposure
	out_color.rgb = pow(out_color.rgb,vec3(.65)); // gamma
}

// Play with F values to get something other
#define F1 .115
#define F2 .75
#define F3 1.5

#define PI 3.1415926
#define PI2 6.2831852

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{    
    vec2 uv = (fragCoord-iResolution.xy*.5)/iResolution.y;


    float m = 0.;
    float stp = PI/5.;
    float odd = -1.;
    for(float n=1.; n<2.5; n+=0.5){        
        odd *= -1.;
        float t = iTime * odd * .3;
        for(float i=0.0001; i<PI2; i+=stp){

            vec2 uvi = uv*n + vec2(cos(i + n*stp*.5 + t)*.4, sin(i + n*stp*.5 + t)*.4);
            float l = length(uvi);
            m += smoothstep(F1 * n, .0, l)*F3;        
        }    
    }

    m = step(F2, fract(m*F3));

    vec3 col = vec3(1.) * m;

    fragColor = vec4(col,1.0);
}

#define nCos(x)		(cos(x)+1.0)*0.5
//#define bell(x)		(1.0/((x*x+1.0)*(x*x+1.0)))
#define bell(x)		(1.0/((1.0+abs(x))*(1.0+abs(x))))
#define rayGen(x) 	((abs((fract(x)-0.5)*2.0)-0.5)*2.0)


#define ITERATIONS 6

//#define USE_BLUE
#define USE_RED
//#define USE_GREEN

float rand(in int x) {
    return fract(sin(0.123456 + 9.87654*float(x))*10000.0);
}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy-0.5;
    uv.x *= iResolution.x/iResolution.y;

    // Ray
    float speed     = 1.5;
    float amplitude = 0.10;
    float freq      = 1.0;
    float parity    = 0.0;
    float dir       = 1.0;

    float halo = 0.7 * amplitude;

    float ray = 0.0;

    // Generate ray that evolve in X and Time with alternative directions (X+, X-)
    for (int i = 0; i < ITERATIONS; i++) {
        ray += amplitude * rayGen(rand(i) + uv.x * (freq + parity) + dir*speed*iTime);

        amplitude *=  0.5;
        freq	  *=  2.0;
        parity     =  0.5 - parity;
        dir       *= -1.0;
    }

    float intensity = bell(abs(uv.y - ray)/halo);

    vec4 color = vec4(0.0,0.0,0.0, 1.0);

    #ifdef USE_RED
    	color.x = intensity;
    #endif
    #ifdef USE_GREEN
    	color.y = intensity;
    #endif
    #ifdef USE_BLUE
    	color.z = intensity;
    #endif

    // Output to screen
    fragColor = color;
}

vec2 rotate(in vec2 p, in float r){
	float c=cos(r), s=sin(r);
    return mat2(c, -s, s, c)*p;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 p  = (fragCoord.xy*2.0-iResolution.xy)/min(iResolution.x, iResolution.y);

    for(int i=0;i<5;i++){
        p = rotate(p, iTime*0.2+sin(length(p)));
		p.x += p.y*sin(p.x*1.97+iTime*0.45 + sin(p.y*1.25+iTime*0.97));
		p.y += p.x*sin(p.y*1.36+iTime*0.45 + sin(p.y*1.82+iTime*0.97));
    }

    vec3 col1 = vec3(0.2, 0.5, 0.9);
    vec3 col2 = vec3(0.9, 0.95, 1.0);
    vec3 col = clamp(mix(col1, col2, min(2.0, (0.5*length(p)))), 0., 1.);
    fragColor = vec4(col,1.0);
}

float holegrid(vec2 pos, float ang, float zoom, vec2 delta) {

    vec2 tr1 = vec2(cos(ang), -sin(ang));
    vec2 tr2 = vec2(sin(ang), cos(ang));
    pos *= zoom;
    pos += delta;
    pos = vec2(
        pos.x * tr1.x + pos.y * tr1.y,
        pos.x * tr2.x + pos.y * tr2.y
    );
    //pos.y *= (1.0 + pos.y * pos.y * 0.03);
    float dt = max(0.0, fract(iTime) * 2.0 - 1.0);
    int row = int(floor(pos.y + 0.5));
    int isRowZero = 1- min(row*row, 1);
    float dt2 = max(0.0, fract(iTime + 0.5) * 2.0 - 1.0);
    int col = int(floor(pos.x + 0.5));
    int isColZero = 1- min(col*col, 1);
    pos.x += float(isRowZero) * dt;
    pos.y += float(isColZero) * dt2;
    // Time varying pixel color
    pos = mod(pos + 0.5, 1.0) -0.5;
    float d = length(pos);
    return 1.0-floor(clamp(d* 6.0 - 1.9, 0.0, 1.0));
}
vec3 calc(vec2 pos) {
    float completion = 6.283185307179586 * iTime / 15.0;
    float ang = completion;
    float zoom = 3.0;
    vec2 delta = vec2(cos(completion), sin(completion));
    float d = holegrid(pos, ang, zoom, delta);
    vec3 col1 = vec3(d, d, d);

    float ang2 = completion / 4.0;
    float zoom2 = 12.0 + sin(completion);
    vec2 delta2 = vec2(0.0, 0.0);
    float d2 = holegrid(pos, ang2, zoom2, delta2);
    vec3 col2 = vec3(1.0, d2, d2);
    return min(col1, col2);
}
