// simulated interference patterns

//some constants

#define PI 3.14159265359

// XY range of the display.

#define DISP_SCALE 8.0

// custom colormap

vec3 marine( float t )
{
	 return vec3(max(3.0 * t + 3.0, 19.0 * t - 5.0)/18.0, max(10.0 * t, 18.0 * t - 4.0)/15.0, min(3.0 * t + 1.0, t + 2.0)/3.0);
}

// function for simulated interference pattern

float interf(vec2 p)
{
      float t = 4.0 * iTime; // phase
      vec2 p1 = vec2(-4.0, 0.0), p2 = vec2(4.0, 0.0); // sources

      if (iMouse.xy != vec2(0.0))
      {
          p2 = DISP_SCALE * ( iMouse.xy - 0.5 * iResolution.xy) / iResolution.y; // move one source
      }

      return cos(2.0 * PI * distance(p, p1) - t) + cos(2.0 * PI * distance(p, p2) - t);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
        vec2 aspect = iResolution.xy / iResolution.y;
        vec2 uv = ( fragCoord.xy / iResolution.y ) - 0.5 * aspect;
        uv *= DISP_SCALE;

        float z = interf(uv);
        fragColor.rgb = marine(0.25 * (z + 2.0));

        // gradient using forward differences
        float h = 0.5 * DISP_SCALE/iResolution.y;
        vec2 grad = z - vec2(interf(uv + vec2(h, 0.0)), interf(uv + vec2(0.0, h)));

        // lighting angles for shading
        float th = 0.75 * PI, ph = 0.25 * PI;

        // shading factor
        fragColor.rgb *= 1.0 - 0.25 * (1.0 + cos(ph) * dot(vec2(cos(th), sin(th)), grad)/(pow(h, 1.4) + length(grad)));

        fragColor.a = 1.0;
}

