float gyroidSDF(vec3 p , float s)
{
//	float g = cos(p.x) * sin(p.y) + cos(p.y) * sin(p.z) + cos(p.z) * sin(p.x);
    float g = dot(cos(p),sin(p.yzx));	// shorthand suggested by Shane
    return length(normalize(p) * g * s);
}

float genusSDF(vec3 p, vec3 q, float s)
{
 //2y(y2-3x2)(1-z2)+(x2+y2)2 - (9z2-1)(1-z2) = 0   
	//float g = 2.0 * p.y * (pow(p.y,2.0)- pow(3.0 * p.x,2.0)) * (1.0-pow(p.z,2.0)) + pow(pow(p.x,2.0)+pow(p.y,2.0),2.0) - (pow(9.0*p.z,2.0)-1.0)*(1.0-pow(p.z,2.0));
//	float g = q.y * 2.0 * p.y * (pow(p.y,2.0)-  pow(q.x * 3.0 * p.x,2.0)) * (q.z * 1.0 -pow(p.z,2.0)) + pow(pow(p.x,2.0)+pow(p.y,2.0),2.0) - ( pow(q.z * 9.0 * p.z,2.0)-1.0)*(q.z-pow(p.z,2.0));
	float g = q.y * p.y * (pow(p.y,2.0)-  pow(q.x * p.x,2.0)) * (q.z-pow(p.z,2.0)) + pow(pow(p.x,2.0)+pow(p.y,2.0),2.0) - ( pow(q.z * p.z,2.0)-1.0)*(q.z-pow(p.z,2.0));
//	float g = p.y * (pow(p.y,2.0)- pow(p.x,2.0)) * (pow(p.z,2.0)) + pow(pow(p.x,2.0)+pow(p.y,2.0),2.0) - (pow(p.z,2.0)-1.0)*(1.0-pow(p.z,2.0));

    return length(normalize(p) * g * s);
}

