float getInRange(float a, float b, float d)
{
	return (d-a)/(b-a);   
}

float remapValue(float originStart, float originEnd, float remapStart, float remapEnd, float v)
{
 	return getInRange(originStart,originEnd,v) * (remapEnd-remapStart) + remapStart;   
}

float sceneSDF(vec3 p)
{
	//float t = mod(iTime,10.0);
    float t = iTime * 1.1;
    float ev = remapValue(-1.0,1.0, 1.6,2.0, cos(t))+1.25;

	mat3 rz = mat3(cos(ev), sin(ev), 0.0,
                   -sin(ev), cos(ev), 0.0,
                   0.0,0.0,1.0);

    vec3 q = inverse(rz) * p;
    return genusSDF(normalize(q), vec3(3.0,2.0,9.0), 0.1);
}


float distToSurface( vec3 eye, vec3 dir, float startDist, float endDist, float EPSILON )
{
	float depth = startDist;
	for (int i = 0; i < MAX_STEPS; i++) 
    {
    	float dist = sceneSDF(eye + depth * dir);
    	if (dist < EPSILON) 
        {
        // We're inside the scene surface!
        return depth;
    	}
    	// Move along the view ray
    	depth += dist;

    	if (depth >= MAX_DIST) 
        {
        	// Gone too far; give up
        	return endDist;
    	}
    }
    return endDist;
}

vec3 estimateNormal(vec3 p, float EPSILON) 
{
    return normalize(vec3(
        sceneSDF(vec3(p.x + EPSILON, p.y, p.z)) - sceneSDF(vec3(p.x - EPSILON, p.y, p.z)),
        sceneSDF(vec3(p.x, p.y + EPSILON, p.z)) - sceneSDF(vec3(p.x, p.y - EPSILON, p.z)),
        sceneSDF(vec3(p.x, p.y, p.z  + EPSILON)) - sceneSDF(vec3(p.x, p.y, p.z - EPSILON))
    ));
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
     // pixel coordinates (from -1 to 1)
    vec2 uv = (fragCoord/iResolution.xy)* 2.0 - 1.0;
	uv.x *= iResolution.x/iResolution.y;

    // camera setup and ray cast
    float zoom = 1.0;
    float ext = iTime * 0.6;
    vec3 lookAt = vec3(0.0,0.0,0.0);
  //  vec3 camOrigin = vec3(-180.0 * sin(ext),0.0,-180.0* cos(ext));
    vec3 camOrigin = vec3(0.0,0.0,-80.0 + sin(ext));
	vec3 forwardVec = normalize(lookAt - camOrigin);
    vec3 rightVec = normalize(cross(vec3(0.0,1.0,0.0),forwardVec));
    vec3 upVec = cross(forwardVec,rightVec);

    vec3 centerVec = camOrigin + forwardVec * zoom;
    vec3 intersectVec = centerVec + uv.x * rightVec + uv.y * upVec;
    vec3 rayDirection = normalize(intersectVec - camOrigin);

	float EPSILON = 5.9 + cos(ext);

    float d = distToSurface(camOrigin,rayDirection, 0.0,MAX_DIST,EPSILON);
	vec3 g = estimateNormal(camOrigin + rayDirection * d,EPSILON);
  //  d = d/length(g);
  	vec3 p = camOrigin + rayDirection * d;


    if (d > MAX_DIST-EPSILON)
    {
     	// no hit
        fragColor = vec4(0.0,0.0,0.0,0.0);
        //discard;
        return;
    }
	vec3 bc = vec3(-0.1+g.x,-0.9+g.y,1.0-g.x);
	//vec3 bc = vec3(-0.4+g.y,-0.3+g.x,1.0-g.x);
    vec3 col = vec3(clamp(d,0.0,0.99))*(bc+g);

    fragColor = vec4(col,1.0);
}

