#define MAX_DIST 1500.0

#define INTEGRATE_UNBOUNDED_STUFF 1
// set to 0 to make the density of the glowy stuff 1/(distance^2)
// by default it's 1/distance, which means the total glow contribution grows with MAX_DIST
// I think it looks better with 1/distance
// but, hey, neither of these functions come from "physics"
// (or, if they do, it is only by coincidence)
//
// p.s. looks really nice when MAX_DIST is huge (thanks @CLPB for pointing this out)

const vec3 light = vec3(0.48, 0.64, -0.6);

vec3 ball1;
vec3 ball2;
vec3 ball3;

vec3 radii;

vec3 s_max_v3(in vec3 x, in vec3 y, in float s) {
    vec3 bridge =
        clamp(abs(x-y)/s, 0.0, 1.0);
    return max(x,y) + 0.25 * s * (bridge - 1.0) * (bridge - 1.0);
}

vec3 s_abs(in vec3 x, in float s) {
    return s_max_v3(x, -x, s);
}

float sdf1(in vec3 pt) {
    return length(max(vec3(0.0), abs(pt - ball1) - 0.25 * radii.x)) - 0.5 * radii.x;
}

float sdf2(in vec3 pt) {
    return length(max(vec3(0.0), abs(pt - ball2) - 0.375 * radii.y)) - 0.375 * radii.y;
        //dot(s_abs(pt - ball2, 0.25 * radii.y), vec3(0.5)) - 0.5 * radii.y;
}

float sdf3(in vec3 pt) {
    return length(max(vec3(0.0), abs(pt - ball3) - 0.5 * radii.z)) - 0.25 * radii.z;
}

vec3 sdfs(in vec3 pt) {
    return vec3(sdf1(pt), sdf2(pt), sdf3(pt));
}

float sdf(in vec3 pt) {
    return min(sdf1(pt), min(sdf2(pt), sdf3(pt)));
}

float min_comp(in vec3 comps) {
    return min(comps.x, min(comps.y, comps.z));
}

vec3 sdf_grad(in vec3 pt) {
    float f = sdf(pt);
    const float h = 0.001;
    const float h_inv = 1000.0;

    return vec3(sdf(pt + vec3(h, 0.0, 0.0)) - f,
                sdf(pt + vec3(0.0, h, 0.0)) - f,
                sdf(pt + vec3(0.0, 0.0, h)) - f);
}

float raymarch(in vec3 orig, in vec3 dir, out vec3 integral) {
    integral = vec3(0.0);
    float curr = 0.0;
    const float step_ratio = 0.25;
    vec3 curr_sdf = sdfs(orig);
    float dist = step_ratio * min_comp(curr_sdf);
    vec3 next_sdf = sdfs(orig + dir * dist);
    // integral from 0 to d of 1/(a+bx) =
    // screw it, just average some things.
    integral = dist * (0.25 / curr_sdf + 1.0 / (curr_sdf + next_sdf) + 0.25 / next_sdf);
    float total_dist = dist;
    const vec3 thresh = vec3(0.004);
    for (int i = 0; i < 128; ++i) {
        curr_sdf = next_sdf;
        dist = step_ratio * min_comp(curr_sdf);
        total_dist += dist;
        next_sdf = sdfs(orig + total_dist * dir);
        vec3 mid = 0.5 * (curr_sdf + next_sdf);
#if INTEGRATE_UNBOUNDED_STUFF        
        integral += dist * (0.25 / max(thresh, curr_sdf) + 
                            0.5 / max(thresh, mid ) + 
                            0.25 / max(thresh, next_sdf));
#else
        integral += dist * (0.25 / max(thresh, curr_sdf * curr_sdf) + 
                            0.5 / max(thresh , mid * mid ) + 
                            0.25 / max(thresh, next_sdf * next_sdf));
#endif        
        if (min_comp(next_sdf) < 1.0e-3 || total_dist > MAX_DIST) {
            return total_dist;
        }
    }
    return total_dist;
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = (2.0 * fragCoord - iResolution.xy) / iResolution.y;

    vec3 orig = vec3(0.0, 0.1, -1.25);
    vec3 dir = normalize(vec3(uv, 1.0));

    mat3 tilt = mat3(1.0, 0.0, 0.0,
                0.0, 0.28, -0.96,
                0.0, 0.96, 0.28); 
    orig = tilt * orig;
    dir = tilt * dir;

    float theta = 0.6 * iTime;
    float ct = cos(theta);
    float st = sin(theta);

    float rad = 0.6;

    ball1 = rad * vec3(ct, st, 0.0);

    ct = cos(theta + 2.0 * 3.141592654 / 3.0);
    st = sin(theta + 2.0 * 3.141592654 / 3.0);
    ball2 = rad * vec3(ct, st, 0.0);

    ct = cos(theta - 2.0 * 3.141592654 / 3.0);
    st = sin(theta - 2.0 * 3.141592654 / 3.0);
    ball3 = rad * vec3(ct, st, 0.0);

    vec3 integral;

    radii = 0.3 + 0.2 * vec3(texture(iChannel0, vec2(0.0, 0.0)).r,
                             texture(iChannel0, vec2(0.5, 0.0)).r,
                             texture(iChannel0, vec2(1.0, 0.0)).r);

    float raydist = raymarch(orig, dir, integral);

#if INTEGRATE_UNBOUNDED_STUFF    
    vec3 col = smoothstep(0.15 * (1.0 *  radii), vec3(0.0), 0.45/(integral));
#else
    vec3 col = smoothstep(0.15 * (1.0 *  radii), vec3(0.0), 1.5/(integral));    
#endif    

    col = mat3(0.7, 0.6, 0.3,
               0.5, 0.1, 0.7,
               0.0, 0.0, 1.1) * col.zyx;
    if (raydist < MAX_DIST) {
        vec3 pt = orig + raydist * dir;
        vec3 norm = normalize(sdf_grad(pt));
        vec3 bounce = normalize(reflect(dir, norm));
        col += 0.5 * smoothstep(0.45, 1.0, dot(bounce, light)) * vec3(0.9, 0.8, 1.0);
    }

    // Output to screen
    fragColor = vec4(col,1.0);
}

#define PI 3.141592
#define MAX_STEPS 255
#define MAX_DIST 100.
#define EPSILON 1.

mat2 rotate(float a) {
  return mat2(cos(a), -sin(a), sin(a), cos(a));
}

float smin( float a, float b, float k ) {
  // http://www.iquilezles.org/www/articles/smin/smin.htm
  float h = max( k-abs(a-b), 0.0 )/k;
  return min( a, b ) - h*h*h*k*(1.0/6.0);
}

float sphereSDF (vec3 p, float r) {
  return length(p) - r;
}

float displacement(vec3 p, float n) {
  return sin(p.x * n) * sin(p.y * n) * sin(p.z * n);
}

float sceneSDF (vec3 p) {
  float s1 = sphereSDF(p - vec3(-.8 * sin(iTime) * .2, .3, -.10 * cos(iTime) * .5), .5 + sin(cos(dot(p, vec3(p.y)) * 1.2 - iTime * .3) * 8. + iTime * .2) * .5 + .5);
  float s2 = sphereSDF(p - vec3(-.8 * sin(iTime) * .2, .5, -.10 * cos(iTime) * .5) * sin(length(p * sin(iTime * .01)) * 15. + cos(iTime)), 1.);

  return smin(s1 * 1.1, s2 * .8 + displacement(p - cos(atan(p.y, p.z) - iTime * .01) * .5 + .5, 5.), 1.2);
}

vec3 getNormal (vec3 p) {
  float d = sceneSDF(p);
  vec2 e = vec2(.01, 0.);

  return normalize(d - vec3(
    sceneSDF(p - e.xyy),
    sceneSDF(p - e.yxy),
    sceneSDF(p - e.yyx)));
}

float raymarch (vec3 ro, vec3 rd) {
  float depth = 0.;
  for (int i = 0; i < MAX_STEPS; i++) {
    float dist = sceneSDF(ro + rd * depth);
    if (dist < EPSILON) return depth;
    depth += dist;
    if (depth >= MAX_DIST) return 0.;
  }
  return 0.;
}

float getLight (vec3 lightPos, vec3 p) {
  vec3 light = normalize(lightPos - p);
  vec3 normal = getNormal(p);

  float diff = clamp(dot(normal, light), 0., 1.);

  return diff;
}

vec3 getRayDir (vec2 uv, vec3 rayOrigin, vec3 lookat, float zoom) {
  // https://www.youtube.com/watch?v=PBxuVlp7nuM
  vec3 forward = normalize(lookat - rayOrigin);
  vec3 right = normalize(cross(vec3(0., 1., 0.), forward));
  vec3 up = cross(forward, right);
  vec3 center = rayOrigin + forward * zoom;
  vec3 intersection = center + uv.x * right + uv.y * up;
  return normalize(intersection - rayOrigin);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
  vec2 uv = (fragCoord.xy / iResolution.xy) * 2.0 - 1.0;
  uv.x *= iResolution.x / iResolution.y;

  // camera
  vec3 ro = vec3(-.3, 1., 8.);
  ro.xz *= rotate(iTime * .02);
  vec3 rd = getRayDir(uv, ro, vec3(-.3), 2.5);

  vec3 color = vec3(0.);
  float d = raymarch(ro, rd);
  vec3 p = ro + rd * d;

  if (d > 0.) {
    vec3 lightPos1 = vec3(4. * sin(iTime * .5), 4., 6. * cos(iTime * .5));
    vec3 lightPos2 = vec3(-6., -3. * sin(iTime * .5), -1. * cos(iTime * .5));
    float diff = getLight(lightPos1 * d, p);
    color += diff * vec3(.2, .4, .8);
    color += diff * .5;
    diff = getLight(lightPos2 * d, p);
    color += diff * vec3(.8, .4, .2);
    color += diff * .4;
    color -= d * .002;

  } else {
    color += 1. - length(uv * .6);
    color *= 1. - cos(length(uv * (uv.x - uv.y)) * 20. - iTime);
    color = mix(color, vec3(0.), cos(length(uv) * 120. + iTime) * .5 + .5) * vec3(.5);
  }

  fragColor = vec4(color, 1.);
}

