    mat3 mat=mat3(1.0-sin(3.14*r.y)*cos(iTime),0.0,0.0,
                  0.0,1.0-cos(3.14*r.x)*cos(iTime),0.0,
                  0.0,0.0,1.0-sin(3.14*r.y)*cos(3.14*r.x)*cos(iTime)); 
    mat=mat/det(mat); 
    vec3 eye=vec3(0.0,0.0,-1.0); 
    vec3 pix_loc=vec3(r,0.0); 
    vec3 dir=ray_dir(eye,pix_loc);  
    vec4 pix_val=ray_march(eye,dir,mat); 

    fragColor=pix_val;

}

