const float PI = 3.14159265359;

float rand(in vec2 n){
    return fract(sin(dot(n, vec2(12.9898, 78.233)))*43758.5453);
}
float rand(in float n){
    return rand(vec2(n));
}
float noise(in float x){
    float f = fract(x);
    float i = floor(x);
    //return mix(rand(i), rand(i+1.0), f);
    return mix(rand(i), rand(i+1.0), f*f*(3.0-2.0*f));
    //return mix(rand(i), rand(i+1.0), smoothstep(0., 1., f));
}
float noise(in vec2 st){
    vec2 f = fract(st);
    vec2 i = floor(st);

    float a = rand(i);
    float b = rand(i + vec2(1.,0.));
    float c = rand(i + vec2(0.,1.));
    float d = rand(i + vec2(1.,1.));

    vec2 u = f*f*(3.0-2.0*f);

    return mix(a,b,u.x)+
        		(c-a)*u.y*(1.0-u.x) +
        		(d-b)*u.x*u.y;
}

mat2 rotate(in float r){
    float c=cos(r), s=sin(r);
    return mat2(c, -s, s, c);
}
float usin(in float x){
    return 0.5+0.5*sin(x);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
	uv = (fragCoord*2.0 - iResolution.xy)/min(iResolution.x, iResolution.y);

    float t = 0.2*iTime;
    for(int i=0;i<5;i++){
        float a = atan(uv.x, uv.y);
        float b = floor(5.0+4.*fract(iTime*0.25));
	    a *= b / (2.0*PI);
        a = abs(fract(a*0.5-b*0.5)*2.0-1.0);
        a *= (2.0*PI)/b;
        uv = length(uv)*vec2(sin(a+t*0.7), cos(a+t*0.8));
        uv -= vec2(0.2+1.8*usin(iTime*0.3), 0.);
        uv = fract(uv)*2.0-1.0;
    }

	float v = noise(uv);

    vec3 col1 = mix(RED, YELLOW, usin(iTime*0.4));
    vec3 col2 = mix(BLACK, HANADA, usin(iTime*0.9));
    vec3 col = mix(col1, col2, v);

    col *= clamp(length(uv), 0., 1.);

	uv = (fragCoord*2.0 - iResolution.xy)/min(iResolution.x, iResolution.y);
    col *= exp(-0.8*length(uv));
    col += 1.1*usin(iTime*0.4)*exp(-1.2*length(uv));

    fragColor = vec4(col,1.0);
}

//Fuzzies Attack by eiffie - some furry creatures running from the northern lights
float time;//made some changes cause it seemed long-winded
float noyz(vec3 co){return sin(co.x+1.3*sin(co.y+2.4*sin(co.z)))*0.5;} 
float Limb2(vec3 p, vec3 p0, vec3 p2, vec3 rt, float d, float r){ 
 vec3 p1=(p2-p0)*0.5;//a simple joint solver, corrected the math after iq's version 
 p1+=p0+normalize(cross(p1,rt))*sqrt(d*d-dot(p1,p1)); 
 vec3 v=p1-p0;v*=clamp(dot(p-p0,v)/dot(v,v),0.0,1.0); 
 vec3 v2=p1-p2;v2*=clamp(dot(p-p2,v2)/dot(v2,v2),0.0,1.0); 
 return min(distance(p-p0,v),distance(p-p2,v2))-r; 
}
float Segment(vec3 p, vec3 p0, vec3 p1, float r){//connect 2 points 
 vec3 v=p1-p0;v*=clamp(dot(p-p0,v)/dot(v,v),0.0,1.0);return distance(p-p0,v)-r; 
} 
const vec3 p1=vec3(0.0,-0.15,0.0),p2=vec3(0.0,-0.5,0.05); 
