  }
  t*=1.0/6.0*(noyz(rd*2.0+vec3(0.0,-time,iTime))+noyz(rd*7.3+vec3(0.0,-time*0.3,0.0))); 
  O=vec4(mix(vec3(0.3,0.0,0.3+rd.y*0.5),vec3(0.3,0.9,0.4),(t*t+a)*0.5)*pow(dot(rd,vec3(0.3,0.1,1.0)),3.0),1.0);
