const float epsilon = 1e-4;

struct Hit {
    bool miss;
    vec3 position;
    float dist;
    vec3 normal;
    vec3 color;
};

struct PointLight {
    vec3 position;
    float intensity;
};

struct Sphere {
    vec3 center;
    float radius;
    vec3 color;
};

struct YPlane {
    float y;
    float x1;
    float x2;
    float z1;
    float z2;
    vec3 color;
};

const Hit miss = Hit(true, vec3(0.), 1000., vec3(0.), vec3(0.));

vec2 rotate2d(vec2 st, float a) {
    float c = cos(a);
    float s = sin(a);
    mat2 t = mat2(c,-s,s,c);
    return t*st;
}

Hit sdf_sphere(vec3 p, Sphere sphere) {
    float d = length(p - sphere.center) - sphere.radius;
    return Hit(false, p, d, normalize(p - sphere.center), sphere.color);
}

bool inBound(vec2 p, float x1, float x2, float y1, float y2) {
    return p.x > x1 && p.x < x2 && p.y > y1 && p.y < y2;
}

bool inStripX(vec2 p, float x1, float x2) {
    return p.x > x1 && p.x < x2;
}

bool inStripZ(vec2 p, float z1, float z2) {
    return p.y > z1 && p.y < z2;
}

Hit sdf_yplane(vec3 p, YPlane plane) {
    float d;
    vec3 hp = p;
    hp.y = plane.y;
    if (inBound(p.xz, plane.x1, plane.x2, plane.z1, plane.z2)) {
        d = abs(p.y - plane.y);
    } else if (inStripX(p.xz, plane.x1, plane.x2)) {
        float l1 = length(p.yz - vec2(plane.y, plane.z1));
        float l2 = length(p.yz - vec2(plane.y, plane.z2));
        d = min(l1, l2);
        hp.z = l1 < l2 ? plane.z1 : plane.z2;
    } else if (inStripZ(p.xz, plane.z1, plane.z2)) {
        float l1 = length(p.xy - vec2(plane.x1, plane.y));
        float l2 = length(p.xy - vec2(plane.x2, plane.y));
        d = min(l1, l2);
        hp.x = l1 < l2 ? plane.x1 : plane.x2;
    } else {
        d = 1000.;
        float l1 = length(p- vec3(plane.x1, plane.y, plane.z1));
        if (l1 < d) {
            hp.x = plane.x1;
            hp.z = plane.z1;
            d = l1;
        }
        float l2 = length(p- vec3(plane.x2, plane.y, plane.z1));
        if (l2 < d) {
            hp.x = plane.x2;
            hp.z = plane.z1;
            d = l2;
        }
        float l3 = length(p- vec3(plane.x1, plane.y, plane.z2));
        if (l3 < d) {
            hp.x = plane.x1;
            hp.z = plane.z2;
            d = l3;
        }
        float l4 = length(p- vec3(plane.x2, plane.y, plane.z2));
        if (l4 < d) {
            hp.x = plane.x2;
            hp.z = plane.z2;
            d = l4;
        }
    }
    return Hit(false, hp, d, vec3(0.,1.,0.), plane.color);
}

Hit sdf_scene(vec3 p) {
    Sphere isphere1 = Sphere(vec3(0.,0.,.0), .4, vec3(1.));
    Sphere isphere2 = Sphere(vec3(.4,-.2,.4), .2, vec3(1.));
    Sphere sphere1 = isphere1;
    Sphere sphere2 = isphere2;
    sphere2.center.xz = rotate2d(isphere2.center.xz, iTime);
    YPlane yplane = YPlane(-.4, -1.2,1.2,-1.2,1.2, vec3(1.));

    Hit result = miss;

    Hit sphere1Hit = sdf_sphere(p, sphere1);
    if (sphere1Hit.dist < result.dist) {
        result = sphere1Hit;
    }
    Hit sphere2Hit = sdf_sphere(p, sphere2);
    if (sphere2Hit.dist < result.dist) {
        result = sphere2Hit;
    }
    Hit yplaneHit = sdf_yplane(p, yplane);
    if (yplaneHit.dist < result.dist) {
        result = yplaneHit;
    }
    return result;
}

Hit ray_march_scene(vec3 ro, vec3 rd) {
    float depth = 0.;
    Hit hitResult = miss;
    for (int i = 0; i < 200; i++) {
        Hit hit = sdf_scene(ro + depth * rd);
        if (hit.dist < epsilon) {
            hitResult = hit;
            return hitResult;
        }
        depth += hit.dist;
        if (depth >= 100.) {
            return miss;
        }
    }
    return miss;
}

const float k = 8.;

float softshadow(vec3 ro ,vec3 rd) {
    float res = 1.0;
    float t = 0.1;
    for (int i = 0;i < 200;i++) {
        float h = sdf_scene(ro + t * rd).dist;
        if (h < epsilon) {
            return 0.0;
        }
        res = min(res, k*((h)/t));
        t += h;
        if (t >= 100.) {
            return res;
        }
    }
    return res;
}

vec3 random3d(vec3 p){
    p = vec3(
        dot(p,vec3(127.1,311.7, 456.1)),
        dot(p,vec3(269.5,183.3, 823.2)),
        dot(p,vec3(329.5,83.3, 513.2))
    );
    return -1.0 + 2.0*fract(sin(p)*43758.5453123);
}

float ssao(Hit p) {
    p.position += 2. * epsilon * p.normal;
    float result = 0.;
    for (int i = -1; i <= 0; i++) {
        for (int j = -1; j <= 0; j++) {
            for (int k = -1; k <= 0; k++) {
                vec3 disp = p.position + vec3(float(i), float(j), float(k));
                vec3 rv = random3d(disp);
                rv = normalize(rv);
                disp = p.position + .1*rv;
                Hit h = sdf_scene(disp);
                if (h.dist < 0.0125) {
                    result += 0.02;
                }
            }
        }
    }
    return result;
}

const PointLight light = PointLight(vec3(5.), .8);
const vec3 ambient_c = vec3(.5);

vec3 calcColor(Hit h, PointLight light, vec3 camera) {
    vec3 light_pos = normalize(light.position - h.position);
    vec3 camera_pos = normalize(camera - h.position);

    vec3 ambient = ambient_c * h.color;

    float diff = clamp(dot(light_pos, h.normal), 0.,1.);
    vec3 diffuse = diff * h.color;

    vec3 refl = reflect(-light_pos, h.normal);
    float spec = pow(max(dot(refl, camera_pos), .0), 64.);
    vec3 specular = spec * h.color;

    vec3 color = ambient + .1*diffuse + specular;
    return color;
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec3 st = vec3(gl_FragCoord.xy / iResolution.xy, 0.);
    st = st * 2.-1.;
    st.z = 0.;
    st.x*=iResolution.x/iResolution.y;
    st.xz = rotate2d(st.xz, iTime*.15);

    vec3 camera = vec3(0,0,-2.);
    camera.xz = rotate2d(camera.xz, iTime*.15);
    vec3 ray = st-camera;
    ray = normalize(ray);

    Hit sceneHit = ray_march_scene(camera, ray);
    vec3 light_pos = normalize(light.position - sceneHit.position);
    vec3 hitColor = calcColor(sceneHit, light, camera);
    // remember when ray tracing again from hit point, the start point needs to be biased
    if (!sceneHit.miss) {
        vec3 reflLight = reflect(ray, sceneHit.normal);
        Hit reflectHit = ray_march_scene(sceneHit.position + 2.*epsilon * sceneHit.normal, reflLight);
        vec3 reflColor = calcColor(reflectHit, light, camera);
        hitColor += .1 * reflColor;

        float shadow = softshadow(sceneHit.position - 2.*epsilon*sceneHit.normal, light_pos);
        shadow = shadow - 1.;
        shadow = shadow / 8.;
        hitColor += shadow;

        float ao = ssao(sceneHit);
        hitColor -= ao;
        fragColor = vec4(hitColor,1.);
    } else {
        fragColor = vec4(vec3(.4),1.);
    }
}

#define MAX_STEPS (128)
#define MAX_DIST (100.0)
#define SURFACE_DIST (0.001)

int mat=0;

vec2 rotate(vec2 p, float a)
{
   float s=sin(a);
   float c=cos(a);
   mat2 r=mat2(c, -s, s, c);
   return p*r;
}

float sphereDist(vec3 p, vec3 origin, float radius)
{
   return length(p-origin)-radius;
}

float planeDist(vec3 p, float position)
{
   return p.y-position;
}

float sceneDistance(vec3 p)
{
   float s1=sphereDist(p, vec3(0.0, 0.5, 0.0), 0.2);
   if(s1<SURFACE_DIST)
   {
      mat=1;
   }
   float p1=planeDist(p, 0.0);
   if(p1<SURFACE_DIST)
   {
      mat=2;
   }
   p.xz=rotate(p.xz, iTime);
   float s2=sphereDist(p, vec3(1.0, 0.5, 0.0), 0.2);
   if(s2<SURFACE_DIST)
   {
      mat=3;
   }
   return min(p1, min(s1, s2));
}

vec3 normalAt(vec3 p)
{
   float dist=sceneDistance(p);
   vec2 epsilon=vec2(0.01,0.0);
   return normalize(vec3(dist-sceneDistance(p-epsilon.xyy), 
                         dist-sceneDistance(p-epsilon.yxy),
                         dist-sceneDistance(p-epsilon.yyx)));
}

float lightAt(vec3 p)
{
   vec3 lightPosition=vec3(0.0,2.0,0.0);
   vec3 n=normalAt(p);
   vec3 lightDirection=normalize(lightPosition-p);

   float diffuse=clamp(dot(n, lightDirection), 0.0, 1.0);
   return diffuse;
}

vec3 pixelColor(vec2 uv)
{
   vec3 direction=normalize(vec3(uv,1.0));
   vec3 color=vec3(0.0,0.0,0.0);
   float distance=0.0;
   vec3 camera=vec3(0.0,0.5,-0.5);
   float sceneDist=0.0;
   vec3 p;
   int reflectCount=0;

   for(int i=0;i<MAX_STEPS;i++)
   {
      p=camera+distance*direction;
      sceneDist=sceneDistance(p);
      distance+=sceneDist;
      if(distance>=MAX_DIST)
      {
         break;
      }
      if(sceneDist<=SURFACE_DIST)
      {
         if(reflectCount<=1)
         {
            color=vec3(lightAt(p));         
            vec3 n=normalAt(p);
            distance=0.0;
            direction=reflect(direction,n);
            camera=p+direction/2.0;
            reflectCount++;
         }
         else
         {
            break;
         }
      }
   }

   if(sceneDist<=SURFACE_DIST)
   {
      color=vec3(lightAt(p));
   }

   if(mat==1)
   {
      color*=vec3(1.0,0.0,0.0);
   }
   else if(mat==2)
   {
      color*=vec3(0.0,1.0,0.0);
   }
   else if(mat==3)
   {
      color*=vec3(0.0,0.0,1.0);
   }

   return color;
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord-.5*iResolution.xy)/iResolution.x;
    fragColor = vec4(pixelColor(uv),1.0);
}

#define M_PI 3.1415926535897932384626433832795

float ci(vec2 _st, float _radius, float _start, float _end, float _div)
{
    vec2 dist = _st-vec2(0.5);
    float lAngle = _start + atan(dist.y, dist.x);
    float lDivision = M_PI / _div;
    float lMod = mod(lAngle, lDivision);
    float lCuts = (_div > -1.0) ? ( smoothstep(lDivision, lDivision - 0.02, lMod) * 
                          smoothstep(_end - 0.02, _end, lMod)) : 1.0;

	return 1.-smoothstep(- 0.001 + _radius-(_radius*0.01),
                         _radius+(_radius*0.01),
                         dot(dist,dist)*4.0)*lCuts; //smoothstep(0.48, 0.5, lMod)
}

float mandala(vec2 uv, float time)
{
    return ( ci(uv, 0.85 + sin(iTime * 2.0) * 0.05, -1.42 - time, 0.6, 3.0f) - 
            ci(uv, 0.65 + sin(iTime * 2.0) * 0.05, -1.42 - time, 0.6, 3.0f)) +

        ( ci(uv, 0.7 + 0.05 + cos(time * 2.0) * 0.05, 0.2 - time, 0.7, 3.0f) - 
         ci(uv, 0.55 + 0.05 + cos(time * 2.0) * 0.05, 0.2 - time, 0.7, 3.0f)) +
        ( ci(uv, 0.5, 0.0 + time, 0.3, 3.0f) - 
         ci(uv, 0.3, 0.0 + time, 0.3, 3.0f)) -
        ( ci(uv, 0.46, 0.0 + time, 0.3, 3.0f) - 
         ci(uv, 0.34, 0.0 + time, 0.3, 3.0f)) +

        ( ci(uv, 0.44, 0.0 - time * 0.5, 0.05, 26.0f) - 
         ci(uv, 0.36, 0.0 - time * 0.5, 0.05, 26.0f)) +

        ( ci(uv, 0.25, 0.0 - time * 2.5, 0.5, 3.0f) - 
         ci(uv, 0.15, 0.0 - time * 2.5, 0.5, 3.0f)) +
        ( ci(uv, 0.12, 0.3 + time * 1.5, 0.2, 8.0f) - 
         ci(uv, 0.07, 0.3 + time * 1.5, 0.2, 8.0f)) +
        ( ci(uv, 0.05, 0.3 - time * 1.1, 0.4, 3.0f) - 
         ci(uv, 0.025, 0.3 - time * 1.1, 0.4, 3.0f)) +
        ( ci(uv, 0.015, 0.5 + time * 3.1, 1.8, 1.0f) - 
         ci(uv, 0.0075, 0.5 + time * 3.1, 1.8, 1.0f));
}

