// ray marching
const int max_iterations = 128;
const float grad_step = 0.0001;
const float min_dist = 0.002;
const float clip_far = 1000.0;

// math
const float PI = 3.14159265359;
const float DEG_TO_RAD = PI / 180.0;

vec3 diffuse_colour = vec3(0.0);
vec3 final_diff_colour = vec3(0.0);

const vec3 sky_colour = vec3(0.2, 0.1, 0.4);
const vec3 clouds_colour = vec3(1.0);
const vec3 sun_pos = normalize(vec3( 20.0, 30.0, 30.0 ));


vec4 dist_sin(vec3 p, vec2 k, float a, float t) {
    float kx = dot(k, p.xz) + t;
    float d = a * sin(kx);
    vec2 g = k * (a * cos(kx));
    return vec4(g.x, 0.0, g.y, d);
}

vec4 sea(vec3 p) {

    diffuse_colour = vec3(1.0, 0.7, 0.2);
    vec4 h = vec4(0.0);
    h += dist_sin(p, vec2(0.112, 0.215), 2.0, iTime * 0.23);
    h += dist_sin(p, vec2(-0.163, 0.243), 1.7, iTime * 1.8);
    h += dist_sin(p, vec2(-0.378, -0.317), 1.0, iTime * 2.1);
    h += dist_sin(p, vec2(0.433, -0.451), 1.8, iTime * 3.6);

    h.w += p.y;
    h.y = 1.0;

    // technique from
    // http://www.iquilezles.org/www/articles/distance/distance.htm
    h.w /= length(h.xyz);

    return h;
}

float dist_field(vec3 p) {
    return sea(p).w;
}

vec3 skybox(vec3 dir) {
    float horizon = smoothstep(0.0, 0.25, dir.y);
    float cloud = mix(0.8, 0.0, horizon);
    return mix(sky_colour, clouds_colour, cloud);
}

vec3 gradient(vec3 p) {
    return sea(p).xyz;
}

// parallel light source
vec3 shading( vec3 v, vec3 n, vec3 dir, vec3 eye ) {
	float diffuse  = max( 0.0, dot( sun_pos, n ) );
	float shininess = 80.0;

	vec3 ref = reflect( dir, n );
	float specular = max( 0.0, dot( sun_pos, ref ) );

	specular = pow( specular, shininess );

	return final_diff_colour * diffuse + vec3(specular);
}

// ray marching
vec3 ray_marching(vec3 o, vec3 dir) {
    vec3 colour = vec3(0.0);
    vec3 acc_refl = vec3(1.0);

    // give the ray a bit of a head start by jumping
    // straight to the upper bounding plane!
    float t = 0.0;

    vec3 v;
    for (int i = 0; i < max_iterations; i++) {
       	float d = dist_field(v = o + dir * t);

        if (abs(d) <= min_dist) {
            break;
        }
        // sometimes it seems to overestimate the distance,
        // so i put this fudge factor in, rofl
		if ((t+=d*0.8) >= clip_far) {
            return skybox(dir);
	    }
    }
	final_diff_colour = diffuse_colour;

    vec3 n = normalize( gradient(v) );

    if (dot(n, sun_pos) < 0.0) {
        return colour;
    }

    // shadows
    t = 0.1;
    for (int i = 0; i < max_iterations; i++) {
        vec3 r = v + sun_pos * t;
		if (r.y > 6.5) {
		    colour += shading( v, n, dir, o );
            break;
	    }

        float d = dist_field(r);
        if (d <= min_dist) {
            break;
        }
        t+=d;
    }
    return colour;
}

// get ray direction
vec3 ray_dir( float fov, vec2 size, vec2 pos ) {
	vec2 xy = pos - size * 0.5;

	float cot_half_fov = tan( ( 90.0 - fov * 0.5 ) * DEG_TO_RAD );
	float z = size.y * 0.5 * cot_half_fov;

	return normalize( vec3( xy, -z ) );
}

// camera rotation : pitch, yaw
mat3 rotationXY( vec2 angle ) {
	vec2 c = cos( angle );
	vec2 s = sin( angle );

	return mat3(
		c.y      ,  0.0, -s.y,
		s.y * s.x,  c.x,  c.y * s.x,
		s.y * c.x, -s.x,  c.y * c.x
	);
}

const vec2 spin = vec2(0.005, 0.0041);
const float radius = 120.0;
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	// rotate camera
#ifdef MOUSE_CONTROL
	mat3 rot = rotationXY( vec2(PI, -PI) * ((iMouse.xy - iResolution.xy)/iResolution.xy).yx );
#else
	mat3 rot = rotationXY( vec2( -0.5, iTime * 0.1 ) );
#endif

	// default ray origin
	vec3 eye = vec3( 0.0, 0.0, 0.0 );
	eye = rot * eye;
    eye.y += 20.0;

    // default ray dir
	vec3 dir = rot * ray_dir( 60.0, iResolution.xy, fragCoord.xy);
	vec3 color = ray_marching(eye, dir);

    fragColor = vec4(color, 1.0);
    return;
}

/**
 * Ray marching parameters
 */
#define MAX_STEPS 100
#define MAX_DIST 100.0
#define EPSILON 0.01
#define STEP_RATIO 0.5

#define AA_STEP 1
#define AA_DIV  1.0

/**
 * For x, return something random-looking.
 */
float pseudorandom(float x) {
    return fract(10000.0 * sin(x * 12345.0));
}

/**
 * Where's the center of this cell?
 */
vec2 cellpoint(ivec2 pos) {
    float id = float(pos.y*47 + pos.x);
    float ang = iTime* (0.1 + pseudorandom(id));
    return vec2(
        0.5 + 0.35 * cos(ang),
        0.5 + 0.35 * sin(ang)
    );
}

/**
 * Give each cell in the grid its own height.
 */
float cellheight(ivec2 pos) {
    float id = float(pos.y*47 + pos.x);
    return 0.5*sin(iTime+6.2*pseudorandom(id));
}

/**
 * Calculate distance to voronai cell border for a plane position.
 * Also return the cell height. Each one has a different height.
 */
vec2 voronai(vec2 pos) {
    ivec2 address = ivec2(floor( pos ));
    vec2  f = fract( pos );
    float best_dist = 10.0;
    ivec2 closest_cell;
    vec2 closest_center;
    for (int i = -2; i < 2; i++) {
	    for (int j = -2; j < 2; j++) {
            ivec2 delta = ivec2(i, j);
            ivec2 cell = address + delta;
            vec2 cp = cellpoint(cell) + vec2(delta) -f;
        	float d = dot(cp, cp);
            if (d < best_dist) {
            	best_dist = d;
                closest_cell = delta;
                closest_center = cp;
            }
        }
    }
    float res = 10.0;
    for (int i = -2; i < 2; i++) {
	    for (int j = -2; j < 2; j++) {

            ivec2 b = closest_cell + ivec2( i, j );
            vec2  other_center = vec2( b ) + cellpoint( address + b ) - f;
            float d = dot( 
                0.5*(closest_center+other_center), 
                normalize(other_center-closest_center)
            );
            if (d > 0.0) {
	            res = min(res, d);
            }
        }
    }
    return vec2(res, cellheight(closest_cell + address));
}

/**
 * Blended union
 */
float blend( float a, float b, float k )
{
    float h = max( k-abs(a-b), 0.0 );
    return min( a, b ) - h*h*0.25/k;
}

/**
 * Blended intersection
 */
float blend_int( float a, float b, float k )
{
    float h = max( k-abs(a-b), 0.0 );
    return max( a, b ) + h*h*0.25/k;
}

#define OBJECT_ID_FLOOR 1

/**
 * Measure the distance to objects in the scene
 * return the distance to the nearest object and its object id
 */
vec2 measure(vec3 where) {
    vec2 zoomed = where.xz;
    vec2 vores = voronai(zoomed);
    float vd = -(vores.x - 0.00);
    float d= blend_int(vd, where.y + vores.y*0.3, 0.2);
    return vec2(d, OBJECT_ID_FLOOR);
}

#define NORMAL_DELTA 0.001

vec3 calc_surface_normal(vec3 hit) {
	return normalize(vec3(
            measure(hit+vec3(NORMAL_DELTA, 0.0, 0.0)).x - measure(hit-vec3(NORMAL_DELTA, 0.0, 0.0)).x,
            measure(hit+vec3(0.0, NORMAL_DELTA, 0.0)).x - measure(hit-vec3(0.0, NORMAL_DELTA, 0.0)).x,
            measure(hit+vec3(0.0, 0.0, NORMAL_DELTA)).x - measure(hit-vec3(0.0, 0.0, NORMAL_DELTA)).x
    ));
}

vec3 trace(vec2 pixel) {
    vec3 eye = vec3(0,10,-10);
    vec3 look = vec3(0,-1,1);
    vec3 up = vec3(0,1,0);
    vec3 right = vec3(1,0,0);

    vec3 actual_up = cross(look, right);
    vec3 ray = normalize(look + pixel.x * right + pixel.y * actual_up);

    vec2 current;
    float total_dist = 0.0;
    int who;
    vec3 where = eye;
    for(int steps = 0; steps < MAX_STEPS; steps++) {

        current = measure(where);
        float current_dist = current.x;
        if (current_dist < EPSILON) {
            who = int(current.y);
            break;
        }
        total_dist += current_dist * STEP_RATIO;
        if (total_dist > MAX_DIST) {
            break;
        }
        where = eye + total_dist * ray;
    }
    vec3 color = vec3(0,0,0);
    if (who == OBJECT_ID_FLOOR) {
		color = vec3(0.8, 0.7, 0.6) * (1.0+vec3(where.y));
        vec3 to_light = normalize(vec3(-5,15,-10));
    	//float shadow = calcSoftshadow(hit, to_light, 0.0, total_dist);
    	vec3 surface_normal = calc_surface_normal(where);
    	float dotty = dot(to_light, surface_normal);
		color *= dotty;
        vec3 reflected = surface_normal * 2.0 * dotty - to_light;
    	vec3 toEye = normalize(-ray);
		float specular = pow(max(0.0, dot(toEye, reflected)), 32.0);
        color += vec3(specular);

    }
    return clamp(color, 0.0, 1.0);
}
vec2 pixelToLocal(vec2 fragCoord) {
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy - 0.5;
    uv.y = uv.y * iResolution.y / iResolution.x;
    return uv;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec3 col = vec3(0,0,0);
    for (int dy = 0; dy < AA_STEP; dy++) {

	    for (int dx = 0; dx < AA_STEP; dx++) {
        	vec2 delta = vec2(float(dx)/AA_DIV, float(dy)/AA_DIV);
		    vec2 uv = pixelToLocal(fragCoord+delta);

			col += trace(uv);
        }
    }
    fragColor = vec4(col / (AA_DIV*AA_DIV), 1.0);
}

#define PI  3.141592
#define TAU 6.283184


vec2 cs(float a){
    return vec2(cos(a),sin(a));
}

vec2 rot(vec2 v, float a) {
    vec2 c = cs(a);
    return v * mat2x2(c.x, -c.y, c.y, c.x);
}

vec2 sp(float t){
    return vec2(floor(t), fract(t));
}

float rand(vec2 co){
  return fract(sin(dot(co.xy ,vec2(12.9898,78.233))) * 43758.5453);
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord*2.-iResolution.xy)/iResolution.y;
	fragColor.b=0.;
    for(float i = 0. ; i < 6. ; i++) {
	    vec2 t = sp(iTime*.15 - i*.1);
	    vec4 c = vec4(
	        rand(vec2(t.x, i+0.))*2.0-1.0,
	        rand(vec2(t.x, i+1.))*2.0-1.0,
	        rand(vec2(t.x, i+2.))*0.5+0.5,
            rand(vec2(t.x, i+3.))*2.0-1.0
	    );

        c.w = mod(t.x, 2.)*2.-1.;

	    uv -= c.xy;
        if(length(uv)<c.z) {
	        uv = rot(uv, smoothstep(0.,1.,t.y)*TAU*sign(c.w));
        } else if(length(uv)<c.z+.01) {
            fragColor.b+=sin(t.y*PI);
        }
        uv += c.xy;
    }


    fragColor.g =1.0-20.*rand(floor(uv*150.0));
    fragColor = max(fragColor, vec4(fragColor.g));
    return;
}

mat2 rot(float a)
{
  float s = sin(a);
  float c = cos(a);
  return mat2(c, s, -s, c);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 pos = (fragCoord.xy - 0.5 * iResolution.xy) / iResolution.y;
    float d = 3.0-length(3.0*pos*pos)*4.0;
    pos*=rot(pos.x*5.0+iTime*0.6+log(d));
    //pos*=0.5;
    pos+=vec2(0.5);
	float vv = (pos.y*pos.y+pos.x*d*d*1.61);
	vv+=sin(pos.x*12.14);
	float v = sin(sin(pos.x*2.6)*1.0+(vv) * 0.5 + iTime * 2.0);
	fragColor = vec4( v*1.44, .05+0.3*v, 0.0, 1.0 )*d;

}

