const int MAX_ITER    = 96;
const float STEP_SIZE = .5;
const float EPSILON   = .0001;
const float PI = 3.14159265359;

struct Result {
	float d;
	vec3 col;
	int id;
};

float udRoundBox (vec3 p, vec3 size, float r)
{
    return length (max (abs (p) - (size - r), .0)) - r;
}

float sdSphere (vec3 p, float r)
{
    return length (p) - r;
}

// PBR toolbox
float DistributionGGX (vec3 N, vec3 H, float roughness)
{
    float a2     = roughness * roughness;
    float NdotH  = max (dot (N, H), .0);
    float NdotH2 = NdotH * NdotH;

    float nom    = a2;
    float denom  = (NdotH2 * (a2 - 1.) + 1.);
    denom        = PI * denom * denom;

    return nom / denom;
}

float GeometrySchlickGGX (float NdotV, float roughness)
{
    float nom   = NdotV;
    float denom = NdotV * (1. - roughness) + roughness;

    return nom / denom;
}

float GeometrySmith (in vec3 N, vec3 V, vec3 L, float roughness)
{
    float NdotV = max (dot (N, V), .0);
    float NdotL = max (dot (N, L), .0);
    float ggx1 = GeometrySchlickGGX (NdotV, roughness);
    float ggx2 = GeometrySchlickGGX (NdotL, roughness);

    return ggx1 * ggx2;
}

vec3 fresnelSchlick (float cosTheta, vec3 F0, float roughness)
{
	return F0 + (max (F0, vec3(1. - roughness)) - F0) * pow (1. - cosTheta, 5.);
}

vec4 smin (vec4 d1, vec4 d2, float k)
{
	float h = clamp (.5 + .5*(d2.a - d1.a)/k, .0, 1.);
	return mix (d2, d1, h) - h*k*(1. - h);
}

float vary (float value, float speed)
{
	return value*(.5 + .5*cos(speed*iTime));
}

// ray-marching stuff
Result scene (in vec3 p)
{
    float floor = p.y + .35;

	vec3 sphereCenter = p;
	vec3 boxCenter = p;
	float offsetX = 1. - vary (1., 1.);
	float offsetZ = 1. - vary (1., 1.);
	sphereCenter -= vec3 (offsetX, .25, offsetZ);
	boxCenter -= vec3 (.0, .0, .5);

	float sphere = sdSphere (sphereCenter, .6);
	float box = udRoundBox (boxCenter, vec3 (.35, .35, .7), .05);
	float r = .1 + .4*(.5 + .5*cos(5.*boxCenter.x*5.*boxCenter.y*5.*boxCenter.z));
	vec3 col1 = vec3 (vary (.5, 2.), vary (1., 1.), vary (.25, 2.));
	vec3 col2 = vec3 (1. - vary (.5, 2.), 1. - vary (1., 1.), vary (1., 1.));
    vec4 colorAndDistance = smin (vec4 (col1, box), vec4 (col2, sphere), r);
    float d = colorAndDistance.a;
	vec3 col = colorAndDistance.rgb;

    Result res = Result (.0, vec3 (.0), 0);
	res.d = min (d, floor);
    res.id = (res.d == floor) ? 1 : 2;
    res.col = (res.d == floor) ? vec3 (.2) : col;
    return res;
}

Result raymarch (in vec3 ro, in vec3 rd)
{
    Result res = Result (.0, vec3 (.0), 0);

    for (int i = 0; i < MAX_ITER; i++)
    {
        vec3 p = ro + res.d * rd;
        Result tmp = scene (p);
        if (abs (tmp.d) < EPSILON*(1. + .125*tmp.d)) return res;
        res.d += tmp.d * STEP_SIZE;
        res.id = tmp.id;
		res.col = tmp.col;
    }

    return res;
}

vec3 normal (in vec3 p)
{
    vec2 e = vec2(.0001, .0);
    float d = scene (p).d;
    vec3 n = vec3 (scene (p + e.xyy).d - d,
                   scene (p + e.yxy).d - d,
                   scene (p + e.yyx).d - d);
    return normalize(n);
}

float shadow (in vec3 ro, in vec3 rd)
{
    float result = 1.;
    float t = .1;
    for (int i = 0; i < MAX_ITER; i++) {
        float h = scene (ro + t * rd).d;
        if (h < 0.00001) return .0;
        result = min (result, 8. * h/t);
        t += h;
    }

    return result;
}

float ao (vec3 p, vec3 n, float d) {
    return clamp (scene (p + d*n).d/d, .0, 1.);
}

vec3 shadePBR (vec3 ro, vec3 rd, float d, int id, vec3 col)
{
    vec3 p = ro + d*rd;
    vec3 nor = normal (p);

    // "material" hard-coded for the moment
    vec3 albedo1 = vec3 (1.);
    vec3 albedo2 = col;
    vec3 albedo = (id == 1) ? albedo1 : albedo2;
    float metallic  = (id == 1) ? .1 : .9;
    float roughness = (id == 1) ? .9 : .1;
    float ao = ao (p, nor, .25);

    // lights hard-coded as well atm
    vec3 lightColors[3];
    lightColors[0] = vec3 (.7, .8, .9)*20.;
    lightColors[1] = vec3 (.9, .8, .7)*20.;
    lightColors[2] = vec3 (.9, .3, .2)*20.;

    vec3 lightPositions[3];
    lightPositions[0] = p + vec3 (.5, .75, -1.5);
    lightPositions[1] = p + vec3 (-.5, .25, 1.);
    lightPositions[2] = p + vec3 (-.1, 1., .1);

	vec3 N = normalize (nor);
    vec3 V = normalize (ro - p);

    vec3 F0 = vec3 (0.04); 
    F0 = mix (F0, albedo, metallic);
    vec3 kD = vec3(.0);

    // reflectance equation
    vec3 Lo = vec3 (.0);
    for(int i = 0; i < 3; ++i) 
    {
        // calculate per-light radiance
        vec3 L = normalize(lightPositions[i] - p);
        vec3 H = normalize(V + L);
        float distance    = length(lightPositions[i] - p);
        float attenuation = 1. / (distance * distance);
        vec3 radiance     = lightColors[i] * attenuation;

        // cook-torrance brdf
        float aDirect = .125 * pow (roughness + 1., 2.);
        float aIBL = .5 * roughness * roughness;
        float NDF = DistributionGGX(N, H, roughness);        
        float G   = GeometrySmith(N, V, L, roughness);      
        vec3 F    = fresnelSchlick(max(dot(H, V), 0.0), F0, roughness);

        vec3 kS = F;
        kD = vec3(1.) - kS;
        kD *= 1. - metallic;	  

        vec3 nominator    = NDF * G * F;
        float denominator = 4. * max(dot(N, V), 0.0) * max(dot(N, L), 0.0);
        vec3 specular     = nominator / max(denominator, .001);  

        // add to outgoing radiance Lo
        float NdotL = max(dot(N, L), 0.0);                
        Lo += (kD * albedo / PI + specular) * radiance * NdotL; 
	    Lo *= shadow (p, L);
    }

    vec3 ambient = (kD * albedo) * ao;

    return ambient + Lo;
}

vec3 camera (vec2 uv, vec3 ro, vec3 aim, float zoom)
{
    vec3 f = normalize (vec3 (aim - ro));
    vec3 wu = vec3 (.0, 1., .0);
    vec3 r = normalize (cross (wu, f));
    vec3 u = normalize (cross (f, r));
    vec3 c = ro + f*zoom;

    return normalize (c + uv.x*r + uv.y*u - ro);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uvRaw = fragCoord.xy/iResolution.xy;
	vec2 uv = uvRaw;
    uv = uv*2. - 1.;
    uv.x *= iResolution.x/iResolution.y;

    vec3 ro = vec3 (-2.*cos(iTime), 1.5, -2.*sin(iTime));
    vec3 aim = vec3 (.0);
    float zoom = 2.;
    vec3 rd = camera (uv, ro, aim, zoom);

    Result res = raymarch (ro, rd);
    float fog = 1. / (1. + res.d * res.d * .1);
    vec3 c = shadePBR (ro, rd, res.d, res.id, res.col);

	c *= fog;
	c = c / (1. + c);
    c = .2 * c + .8 * sqrt (c);
    c *= vec3 (.9, .8, .7);
    c *= .2 + .8*pow(16.*uvRaw.x*uvRaw.y*(1. - uvRaw.x)*(1. - uvRaw.y), .3);

	fragColor = vec4(c, 1.);
}

// Metaball variant of "Perlinfall" https://www.shadertoy.com/view/ttfGzj


void mainImage( out vec4 O, vec2 u )
{
    vec2 R = iResolution.xy, P,D,
         U = u / R.y, V = 15.*U; V.y += iTime;
    float p = 0.;

    for (int k=0; k<9; k++)                            // neigborhood
        P = vec2(k%3-1,k/3-1),                         // cur. cell 
        D = fract(1e4*sin(ceil(V-P)*mat2(R.xyyx)))-.5, // node = random offset in cell
        P = fract(V) -.5 + P+ D,                       // node rel. coords
        p += smoothstep( 1.3*U.y,0.,length(P) );       // its potential

  //p = sqrt(p);
  //p = sin(10.*p); 
    O = vec4( (p -.5) / fwidth(p) ); // * vec4(.5,.7,1.2,1);
}

// Sphere Swirls
// by Hazel Quantock 2019
// This work is licensed under a Creative Commons Attribution-NonCommercial-ShareAlike 4.0 International License. http://creativecommons.org/licenses/by-nc-sa/4.0/

// reduce this to improve frame rate in windowed mode (0 disables anti-aliasing)
#define AA_QUALITY 2

// allow a little bleed between pixels - I think this looks more photographic, but blurrier
#define AA_ROUND false
#define AA_ROUND_RADIUS 0.7071


float Torus( vec3 position, float majorRadius, float minorRadius )
{
    float majorCircle = length(position.xy) - majorRadius;
    vec2 crossSection = vec2( position.z, majorCircle );
    return length( crossSection ) - minorRadius;
}


float SDF( vec3 pos )
{
    float thickness = .02;
    const int n = 50;
    float f = 1e20;
    for ( int i=0; i < n; i++ ) // can make this faster by using pos.z as an index, but then need to have bounds on adjacent rings
    {
        float fi = (float(i)+.5)/float(n);
        float a = 6.283*(fi*.5-.25);
        float h = sin(a);
        float r = cos(a);

        float sliceAngle = -iTime*1.618 + (fi-.5)*6.283*(1.8*cos(iTime*.6) + 49.*smoothstep(.5,1.,-cos(iTime*.1)));
        vec2 slice = vec2(cos(sliceAngle),sin(sliceAngle));

        f = min( f,
                max(
                    Torus( pos-vec3(0,0,h), r, thickness),
                    dot(pos.xy,slice) // plane
                )
            );

        // add sphere end caps
        vec3 end = vec3( slice.y*r, -slice.x*r, h );
        f = min( f, length(pos - end) - thickness );
        end.xy = -end.xy;
        f = min( f, length(pos - end) - thickness );
    }

    return f;
}


const float epsilon = .0004;
float Trace( vec3 rayStart, vec3 rayDirection, float near, float far )
{
    float t = near;

    float h = 0.;
    float lasth = 0.;
    float bestt = 0.;
    float bestDist = 1e30;
    float sdf = 0.;
    for ( int i=0; i < 200; i++ )
    {
        lasth = h;
        sdf = SDF( rayDirection*t+rayStart );
        h = sdf + epsilon*.5;
		t += h;
        if ( t > far ) return 1e10;
        if ( sdf < bestDist ) { bestt = t; bestDist = sdf; } // not sure if all these conditionals will compile well, could flip to use step and mix
        if ( h < epsilon ) break;
    }

    if ( sdf == bestDist )
    {
        // improve precision
		float lastt = t-epsilon;
		float lastsdf = SDF( rayDirection*lastt+rayStart );
		float sdf = SDF( rayDirection*t+rayStart );
       	t = mix( lastt, t, (0.-lastsdf)/(sdf-lastsdf) );
    }
    else if ( t < bestt*2. )
    {
        // use the closest sample we had, to avoid sampling back-sides and things
        t = bestt;
		// this can add a fringe, but maybe better than the alternative
    }

    return t;
}


vec3 GetNormal( vec3 p )
{
    vec2 d = vec2(-1,1)*epsilon;
    return normalize(
        	SDF(p+d.xxx)*d.xxx +
        	SDF(p+d.yyx)*d.yyx +
        	SDF(p+d.yxy)*d.yxy +
        	SDF(p+d.xyy)*d.xyy
        );
}


vec3 HDRtoLDR( vec3 col )
{
	const float whiteSoftness = 0.15; // this mostly affects the diffuse light - rewind to recompute it!

    // soft clamp to white (oh this is so good)
    float w2 = whiteSoftness*whiteSoftness;
    col += w2;
    col = (1.-col)*.5;
    col = 1. - (sqrt(col*col+w2) + col);

    col = clamp( col, 0., 1. );

    // linear to sRGB (approx)
    //col = pow( col, vec3(1./2.2) ); // moved to after anti-aliasing

    return col;
}


void mainImage2( out vec4 fragColour, in vec2 fragCoord )
{
    vec3 ray = vec3((fragCoord-iResolution.xy*.5)/iResolution.y,2.3);

    vec3 camPos = vec3(4,0,-3);

    float time2 = iTime*.2;
    vec2 rot = vec2(0);

    // wandering camera
    rot += sin(vec2(1,-.618)*(time2-sin(time2))*.5)*vec2(.5,.2);

//    if ( max(iMouse.x,iMouse.y) > .0 ) // use last mouse position (unless 0,0 => never clicked)
    if ( iMouse.z > .0 ) // snap back when mouse released
    {
        rot = (iMouse.xy/iResolution.xy-.5);
    }
    rot *= vec2(1,.5)*6.283185;
    camPos.xz = camPos.xz*cos(rot.x) + sin(rot.x)*vec2(1,-1)*camPos.zx;
    float l = length(camPos.xz);
    camPos.xz *= cos(rot.y) + sin(rot.y)*camPos.y/l;
    camPos.y = camPos.y*cos(rot.y) - sin(rot.y)*l;

    vec3 camK = normalize(vec3(0)-camPos);
    vec3 camI = normalize(cross(vec3(0,1,0),camK));
    vec3 camJ = cross(camK,camI);

    ray = ray.x*camI + ray.y*camJ + ray.z*camK;
    ray = normalize(ray);

    // jump ahead because we know the object is inside a 1+thickness sphere
    float near = (length(camPos)-1.1);
    float far = (length(camPos)+1.1);

	float t = Trace( camPos, ray, near, far );

    vec3 pos = camPos+t*ray;

    if ( t >= far )
    {
        // background

        // make it consistent with ambient light direction
        fragColour.rgb = (vec3(.5,.6,.8)-.25) * smoothstep(-.3,.3,dot(ray,normalize(vec3(-4,-3.5,3)))) + .25;
    }
    else
    {        
        vec3 normal = GetNormal(pos);

        // ambient light
		// this looks pretty nice, it implies a studio where the camera is on the dark side
        fragColour.rgb = (dot(normal,normalize(vec3(-4,-3.5,3)))*.5+.5)*(vec3(.5,.6,.8)-.25);
        fragColour.rgb += vec3(.25);

        // some rough AO
        float aoDist = .1;
        fragColour.rgb *= smoothstep(-aoDist*2.,.0,SDF(pos+normal*aoDist)-aoDist);

        vec3 lightDir = normalize(vec3(-2,3,-1));
        vec3 lightCol = vec3(1);

        float shadowT = Trace( pos, lightDir, .001, 2. );
        if ( shadowT >= 2. )
        {
            fragColour.rgb += max(0.,dot(normal,lightDir))*lightCol;
        }
    }

    fragColour.rgb = HDRtoLDR(fragColour.rgb);
    fragColour.a = 1.;
}


// Anti-aliasing
void mainImage( out vec4 fragColour, in vec2 fragCoord )
{
#if ( AA_QUALITY == 0 )
    mainImage2( fragColour, fragCoord );
#else
    int numSamples = max( 1, int((1920.*1080.*float(AA_QUALITY)*.15) / (iResolution.x*iResolution.y)) );

    vec4 colSum = vec4(0);
    for ( int i=0; i < numSamples; i++ )
    {
        uvec2 quasi2 = uvec2(0xC13FA9A9u,0x91E10DA5u);
        uint seed = uint(i);
        //seed += uint(iFrame*numSamples); // randomize per frame - causes shimmering
        //seed += uint(fragCoord.x)*quasi2.x+uint(fragCoord.y)*quasi2.y; // randomize per pixel - this looks bad at low sample counts (and at high counts it's less important)
        vec2 jitter = vec2( quasi2 * seed ) / exp2(32.);

        if ( AA_ROUND )
        {
            // circle of confusion slightly bigger than a pixel - should look more photographic
            jitter.x *= 6.283185;
            jitter = AA_ROUND_RADIUS*(1.-jitter.y*jitter.y)*vec2(cos(jitter.x),sin(jitter.x));
        }
        else
        {
            jitter -= .5;
        }

        vec4 col;
        mainImage2( col, fragCoord + jitter );
        colSum += col;
    }

    fragColour = colSum/float(numSamples);
#endif
    fragColour.rgb = pow( fragColour.rgb, vec3(1./2.2) );
}

/**
 * Basic raytracing algorithm 
 */

#define NEAR 0.001
#define FAR 40.0
#define FOG 0.00005
#define FOV 0.5
#define RT_ITERATIONS 128
#define TARGET vec3(0.0)
#define PI 3.14159265359



float glow = 0.0, ttpi;

vec3 cw, cu, cv, rd;
vec4 np;

vec2 e = vec2(0.00035, -0.00035);

// useful functions
mat2 r2 (float r) { return mat2(cos(r), sin(r), -cos(r), cos(r)); }
float noise (vec3 p) {
	vec3 ip = floor(p),
         s = vec3(7.0, 157.0, 113.0);
    p-= ip;
    vec4 h = vec4(0.0, s.yz, s.y+s.z) + dot(ip, s);
    h = mix(fract(sin(h)*43758.5), fract(sin(h+s.x)*43758.5), p.x);
    h.xy = mix(h.xz, h.yw, p.y);
    return mix(h.x, h.y, p.z);
}

// primitives
float bo (vec3 p, vec3 r) { p = abs(p)-r; return max(max(p.x, p.y), p.z); } // box
float ca (vec3 p, float h, float r) { p.y-= clamp(p.y, 0.0, h); return length(p)-r; } // capsule


/**
 * this function generates a scene, distance based
 */
vec2 fb (in vec3 p) {
    vec2 h, t = vec2(ca(p+vec3(0.0,2.0,0.0), 4.0, 1.0), 5.0);
    t.x = min(bo(abs(p)-vec3(4.8,4.8,4.8), vec3(1.2, 1.2, 1.2)), t.x);

    h = vec2(ca(p, 10.0, 0.2), 3.0);

    t = t.x < h.x ? t : h;

    glow+= 0.1 / (0.1+t.x*t.x*100.0);

    t.x*= 0.1;

    return t;
}


/**
 * MAPPING FUNCTION
 */
vec2 mp (vec3 p) {
    p.z = mod(p.z+iTime*12.0, 20.0)-8.0;


    np = vec4(p, 1.0);

    float s = sin(p.z*0.1+ttpi*2.0-5.0)*0.1;

    for (float i = 0.0; i < 6.0; i++) {
    	np = abs(np) - vec4(4.0, 0.0, max(4.0*s, 2.2), 0.0);
        np.xy*= r2(0.4+s);
        np.zy*= r2(0.1+s*abs(cos(iTime/6.0)));
        np.xz*= r2(0.4*s*cos(iTime/10.0));
    }

    vec2 h, t = fb(np.xyz);
    return t;
}


/**
 * RAY TRACING METHOD 
 */
vec2 tr (in vec3 ro, in vec3 rd) {
    vec2 h, t = vec2(0.1);
    for (int i = 0; i < RT_ITERATIONS; i++) {
        h = mp(ro+rd*t.x);
        if (h.x < NEAR || t.x > FAR) break;
        t.x+= h.x;
        t.y = h.y;
    }
    if (t.x > FAR) t.x = 0.0;
    return t;
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    uv-= 0.5;
    uv/= vec2(iResolution.y/iResolution.x, 1.0);
    uv.x/= 2.6; // looks better if upscaled on x

    // a bit naive but enough
    float bassLevel = (texture(iChannel0, vec2(0.0, 0.0)).r+texture(iChannel0, vec2(0.2, 0.0)).r)/2.0;

    // time 
    float tt = mod(iTime, 100.0),
          bb = 0.5 + clamp(sin(tt), -0.5, 0.5);

    // background
    vec3 col, fo, ld = normalize(vec3(0.1,0.5,-0.5));
    col = fo = vec3(0.4)*cos(iTime/8.0) + vec3(0.1) * (1.0 - length(uv)*1.5 - 0.1);

    // camera 
    ttpi = mod(tt, 2.0*PI);
    ttpi = ttpi > PI ? 2.0*PI-ttpi : ttpi;
    //vec3 ro = vec3(cos(ttpi)*15.0, 0.0, sin(ttpi)*16.0);
    vec3 ro = vec3(0.0,0.0,-10.0);
    cw = normalize(TARGET-ro);
    cu = normalize(cross(cw, vec3(0.0, 1.0, 0.0)));
    cv = normalize(cross(cu, cw));
    rd = mat3(cu, cv, cw) * normalize(vec3(uv, FOV));

    // raytracer algorithm is called
    vec2 sc = tr(ro, rd);

    // distance camera / object
    float d = sc.x;

    // if d > 0, we compute the color of the pixel based on the scene 
    if (d > 0.0) {
   		vec3 po = ro + rd*d,
             no = normalize(e.xyy*mp(po+e.xyy).x + e.yyx*mp(po+e.yyx).x + e.yxy*mp(po+e.yxy).x + e.xxx*mp(po+e.xxx).x),
             al = mix(vec3(1.0, 1.0,1.0), vec3(1.0, 0.0, 0.32),abs(cos(iTime/5.0)));

        if (sc.y < 5.0) {
            al = mix(vec3(1.0, 1.0,1.0), vec3(1.0, 0.0, 0.8),abs(cos(iTime/5.0)));
        }

        // lighting

        float dif = max(0.0, dot(no, ld)),
              aor = d / 50.0,
              fr = pow(1.0+dot(no, rd), 4.0);

        vec3 sss = vec3(1.5) * smoothstep(0.0, 1.0, mp(po+ld*0.4).x/0.4);


        col = mix(al*0.2+al*(dif+sss), fo, min(fr, 0.5));
        col = mix(col, fo, 1.0 - exp(-FOG*d*d*d));
    }

    // we add the glow
    //bassLevel*= bassLevel;
    col+= vec3(0.0,.9,.3)*glow*0.05*bassLevel;

    // Output to screen
    fragColor = vec4(col, 1.0);

}

#define EPS 0.001

float rand(vec2 p) {
	return fract(sin(p.x*12.9898+p.y*78.233)*43758.543);
}

float noise(vec2 p) {
	vec2 i = floor(p);
	vec2 f = fract(p);

	vec2 o = vec2(0.,1.);
	float a = rand(i+o.xx);
	float b = rand(i+o.yx);
	float c = rand(i+o.xy);
	float d = rand(i+o.yy);

	return mix(mix(a,b,f.x), mix(c,d,f.x), f.y);
}

float fractNoise(vec2 p) {
	float r = noise(p);
	for (float i = 1.; i < 7.; i++) {
		r += noise(p*pow(2., i)) / pow(i, 2.5);
	}
	return r;
}

float artifacts(vec3 p) {
	p = vec3(mod(p.x, 4.)-2.,p.y-1.5,mod(p.z, 2.6)-1.3);
	p.y /= 2.;
	p.x /= 2.;
	return length(p) - .5;
}

float terrain(vec3 p) {
	return p.y - fractNoise(p.xz/1.8)*1.2;
}

float map(vec3 p) {
	return min(
		terrain(p),
		artifacts(p));
}

vec3 normal(vec3 p) {
	vec2 e = vec2(EPS, 0.);
	return normalize(vec3(
		map(p+e.xyy)-map(p-e.xyy),
		map(p+e.yxy)-map(p-e.yxy),
		map(p+e.yyx)-map(p-e.yyx)
		));
}

vec3 lightdir = normalize(vec3(.5,-5.,-2.));
vec3 terrainColor(vec3 p, float hit) {
	return mix(
		vec3(.0,.2,.0),
		vec3(.3,.8,.6),
		dot(normal(p), -lightdir) - hit/100.);
}


void mainImage( out vec4 fragColor, in vec2 fragCoord ) {

	vec2 uv = ( 2. * fragCoord.xy - iResolution.xy ) / iResolution.y * 5.;
	uv += (sin(uv.x*10.+iTime/4.)+cos(uv.y*5.+iTime/4.))/50.;

	vec3 eye = vec3(0.,3.,iTime);
	vec3 p = eye;
	vec3 phit = eye;
	vec3 raydir = normalize(vec3(uv.x, uv.y-2.5, 4.));
	float hit = -1.;
	float reflectionHit = -1.;

	for (float i = 0.; i < 100.; i++) {
		float d = (hit > -1.) ? terrain(p) : map(p);
		if (d < EPS) {
			if (hit == -1.) {
				hit = i;
				phit = p;
				vec3 n = normal(p);
				raydir = raydir - 2.*dot(raydir,n)*n;
				if (terrain(p) < EPS) {
					break;
				}
			} else {
				reflectionHit = i;
				break;
			}
		}
		p += d * raydir * .7;
	}


	vec3 color;
	if (hit > 0.) {
		if (artifacts(phit) < EPS) {
			color = mix(
				vec3(.7,.2,.1),
				vec3(.9,.8,.6),
				dot(normal(phit), -lightdir) - hit/100.);
			color += clamp(terrainColor(p, hit)/2., 0.1, .5) * length(terrainColor(p, hit))/3. * 5.;
		} else {
			color = terrainColor(p, hit);
		}
	}
	color -= smoothstep(0.7,1.,p.y)/5.;
	color -= pow(length(p.z-eye.z), 0.2)/10.;
	color /= clamp(length(p.z-eye.z)/1.5, .5, 1.5);
	color = clamp(color, vec3(0.), vec3(1.));
	color += vec3(.02,.02,.15);
	color += vec3(.2,.4,.8)*clamp(uv.y/8.-.5,0.,.3);
	color = vec3(pow(color.x, .9), pow(color.y, .9), pow(color.z, .9));


	fragColor = vec4(color, 1.0 );

}

// Fork of "raymarched 4d grid" by yx
// https://shadertoy.com/view/3lsGR2

int mat;
float time;
vec4 rv;

void shuffle()
{
    rv=fract(1e4*sin(rv)+rv.wxyz);
}

mat2 R(float a)
{
    return mat2(
        cos(a),-sin(a),
        sin(a), cos(a)
    );
}

void rotate(inout vec4 v)
{
    float a = 0.06 * time;
    v.xy*=R(a);
    v.xw*=R(a);
    v.yz*=R(a);
    v.yw*=R(a);
    v.zx*=R(a);
    v.zw*=R(a);
}

void translate(inout vec4 v)
{
    float t = 0.2 * time;
    v[int(t)%4] += 3.*cos(acos(-1.)*fract(t));
}

float box(vec3 p, float r)
{
    p=abs(p);
    return max(
        max(max(p.x,p.y),p.z)-r,
        abs(length(p)-r*1.2)-.02
    );
}

float scene(vec4 p)
{
    p=mod(p,3.)-1.5;

    const float r1 = .1;
    float cxy=length(p.xy)-r1;
    float cxz=length(p.xz)-r1;
    float cxw=length(p.xw)-r1;
    float cyz=length(p.yz)-r1;
    float cyw=length(p.yw)-r1;
    float czw=length(p.zw)-r1;

    const float r2 = .4;
    float sxyz=box(p.xyz,r2);
    float sxyw=box(p.xyw,r2);
    float sxzw=box(p.xzw,r2);
    float syzw=box(p.yzw,r2);

    float d1 = min(min(min(cxy,cxz),min(cxw,cyz)),min(cyw,czw));
    float d2 = min(min(sxyz,sxyw),min(sxzw,syzw));
    float dx = min(d1, d2);

    mat = (dx == d1) ? 0 : 1;

    return dx;
}

vec4 normal(vec4 p)
{
    vec2 o=vec2(.01,0);
    return normalize(scene(p)-vec4(
		scene(p-o.xyyy),scene(p-o.yxyy),
		scene(p-o.yyxy),scene(p-o.yyyx)
	));
}


vec4 noise(vec4 p)
{   
    p = mod(p, 3.) - 1.5;
    p = round(3.*p);

    for(int i=0; i<13; i++)
        p = fract(1e4*sin(p) + p.wxyz);

    return p;
}

