float badao(vec4 p, vec4 n)
{
    const float d = 0.35;
    return pow(max(scene(p+d*n) / d, .0), 1.2);
}

float badsss(vec4 p, vec4 n)
{
    const float d = -0.03;
    return .4+.6*pow(max(scene(p+d*n) / d, .0), .7);
}

vec4 render(vec2 coord)
{
    shuffle();

    vec2 uv = (coord+rv.xy-.5*iResolution.xy) / iResolution.y;
    time = iTime + .5 * rv.z * iTimeDelta;

    float fisheye = .9;
    if (fisheye != 0.)
    	uv = normalize(uv) * tan(asin(length(uv) * fisheye)) / fisheye;

    float a = 0.05 * time;
    vec2 zw = vec2(cos(a),sin(a));

    vec4 cam=vec4(0);
    vec4 dir=normalize(vec4(uv,zw));

    rotate(dir);
    translate(cam);

    float k, t=.2;
    for(int i=0;i<80;++i){
		t+=k=scene(cam+dir*t);
        if(k<.01)break;
    }

    vec4 h=cam+dir*t;
    vec4 n=normal(h);

    cam = h;
    dir = reflect(dir, n);

    float t2 = .0;
    for(int i=0;i<40;++i){
		t2+=k=scene(cam+dir*t);
        if(k<.05)break;
    }

    vec4 col = 4. * pow(.7, t) * badao(h,n) * badsss(h,n) * (noise(h) + pow(.7, t2 + 2.));

    return pow(clamp(col, .0, 1.), vec4(.454545454545454545454545));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    rv = vec4(fragCoord, iFrame, 1);
    for(int i=0;i<8;i++)shuffle();

    vec4 acc = vec4(0);

    const int n = 2;

    for(int i=0; i<n; i++)
        acc += render(fragCoord);

    fragColor = acc / float(n);
}

// domain coloring of the Doyle-McMullen iteration function on the Riemann sphere

// domain coloring code from https://www.shadertoy.com/view/wtf3DN
// sphere rendering forked from https://www.shadertoy.com/view/MssGRl by asalga

// some constants

#define PI 3.14159265359

// range of the display

#define SCALE 8.0

// grid line spacing

#define SPACING 0.125

// from Fabrice Neyret, 
#define cmul(A, B) ( mat2( A, -(A).y, (A).x ) * (B) )
#define cinv(Z) ( vec2( (Z).x, -(Z).y ) / dot(Z, Z) ) 
#define cdiv(A, B) cmul( A, cinv(B) )
#define cis(a) vec2( cos(a), sin(a) )

// Schlick bias function, from http://dept-info.labri.u-bordeaux.fr/~schlick/DOC/gem2.ps.gz

float bias( float a, float x )
{
    return x/((1.0/a - 2.0) * (1.0 - x) + 1.0);
}

// biased sawtooth

float my_saw( float x, float p )
{
    float xs = mod(x, 1.0);
    float xh = clamp(xs, 0.0, p);
    return 0.5 + 0.5 * bias(0.95, xh) * (1.0 - smoothstep(p, 1.0, xs));
}

// modified DLMF coloring, from https://www.shadertoy.com/view/WtXGWN

vec3 smooth_dlmf( float h )
{
    vec3 rgb = clamp( vec3(1., -1., -1.) * abs((vec3(8., 4., 8.) * mod(h, 1.0) - vec3(4.5, 1.5, 5.5))) + vec3(-1.5, 1.5, 2.5), 0.0, 1.0 );
	return rgb * rgb * rgb/(1.0 - 3.0 * rgb * (1.0 - rgb));
}

// exponentiation using binary powering
vec2 cpow( in vec2 z, in int n )
{
    if (n == 0) return vec2(1.0, 0.0);
    if (n == 1) return z;

    if (n < 0) z = cinv(z);
    n = abs(n);

    vec2 w = vec2(1.0, 0.0);

    for(int k = 0; k < 11; k++) { // should cover up to n == 1000

        if ((n & 1) == 1) w = cmul(w, z);

        z = cmul(z, z);        
        n >>= 1;

        if (n < 2) break;
    }

    return cmul(z, w);
}

// sphere normal

vec3 getNormal(in vec2 c, in float r, in vec2 point)
{
	return mix(vec3(0.0, 0.0, 1.0), normalize(vec3(point - c, 0.0)), length(point - c)/r);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 aspect = iResolution.xy / iResolution.y;
    vec2 uv = ( fragCoord.xy / iResolution.y ) - 0.5 * aspect;
    uv *= SCALE;
    float r = 3.0; // sphere radius

	if( length(uv) < r)
    {
		vec3 sphereNormal = vec3(getNormal(vec2(0.0), r, uv));
		vec3 dirLight = normalize(vec3(0.0, 0.0, 1.0));
		vec3 col = normalize(vec3(1.0)) * dot(sphereNormal, dirLight);

		float xa = sphereNormal.x, ya = sphereNormal.y;
        float s = sqrt((1.0 - ya) * (1.0 + ya)), sx = sqrt((s - xa) * (s + xa));

        // Riemann sphere variable
        vec2 zz = sqrt((1.0 + ya)/(1.0 - ya)) * normalize(vec2(xa, sx));
        // animate the variable, anticlockwise rotation
        zz = cmul(zz, cis(0.1 * PI * iTime));

        // two steps of the Doyle-McMullen iteration; https://doi.org/10.1007/BF02392735
        vec2 w = zz - cmul(12.0 * zz, cdiv(cmul(cpow(zz, 5) + vec2(11.0, 0.0), cpow(zz, 5)) - vec2(1.0, 0.0), cmul(11.0 * cpow(zz, 5), cpow(zz, 5) + vec2(6.0, 0.0)) - vec2(1.0, 0.0)));
        w = w - cmul(12.0 * w, cdiv(cmul(cpow(w, 5) + vec2(11.0, 0.0), cpow(w, 5)) - vec2(1.0, 0.0), cmul(11.0 * cpow(w, 5), cpow(w, 5) + vec2(6.0, 0.0)) - vec2(1.0, 0.0)));

        float ph = atan(w.y, w.x), lm = log(0.0001 + length(w));

        vec3 c = smooth_dlmf(0.5 * (ph / PI));

        c *= my_saw((0.5 * (lm/PI))/SPACING, 0.95) * my_saw((0.5 * (ph / PI))/SPACING, 0.95);

		fragColor = vec4(0.2 + 2.5 * col * c, 1.0);

	} else {

		fragColor = vec4(0.16, 0.14, 0.13, 1.0);

	}
}

