const float PointRadius = 0.0125;

const vec2 Basis[3] = vec2[3](
	vec2(  sqrt(8.0/9.0),           0.0),
	vec2( -sqrt(2.0/9.0), sqrt(2.0/3.0)),
	vec2( -sqrt(2.0/9.0),-sqrt(2.0/3.0))
);

vec3 Project(vec2 Coord)
{
    return vec3(
        dot(Coord,Basis[0]),
        dot(Coord,Basis[1]),
        dot(Coord,Basis[2])
    );
}

struct AABO
{
    vec3 Min;
    vec3 Max;
};

vec3 SweepTest(AABO Bounds, vec3 Test)
{
    return 
        vec3(greaterThanEqual(Bounds.Min,Test))
        - vec3(lessThanEqual(Bounds.Max,Test));
}
float LineDist(vec2 p1, vec2 p2, vec2 Point)
{
    float a = p1.y - p2.y;
    float b = p2.x - p1.x;
    return
        abs(a * Point.x + b * Point.y + p1.x * p2.y - p2.x * p1.y)
        / sqrt(a * a + b * b);
}

mat2 Rotation(float Angle)
{
	float Sin = sin(Angle);
	float Cos = cos(Angle);
	return mat2(Cos, -Sin, Sin, Cos);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    mat2 CurRotation = Rotation(iTime * 0.25);
    vec2 Coord = fragCoord/iResolution.xy;
    vec2 Mouse = iMouse.xy/iResolution.xy;
    Coord = mix(vec2(-1),vec2(1),Coord);
    Mouse = mix(vec2(-1),vec2(1),Mouse);
    Coord.x *= iResolution.x/iResolution.y;
    Mouse.x *= iResolution.x/iResolution.y;

    vec3 MouseProj = Project(Mouse);
    vec3 CoordProj = Project(Coord);

    // Draw the "mesh" (vertex soup)
    float MinDist = 1e9;
    for( uint i = 0u; i < 5u; ++i)
    {
        float CurDist = distance(CurRotation * SomePoints[i],Coord) - PointRadius;
        MinDist = min(MinDist,CurDist);
    }
    float MouseDist = 1.0 - step(0.0,distance(Coord,Mouse) - PointRadius * 2.0);
    float Dots = 1.0 - step(0.0,MinDist);

    // Calculate AABO of "mesh"
    AABO Bounds;
    // Project points onto basis
    vec3 CurProject = Project(CurRotation * SomePoints[0]);
    Bounds.Min = CurProject;
    Bounds.Max = CurProject;
    for( uint i = 1u; i < 5u; ++i)
    {
        vec3 CurProject = Project(CurRotation * SomePoints[i]);
        Bounds.Min = min(Bounds.Min,CurProject);
        Bounds.Max = max(Bounds.Max,CurProject);
    }
    vec3 MouseSweep = SweepTest(Bounds,MouseProj);
    vec3 CoordSweep = SweepTest(Bounds,CoordProj);

    vec3 BasisLines;
    BasisLines.r = LineDist(vec2(0.0),Basis[0],Coord);
    BasisLines.g = LineDist(vec2(0.0),Basis[1],Coord);
    BasisLines.b = LineDist(vec2(0.0),Basis[2],Coord);
    BasisLines = mix(vec3(0.025),vec3(0.005),abs(CoordSweep)) - BasisLines;
    BasisLines = step(vec3(0.0),BasisLines);


    fragColor = vec4(vec3(Dots),0.0);
    vec3 CoordBoundMax = mix(MouseSweep * -0.25,vec3(0.125),lessThanEqual(CoordProj,Bounds.Max));
    vec3 CoordBoundMin = mix(MouseSweep * +0.25,vec3(0.125),greaterThanEqual(CoordProj,Bounds.Min));
    fragColor.rgb += CoordBoundMax; fragColor.rgb += CoordBoundMin;
    fragColor.rgb += BasisLines;
    fragColor.rgb += all(equal(MouseSweep,vec3(0.0)))
        ? vec3(0.0,MouseDist,0.0)
        : vec3(MouseDist,0.0,0.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;

    float t = iTime;

    float k = floor(fract(t+uv.y*10.0)*2.0);

    vec3 img = texture(iChannel0,uv).rgg;

    vec3 col = vec3(k);

    col -= img;

    float cut = fract(t*0.5);
    cut = (sin(t)+1.0)*0.5;
    if(uv.x > cut){
        col = img;
    }

    fragColor = vec4(col,1.0);
}

