// Hopefully this shader debunks some of the misconceptions and myths
// about how to perform conditional operations in GLSL, which wrongly 
// preach that all ternary operations (?:) and branching (if) should
// be avoided at all cost in favor of arithmetic replacements (step, *).
// 
// While the topic of conditional branching is complex and has much to
// do with the work done in the body of the conditional and the coherency
// of computations across neighbor pixels, most of the simple conditional
// operations clearly benefit from using simple and straighforward ternary
// operations.
//
// This shader shader shows how, in particular, painting a single pixel
// in white is actually twice as fast when done with a ternaty operation
// instead of arithmetics. Here are results for my PC:


// change this to see different results
#define METOD 0

//                                   LAPTOP   PHONE
//                                   ------   --------
// METHOD 0 : ternary operation:     83 fps   10.6 fps
// METHOD 1 : step arithmetic:       57 fps    9.9 fps
// METHOD 2 : float to bool:         71 fps    8.9 fps
// METHOD 3 : conditional braching:  83 fps   10.5 fps


// number of tests per frame. Reduce this if your machine is too slow
const int num = 15000;


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 p = floor(fragCoord);

    vec3 tmp = 0.5 + 0.5*sin(iTime+vec3(0.0,2.0,4.0));

    vec3 col = vec3(0.0);
    float h = 127.0 + 0.0001*iTime;

	#define ZERO min(iFrame,0)
    for( int i=ZERO; i<num; i++ )
    {
        // conditional move
        #if METOD==0
        col = (p.x<h && p.y<h+h) ? col+tmp : col;
        #endif

        // "smart" way
        #if METOD==1
        col += tmp*step(p.x,h)*step(p.y,h+h);
        #endif

        // even "smarter" way
        #if METOD==2
        col += tmp*float(p.x<h && p.y<h+h);
        #endif

        // conditional branching
        #if METOD==3
        if( p.x<h && p.y<h+h ) col += tmp;
        #endif

    	h += 0.000013;
    }
    col /= float(num);

    fragColor = vec4(col,1.0);
}

float rand(float n){return fract(sin(n) * (1.+iMouse.x + iMouse.y/10000.));}

float noise(float p){
	float f = floor(p);
	return mix(rand(f), rand(f + 1.0), fract(p));
}


void mainImage( out vec4 fragColor, in vec2 p )
{

    p = (p + p - iResolution.xy) / iResolution.y;
    vec2 m = vec2(-iTime/2. , iTime/3.);
    vec2 noised = vec2(noise(m.x), noise(m.y))/2.;
    for(int i = 0; i < 11; i++)
        p = abs(p) / dot(p,p) - noised-iMouse.xy/iResolution.xy*0.5;

    fragColor = vec4(noise(p.x)*p.y, noise(p.y)*p.x, noise(p.x+p.y),1.);

}

float plane(vec3 p, float y)
{
 return p.y-y;
}

float sphere(vec3 p,vec3 pos,float r)
{
 return length(pos-p)-r;
}

float scene(vec3 p)
{
 float sphere=sphere(p,vec3(0.0,1,6),1.0);
 float plane=plane(p,0.0);
 return min(sphere,plane);
}

float raymarch(vec3 origin,vec3 direction)
{
 float traveled=0.0;
 float distance=0.0;
 for(int i=0;i<128;i++)
 {
  vec3 p=origin+traveled*direction;
  distance=scene(p);
  if(distance<=0.001 || traveled>100.0)
  {
   break;
  }
  traveled+=distance;
 }
 return traveled;
}

vec3 normalAt(vec3 p)
{
 vec2 epsilon=vec2(0.01,0);
 float distanceAtP=scene(p);
 return normalize(vec3(distanceAtP-scene(p-epsilon.xyy),
                       distanceAtP-scene(p-epsilon.yxy),
                       distanceAtP-scene(p-epsilon.yyx)));
}

float diffuse(vec3 p)
{
 vec3 sunPos=vec3(sin(iTime),5,cos(iTime)+6.0);
 vec3 normal=normalAt(p);
 vec3 lightDir=normalize(sunPos-p);
 if(raymarch(p+lightDir*0.1,lightDir)<length(sunPos-p))
 {
  return 0.0;
 }
 return clamp(dot(lightDir,normal),0.0,1.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.x-0.5;

    vec3 color=vec3(0,0,0);
    vec3 origin=vec3(0.0,2.0,0);
    vec3 direction=normalize(vec3(uv,1));
    float traveled=raymarch(origin,direction);
    color=vec3(diffuse(origin+direction*traveled));

    fragColor = vec4(color,1.0);
}

#define MAX_DIST  10000.0
#define RM_DIST 0.01

float PI = 3.14159;

mat2 Rot(float deg)
{ 
    float rad = ((2.*PI)/360.)*deg;
    float c = cos(rad);
    float s = sin(rad);
    return mat2(c, -s, s, c);
}

float plane(vec3 p)
{
  return p.y;
}

float box(vec3 p, vec3 center, vec3 size)
{
    vec3 centeredPos = p-center;
    return length(max(abs(centeredPos)-size, 0.));
}

float sphere(vec3 p, vec3 center, float radius)
{
  return length(p-center)-radius;
}


float smin(float a, float b, float k)
{
  float h = clamp(0.5+0.5*(b-a)/k,0.,1.);
  return mix(b,a,h)-k*h*(1.-h);
}

float map(vec3 pos)
{
  // sphere ring
  // pos = pos + vec3(0.0, 1.0, 0.0);
  // pos = pos * RotXY(fGlobalTime);
  float count = 12.;
  float spheres[12];
  float ds = 0.;
  for (float i=0.; i < count; ++i)
  {
      vec3 spherePos = pos;
      spherePos += vec3(-2,0,0);  //translate          
      spherePos.xz *= Rot(30.*i+2.*iTime);  //rotate  
      spherePos += vec3(2,0,0);  //translate    
      spheres[int(i)] = sphere(spherePos, vec3(0.,0.15,0.), 0.35);    

      if (i==0.)
        ds = spheres[int(i)];
      else
        ds = smin(spheres[int(i)], ds,0.2);
  }

  for (float i=0.; i < count; ++i)
  {
      vec3 spherePos = pos;
      spherePos += vec3(2,0,0);  //translate          
      spherePos.yz *= Rot(90.);  //rotate  
      spherePos.xz *= Rot(30.*i+10.*iTime);  //rotate  
      spherePos += vec3(2,0,0);  //translate    

      spheres[int(i)] = sphere(spherePos, vec3(0.,0.5,0.), .35);    

      ds = smin(spheres[int(i)], ds,.2);
  }



  float dp = plane(pos);

  float db = box(pos, vec3(-2.,0.5,0.), vec3(.25));

  // float dmix = mix (ds, db, sin(fGlobalTime));

  float d = smin(dp,ds,.2);


  return d;
}

vec3 RayMarch(vec3 ro, vec3 rd)
{
  vec3 pos = ro;
  float dist = 0.;

  for (int i=0; i<100; ++i)
  { 
    pos += dist*rd;
    dist = map(pos);

    if ((dist < RM_DIST) || dist > MAX_DIST)
    {
      break;
    }
  }

  return pos;
}

vec3 getNormal(vec3 pos)
{
  vec2 eps = vec2(0.0001,0);
  vec3 normal = normalize(vec3(map(pos + eps.xyy)-map(pos - eps.xyy),map(pos + eps.yxy)-map(pos - eps.yxy),map(pos + eps.yyx)-map(pos - eps.yyx)));

  return normal;
}

vec3 applyFog(vec3 color, float dist)
{ 
   float fogColor = (exp(1.-10./(dist*dist)))/exp(1.);
   return mix(color, vec3(0.), fogColor);
}

float getDiffuse(vec3 pos)
{
  vec3 lightPos = vec3(2,5,-4);
  // lightPos.xz *= Rot(30.*iTime);
  vec3 light = normalize(lightPos - pos);
  vec3 normal = getNormal(pos);

  float dimFactor=1.0;

  vec3 sd = normalize(lightPos-pos);
  vec3 so = pos + 0.05*sd;

  vec3 surface = RayMarch(so, sd);
  if (length(surface-pos) < length(lightPos-pos))
      dimFactor = 0.2;

  return dimFactor * dot(normal,light)*0.5+0.25;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
  vec2 uv = vec2(fragCoord.x / iResolution.x, fragCoord.y / iResolution.y);
  uv -= 0.5;
  uv /= vec2(iResolution.y /iResolution.x, 1);

  vec3 ro = vec3(0,1,-5.5);
  vec3 rd = normalize(vec3(uv, 1.0));
  vec3 pos = RayMarch(ro, rd);
  vec3 myColor = vec3(getDiffuse(pos));
  myColor = applyFog(myColor, pos.z);
  fragColor  = vec4(myColor, 1.0);
}

