// experimental implementation of lattice convolution noise

// permutation polynomial

int permp (int i1, int i2)
{
      int t = (i1 + i2) & 255;

      return ((112 * t + 153) * t + 151) & 255;
}

// lattice convolution noise; adapted from Ebert et al., Texturing & Modeling: A Procedural Approach 3rd ed.

float lcnoise(vec3 p)
{
    float f = 0.0;
    ivec3 iv = ivec3(floor(p));
    vec3 fv = fract(p) + 1.0;

    int m = 0; // initialization for Knuth's "algorithm H"
    ivec3 di = ivec3(1), ki = ivec3(0);
    ivec4 fi = ivec4(0, 1, 2, 3);

    // instead of writing a triply nested loop (!!)
    // generate the indices for the neighbors in Gray order (Knuth's "algorithm H")
    // see section 7.2.1.1 of TAOCP

	for (int k = 0; k < 64; k++) // loop through all neighbors
    { 
		 // seeding
        int s = permp(permp(permp(0, iv.z + ki.z), iv.y + ki.y), iv.x + ki.x) + 531;

        // L'Ecuyer simple LCG
        s = (2469 * s) % 65521;
        float c = 2.0 * (float(s)/65521.0) - 1.0;

        // Mitchell-Netravali cubic, https://doi.org/10.1145/54852.378514
        float r = length(vec3(ki) - fv);
        f += c * (( r < 1.0 ) ? (16.0 + r * r * (21.0 * r - 36.0)) : (( r < 2.0 ) ? (32.0 + r * ((36.0 - 7.0 * r) * r - 60.0)) : 0.0))/18.0;

        // updating steps for Knuth's "algorithm H"
        m = fi[0]; fi[0] = 0; ki[2 - m] += di[m];
        if ((ki[2 - m] % 3) == 0) {
            di[m] = -di[m];
            fi[m] = fi[m + 1]; fi[m + 1] = m + 1;
        }
	}

    return f;
}

// modified MATLAB bone colormap

vec3 bone( float t )
{
	 return 0.875 * t + 0.125 * clamp(vec3(4.0, 3.0, 3.0) * t - vec3(3.0, 1.0, 0.0), 0.0, 1.0);
}

// adapted from Inigo Quilez's demo, https://www.shadertoy.com/view/Xsl3Dl

const mat3 m = mat3( 0.00, -0.80,  0.70,
                     0.80, -0.86, -0.48,
                    -0.70,  0.48, -0.64 );

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 p = (-iResolution.xy + 2.0 * fragCoord.xy) / iResolution.y;

     // camera movement	
	float an = 0.5 * iTime;
	vec3 ro = vec3( 2.5 * cos(an), 1.0, 2.5 * sin(an) );
    vec3 ta = vec3( 0.0, 1.0, 0.0 );
    // camera matrix
    vec3 ww = normalize( ta - ro );
    vec3 uu = normalize( cross(ww, vec3(0.0, 1.0, 0.0) ) );
    vec3 vv = normalize( cross(uu, ww));
	// create view ray
	vec3 rd = normalize( p.x * uu + p.y * vv + 1.5 * ww );

    // sphere center	
	vec3 sc = vec3(0.0, 1.0, 0.0);

    // raytrace
	float tmin = 1.0e3;
	vec3  nor = vec3(0.0);
	float occ = 1.0;
	vec3  pos = vec3(0.0);

	// raytrace-plane
	float h = -ro.y/rd.y;
	if( h > 0.0 ) 
	{ 
		tmin = h; 
		nor = vec3(0.0, 1.0, 0.0); 
		pos = ro + h * rd;
		vec3 di = sc - pos;
		float l = length(di);
		occ = 1.0 - dot(nor, di/l)/(l * l); 
	}

	// raytrace-sphere
	vec3  ce = ro - sc;
	float b = dot( rd, ce );
	float c = dot( ce, ce ) - 1.0;
	h = b * b - c;
	if( h > 0.0 )
	{
		h = -b - sqrt(h);
		if( h < tmin ) 
		{ 
			tmin = h; 
			nor = normalize(ro + h * rd - sc); 
			occ = 0.5 + 0.5 * nor.y;
		}
	}

    // shading/lighting	
	vec3 col = vec3(0.85);
	if( tmin < 100.0 )
	{
	    pos = ro + tmin * rd;
	    float f = 0.0;

		if( p.x < 0.0 )
		{
			f = lcnoise( 18.0 * pos );
		}
		else
		{
            vec3 q = 7.5 * pos;
            f  = 0.5000 * lcnoise( q ); q = m * q * 2.02;
            f += 0.2500 * lcnoise( q ); q = m * q * 2.03;
            f += 0.1250 * lcnoise( q ); q = m * q * 2.01;
            f += 0.0625 * lcnoise( q );
		}


		f = smoothstep( -0.7, 0.7, f ) * occ;
		col = bone(sqrt(f));
		col = mix( vec3(0.85), col, exp( -0.005 * tmin * tmin ) );
	}

	fragColor = vec4(col * smoothstep( 0.006, 0.008, abs(p.x) ), 1.0);
}

