#define time iTime

float PI = 3.141592;
float BPM = 138./60.;


mat2 rot(float a)
{return mat2(cos(a),sin(a),-sin(a),cos(a));}

void moda (inout vec2 p, float rep)
{
    float per = 2.*PI/rep;
    float a = atan(p.y, p.x);
    float l = length(p);
    a = mod(a-per*0.5,per)-per*0.5;
    p = vec2(cos(a),sin(a))*l;  
}

void mo (inout vec2 p, vec2 d)
{
    p = abs(p)-d;
    if(p.y >p.x) p = p.yx;
}

float stmin (float a, float b, float k, float n)
{
    float st = k/n;
    float u = b-k;
    return min(min(a,b), 0.5*(u+a+abs(mod(u-a+st,2.*st)-st)));
}

float box (vec3 p, vec3 c)
{
    vec3 q = abs(p)-c;
    return min(0., max(q.x, max(q.y,q.z))) + length(max(q,0.));
}

float cyl(vec3 p, float r, float h)
{return max(length(p.xy)-r,abs(p.z)-h);}

vec3 palette (float t, vec3 a, vec3 b, vec3 c, vec3 d)
{return a+b*cos(2.*PI*(c*t+d));}

float set (vec3 p)
{
    float per = 4.;
    p.xy *= rot(p.z*0.3);
    p.z = mod(p.z-per*0.5, per)-per*0.5;
    mo(p.xy, vec2(5.,1.));
    moda(p.xy, 5.);
    p.x -= 2.;
    mo(p.xy, vec2(2.,1.));
    p.xz *= rot(time);
    p.xy *= rot(-time);
    float b1 = box(p, vec3(0.18,5., 0.18));
    moda(p.xz, 5.);
    p.x -= 1.;
    moda(p.xy, 5.);
    p.x -= 1.;
    return stmin(b1,box (p, vec3(0.15, 5., 0.15)),1.2, 3.);
}

float c1_id = 0.;
float c2_id = 0.;
float knitting (vec3 p)
{
    p.z += time*0.5;
    p.x += sin(p.y*2.+time)*0.2;
    float per = 0.22;
    vec3 pp = p;

    c1_id = floor((p.x-per*.5)/per);
    p.x = mod(p.x-per*.5, per)-per*.5;
    float c1 = cyl(p.xzy, 0.05, 1e10); 

    p = pp;

    c2_id = floor((p.y-per*.5)/per);
    p.y = mod(p.y-per*.5, per)-per*.5;
    p.z = mod(c2_id-.5, 2.) == 0. ? p.z-cos((p.x+.5)*PI)*0.3 : p.z-sin((p.x)*PI)*0.3;
    float c2 = cyl(p.yzx, 0.05, 1e10);
    return min(c1,c2);
}

float SDF(vec3 p)
{
    p.xy *= rot(floor(time*BPM)*(PI/5.));
    return max(knitting(p),set(p));
} 


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Sorry Fabrice Neyret! Maybe next time I'll be brave enough to change it ^^
    vec2 uv = vec2(fragCoord.x / iResolution.x, fragCoord.y / iResolution.y);
    uv -= 0.5;
    uv /= vec2(iResolution.y / iResolution.x, 1);

    vec3 ro = vec3(0.001,0.001,-12.-time*0.5); vec3 p = ro;
    vec3 rd = normalize(vec3(uv,1.));
    vec3 col = vec3(0.);
    bool hit = false;

    for (float i=0.; i<100.; i++)
    {
        float d = SDF(p);
        if(d<0.01)
        {
            hit = true;
            break;
        }
        p += d*rd*0.5;
    }

    if (hit)
    {
        vec3 c1 = palette (c1_id*c2_id, vec3(0.5), vec3(0.5), vec3(0.08), vec3(0.,0.36,0.74));
        vec3 c2 = palette (c1_id*c2_id, vec3(0.5), vec3(0.5), vec3(0.08), vec3(0.5));
        col = mix(c1, c2, clamp(floor(sin(time*(BPM/2.)*PI))+1.,0.,1.));
    }
    else col = vec3(0.,0.,0.01);

    fragColor = vec4(pow(col,vec3(0.4545)), 1.);
}

