// Newton-Raphson fractal implementation, using Horner's method

#define PI 3.14159265359
#define SCALE 7.5 // plot scale

// from Fabrice Neyret, 
#define cmul(A, B) ( mat2( A, -(A).y, (A).x ) * (B) )
#define cinv(Z) ( vec2( (Z).x, -(Z).y ) / dot(Z, Z) ) 
#define cdiv(A, B) cmul( A, cinv(B) )

// modified DLMF coloring, adapted from https://www.shadertoy.com/view/WtXGWN
vec3 smooth_dlmf( float h )
{
    vec3 rgb = clamp( vec3(1., -1., -1.) * abs((vec3(8., 4., 8.) * mod(h, 1.0) - vec3(4.5, 1.5, 5.5))) + vec3(-1.5, 1.5, 2.5), 0.0, 1.0 );
	return rgb * rgb * rgb/(1.0 - 3.0 * rgb * (1.0 - rgb));
}

// order and coefficients of base polynomial, constant term first
#define ORDER 3
const float[] P = float[](0.0, -2.0, 0.2, 1.0);

// use Horner's method to evaluate the Newton-Raphson update
vec2 Horner( in vec2 z )
{
    vec2 w = vec2(P[ORDER], 0.0);
    vec2 d = vec2(0.0, 0.0);

    for(int k = ORDER - 1; k >= 0; k--)
    {
        d = cmul(d, z) + w;        
        w = cmul(w, z) + vec2(P[k], 0.0);
    }

    w += vec2(7.5, 2.0) * cos(0.2 * iTime); // jiggle the constant term a bit for animation

    return cdiv(w, d);
}

vec3 NewtonFractal( vec2 z) {

    vec2 ld = vec2(0.0);
    float k = 0.0;

    for(int i = 0; i < 20; i++) {
        ld = Horner(z);
        if (dot(ld, ld) < 0.001) break;
        z = z - ld;
        k += 1.0;
    }

    // multiplier highlights the positions of the roots
    return pow((k + 0.1)/20.0, 0.18) * smooth_dlmf(0.5 * atan(z.y, z.x)/PI);

}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 aspect = iResolution.xy / iResolution.y;
    vec2 z = ( fragCoord.xy / iResolution.y ) - 0.5 * aspect;
    z *= SCALE;

    vec3 c = NewtonFractal(z);

    fragColor = vec4(c, 1.0);
}

/*

2019 Nel (@nelstuff)
Based on IQ's https://www.shadertoy.com/view/Xsl3Dl and https://www.shadertoy.com/view/XlXcW4

Goal:
- 3D gradient noise at very far distances
- Generate roughly the same values across all high-end GPU hardware (so no "fract(sin)")

Assumption:
- Able to provide a true integer coordinate and a float offset to this coordinate

*/


//===============================================================================================
// High Precision 3D Gradient Noise
//===============================================================================================


// Based on IQ's Integer Hash - II  https://www.shadertoy.com/view/XlXcW4
vec3 inthash( uvec3 x ){
    const uint k = 1103515245U;  // GLIB C
    x = ((x>>8U)^x.yzx)*k;
    x = ((x>>8U)^x.yzx)*k;
    x = ((x>>8U)^x.yzx)*k;

    //Convert uvec3 to vec3
    // Insert the result into a float's mantissa: https://stackoverflow.com/a/17479300
    const uvec3 ieeeMantissa = uvec3(0x007FFFFFu); 	// binary32 mantissa bitmask
    const uvec3 ieeeOne      = uvec3(0x3F800000u); 	// 1.0 in IEEE binary32
    x &= ieeeMantissa;                     			// Keep only mantissa bits (fractional part)
    x |= ieeeOne;                          			// Add fractional part to 1.0
    vec3  f = uintBitsToFloat( x );       			// Range [1:2]
    return -3.0+2.0*f;                        		// Range [-1:1]

    // Other method to convert uvec3 to vec3
    //return -1.0 + 2.0 * vec3(x)*(1.0/float(0xffffffffU));
}


// High Precision 3D Gradient Noise
// ix: integer coordinate, fx: float offset to the integer coordinate
float newnoise(in ivec3 ix, in vec3 fx){
    // grid
    uvec3 p = uvec3(ix + ivec3(floor(fx)) );
    vec3 w = fract(fx);
    vec3 u = w*w*(3.0-2.0*w);
    return mix( mix( mix( dot( inthash( p  ), w  ), 
                      dot( inthash( p + uvec3(1,0,0) ), w - vec3(1.0,0.0,0.0) ), u.x),
                 mix( dot( inthash( p + uvec3(0,1,0) ), w - vec3(0.0,1.0,0.0) ), 
                      dot( inthash( p + uvec3(1,1,0) ), w - vec3(1.0,1.0,0.0) ), u.x), u.y),
            mix( mix( dot( inthash( p + uvec3(0,0,1) ), w - vec3(0.0,0.0,1.0) ), 
                      dot( inthash( p + uvec3(1,0,1) ), w - vec3(1.0,0.0,1.0) ), u.x),
                 mix( dot( inthash( p + uvec3(0,1,1) ), w - vec3(0.0,1.0,1.0) ), 
                      dot( inthash( p + uvec3(1,1,1) ), w - vec3(1.0,1.0,1.0) ), u.x), u.y), u.z );
}

// Fractal Brownian Motion 
float newfractal(ivec3 ixyz, vec3 fxyz){ 
    // instead of floats, we need to use integer period and integer lacunarity
    const int period = 2; 
    const int octaves=3;
    const int lacunarity = 2; 
    const float persistence = 0.6;

    float value = 0.0;
    float curpersistence = 1.0;

    ivec3 ispace = ixyz / period;
    vec3 fspace = vec3(ixyz - ispace * period) / vec3(period) + fxyz / vec3(period);

    for (int i = 0; i < octaves; i++){
        value += newnoise(ispace, fspace) * curpersistence;
        curpersistence *= persistence;
        ispace *= lacunarity;
        fspace *= float(lacunarity);
    }
    return value;
}

//===============================================================================================
// Baseline: IQ's 3D Gradient Noise  https://www.shadertoy.com/view/Xsl3Dl
//===============================================================================================

vec3 hash( vec3 p ){ // replace this by something better
	p = vec3( dot(p,vec3(127.1,311.7, 74.7)),
			  dot(p,vec3(269.5,183.3,246.1)),
			  dot(p,vec3(113.5,271.9,124.6)));
	return -1.0 + 2.0*fract(sin(p)*43758.5453123);
}

float oldnoise( in vec3 p ){
    vec3 i = floor( p );
    vec3 f = fract( p );
	vec3 u = f*f*(3.0-2.0*f);
    return mix( mix( mix( dot( hash( i + vec3(0.0,0.0,0.0) ), f - vec3(0.0,0.0,0.0) ), 
                          dot( hash( i + vec3(1.0,0.0,0.0) ), f - vec3(1.0,0.0,0.0) ), u.x),
                     mix( dot( hash( i + vec3(0.0,1.0,0.0) ), f - vec3(0.0,1.0,0.0) ), 
                          dot( hash( i + vec3(1.0,1.0,0.0) ), f - vec3(1.0,1.0,0.0) ), u.x), u.y),
                mix( mix( dot( hash( i + vec3(0.0,0.0,1.0) ), f - vec3(0.0,0.0,1.0) ), 
                          dot( hash( i + vec3(1.0,0.0,1.0) ), f - vec3(1.0,0.0,1.0) ), u.x),
                     mix( dot( hash( i + vec3(0.0,1.0,1.0) ), f - vec3(0.0,1.0,1.0) ), 
                          dot( hash( i + vec3(1.0,1.0,1.0) ), f - vec3(1.0,1.0,1.0) ), u.x), u.y), u.z );
}

float oldfractal(vec3 xyz){ 
    const float frequency = 0.5; 
    const int octaves=3;
    const float lacunarity = 2.0; 
    const float persistence = 0.6;

    float value = 0.0;
    float curpersistence = 1.0;

    vec3 space = xyz * frequency;

    for (int i = 0; i < octaves; i++){
        value += oldnoise(space) * curpersistence;
        curpersistence *= persistence;
        space *= lacunarity;
    }
    return value;
}

//===============================================================================================
// Image (https://www.shadertoy.com/view/Xsl3Dl)
//===============================================================================================

const mat3 m = mat3( 0.00,  0.80,  0.60,
                    -0.80,  0.36, -0.48,
                    -0.60, -0.48,  0.64 );

void mainImage( out vec4 fragColor, in vec2 fragCoord ){
	vec2 p = (-iResolution.xy + 2.0*fragCoord.xy) / iResolution.y;

     // camera movement	
	float an = 0.5*iTime;
	vec3 ro = vec3( 2.5*cos(an), 1.0, 2.5*sin(an) );
    vec3 ta = vec3( 0.0, 1.0, 0.0 );
    // camera matrix
    vec3 ww = normalize( ta - ro );
    vec3 uu = normalize( cross(ww,vec3(0.0,1.0,0.0) ) );
    vec3 vv = normalize( cross(uu,ww));
	// create view ray
	vec3 rd = normalize( p.x*uu + p.y*vv + 1.5*ww );

    // sphere center	
	vec3 sc = vec3(0.0,1.0,0.0);

    // raytrace
	float tmin = 10000.0;
	vec3  nor = vec3(0.0);
	float occ = 1.0;
	vec3  pos = vec3(0.0);

	// raytrace-plane
	float h = (0.0-ro.y)/rd.y;
	if( h>0.0 ) { 
		tmin = h; 
		nor = vec3(0.0,1.0,0.0); 
		pos = ro + h*rd;
		vec3 di = sc - pos;
		float l = length(di);
		occ = 1.0 - dot(nor,di/l)*1.0*1.0/(l*l); 
	}

	// raytrace-sphere
	vec3  ce = ro - sc;
	float b = dot( rd, ce );
	float c = dot( ce, ce ) - 1.0;
	h = b*b - c;
	if( h>0.0 ){
		h = -b - sqrt(h);
		if( h<tmin ) { 
			tmin=h; 
			nor = normalize(ro+h*rd-sc); 
			occ = 0.5 + 0.5*nor.y;
		}
	}

    // shading/lighting	
	vec3 col = vec3(0.9);
	if( tmin<100.0 )
	{
	    pos = ro + tmin*rd;
	    float f = 0.0;

        // varying distance every 2 secs
        int dist = 0;
        int t = int(floor(iTime*0.5));
        if(t % 3 == 1){
            dist = int(1e+7);
        }else if(t % 3 == 2){
            dist = int(1e+9);
        }

        //left
		if( p.x<0.0 ){
			f = newfractal(ivec3(0, 0, dist), 16.0*pos );
		}
        //right
		else{
            f = oldfractal(vec3(0, 0, dist) + 16.0*pos );
		}


		f = smoothstep( -0.7, 0.7, f );
		f *= occ;
		col = vec3(f*1.2);
		col = mix( col, vec3(0.9), 1.0-exp( -0.003*tmin*tmin ) );
	}

	col = sqrt( col );

	col *= smoothstep( 0.006, 0.008, abs(p.x) );

	fragColor = vec4( col, 1.0 );
}

#define s(a,b,t) smoothstep(a,b,t)
#define sat(x) clamp(x, 0., 1.)
#define da iTime
float remap01(float a, float b, float t){
    return sat((t-a)/(b-a));
}

float remap(float a, float b, float c, float d, float t){
    return sat((t-a)/(b-a))*(d-c) + c;
}
vec2 within(vec2 uv, vec4 rect){
    return (uv - rect.xy)/(rect.zw - rect.xy);
} 

vec4 Eye(vec2 uv){
    uv -= .5;
    vec4 col = vec4(0.);
    vec4 iris = vec4(.3,.5,1.,1.);

    float d = length(uv);
    col = mix(vec4(1.), iris, s(.1,.7,d)*.5);

    col.rgb *= 1. - s(0.45, 0.5, d)*.5*sat(-uv.x-uv.y);

    col.rgb = mix(col.rgb, vec3(0.), s(.3,.25,d));

    iris.rgb *= 1. + s(.3, .05, d);
    //if(da>5.)
    d = length( uv*cos(da-uv) + atan(uv.y*-tan(da)*uv.x));
    d = atan(d);
    d = pow(d,(sin(da)+1.)/4.);
    col.rgb = mix(col.rgb, iris.rgb, s(.28, .25, d));

    col.rgb = mix(col.rgb, vec3(0.), s(.16, 0.14, d));

    float highlight = s(.1, .05, length(uv - vec2(-.15,.15)));
    highlight += s(.09, 0.03, length(uv + vec2(-.08,.08)));
    col.rgb = mix(col.rgb, vec3(1.), highlight);
    col.a = s(.5, .48, d);

    return col;
}   

vec4 Mouth(vec2 uv){
    uv -= .5;
    vec4 col = vec4(0.5, .18, .05, 1.);
    uv.y *= atan(da,uv.y);
    uv.y -=  tan(uv.x+da)*uv.x*uv.x*uv.x*sin(iTime*2.)*5. ;
    uv.y += uv.x*uv.x*sin(da*2.)*tan(uv.x*-uv.y-da);
    float d = length(uv);
    col.a = s(.5,.48,d);

    float td = length(uv - vec2(0., .6));
    vec3 tc = vec3(1.)*s(.6, .35, d);
    col.rgb = mix(col.rgb, tc, s(.4, .39,td));

    td = length(uv + vec2(0., .5));
    col.rgb = mix(col.rgb, vec3(1., .5, .5),s(.5,.4,td));
    return col;
}

vec4 Head(vec2 uv){
    vec4 col = vec4(1.);

    float d = length(uv);

    col.a = s(0.5, 0.49, d);

    float edge = remap01(0.35, 0.5, d);
    edge *= edge;
    col.rgb *=  1. - edge*.5;

    col.rgb = mix(col.rgb, vec3(.2,.5,.9), s(.485,.49,d));

    float highlight = s(0.41, 0.405, d) ;
    highlight *= remap(0.41, 0.0, .75, 0., uv.y);
    col.rgb = mix(col.rgb, vec3(1.),highlight);

    d = length(uv - vec2(.25, -.2) );
    float cheek = s(0.2, 0.01,d)*.3;
    cheek *= s(0.18, 0.17, d);
    col.rgb = mix(col.rgb, vec3(1., .1, .1), cheek);


    return col;
}

vec4 Smiley(vec2 uv){
    vec4 col = vec4(0.);
    uv.x = abs(uv.x);
    vec4 head = Head(uv);
    vec4 eye = Eye( within(uv, vec4(0.03, -0.1, .37, .24)) );
    vec4 mouth = Mouth( within(uv, vec4(-.3, -0.4, .3, -.1)) );

    col = mix(col, head, head.r);
    col = mix(col, eye, eye.a);
    col = mix(col, mouth, mouth.a);

    return col;
}

void mainImage(out vec4 FragColor, in vec2 FragCoord ){
    vec2 uv = FragCoord.xy/iResolution.xy;
    uv -= 0.5;
    uv.x *= iResolution.x/iResolution.y;


    vec4 col = Smiley(uv+dot((sin(da)+1.)/2.,uv.x));

    if(da<5.)
        col = Smiley(uv);

    FragColor = col;

}

#define DARK_BLUE 	0x6169AE
#define PURPLE 		0x5C3489
#define CYAN 		0x83C9E3
