/*
	Oren-Nayar reflectance modeling. I use this everywhere. Just looks good.
*/
float orenNayar( in vec3 n, in vec3 v, in vec3 ldir )
{
    float r2 = pow(MAT_REFLECTANCE, 2.0);
    float a = 1.0 - 0.5*(r2/(r2+0.57));
    float b = 0.45*(r2/(r2+0.09));

    float nl = dot(n, ldir);
    float nv = dot(n, v);

    float ga = dot(v-n*nv,n-n*nl);

	return max(0.0,nl) * (a + b*max(0.0,ga) * sqrt((1.0-nv*nv)*(1.0-nl*nl)) / max(nl, nv));
}

/**
 * Models a point light.
 */
vec3 pointLight( in vec3 p, in vec3 n, in vec3 lp, in vec3 rd, in vec3 texel, in vec3 lc )
{

    vec3 ld = lp-p; 								// Direction of light.
    float dist = length(ld); 						// Distance to the light.
    ld = normalize(ld); 							// Normalize for correct trig.
    float base = orenNayar(n, rd, ld)*BRIGHTNESS; 	// Base lighting coefficient.
    float falloff = clamp(1.0/(dist*dist),0.0,1.0); // Quadratic coefficient.

    // Specular.
    #ifdef ENABLE_SPECULAR
    vec3 reflection = normalize(reflect(rd,n));
    float specular = clamp(pow(clamp(dot(ld, reflection),0.0,1.0),25.0),0.0,1.0);
    #else
    float specular = 0.0;
    #endif // ENABLE_SPECULAR

    // Optionally do shadows.
    #ifdef ENABLE_SHADOWS
    float shadowDist;
    traceShadow(p+ld*.01, ld, shadowDist);
    float shadow = smoothstep(dist*.99, dist*1.01,shadowDist);
    #else
    float shadow = 1.0;
    #endif // ENABLE_SHADOWS

    return lc*specular*shadow + base*falloff*shadow*lc*texel + lc*.0125;
}

/**
 * Lights the entire scene by tracing both point lights.
 */
vec3 lightScene( in vec3 p, in vec3 n, in vec3 rd, in vec3 texel )
{

    return clamp( pointLight(p, n, lightpos1(), rd, texel, LIGHT1_COLOR) +
              	  pointLight(p, n, lightpos2(), rd, texel, LIGHT2_COLOR),
              	  vec3(0),vec3(1) );
}

/**
 * Takes it a step further by coloring based on object ID.
 */
vec3 shade( in vec3 p, in vec3 n, in vec3 rd, in float dist, in float id )
{
    if(id == ID_NONE) return vec3(0);
    else if(id == ID_TUNNEL) return vec3( lightScene(p+rd*dist, n, rd, vec3(1)) );
    else if(id == ID_LIGHT1) return LIGHT1_COLOR*BRIGHTNESS*2.0;
    else if(id == ID_LIGHT2) return LIGHT2_COLOR*BRIGHTNESS*2.0;
    else return vec3(0);
}

/**
 * Some quick tonemapping and vignetting.
 */
vec4 postProcess( in vec3 c, in vec2 uv )
{
    float vig = 1.0-dot(uv,uv)*.6;
    c = pow(clamp(c, 0., 1.), vec3(.4545));
    return vec4(c*vig,1);
}

/**
 * Entrypoint.
 */
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord / iResolution.xy - 0.5;
	uv.x *= iResolution.x/iResolution.y; //fix aspect ratio

    // Set up the camera.

    // Position.
    vec3 cp =  vec3(3.0*cos(iTime*.5), sin(iTime*.25), 3.0*sin(iTime*.25));

    // Direction.
    vec3 cd = normalize(vec3(-cos(iTime*.5), .5*cos(iTime*.25), -sin(iTime*.25)));

    // Places to store results.
    vec3 p, d;

    // Create the view ray.
    camera(uv, cp, cd, .667, p, d);

    // Do the traces.
    float id; float dist; vec3 n; vec2 texCoord;
    trace(p, d, id, dist, n, texCoord);

    // Shade the point.
    vec3 c = shade(p, n, d, dist, id);



    // Based on the results of that trace, we shade accordingly.
    fragColor = postProcess(c, uv);
}

const vec2 gradients[4] = vec2[4](
    vec2(1.0,1.0),vec2(1.0,-1.0),
    vec2(-1.0,-1.0),vec2(-1.0,1.0));

const float pi = 3.141592653589793238462643383279502884157169399375105820974944592307816406286208998628034825342117067982148086513282;

const vec3 rainbowColors[6] = vec3[6](
    vec3(1.0,0.0,0.0),vec3(1.0,0.5,0.0),vec3(1.0,1.0,0.0),
    vec3(0.0,0.75,0.0),vec3(0.0,0.75,1.0),vec3(0.5,0.0,1.0));

float hash(vec2 v)
{
    float h = v.x * 1.0071 + v.y * 0.9873;
    h = fract((h+0.00103)*v.x*0.927) + fract((h+0.0010421)*v.y*1.0219);
    h = fract(h*2184.72427142 + 1.732);
    h = fract(h * 21982.623632 + 4.637235);
    return h;
}

float sFac(float f)
{
    return 0.5-cos(clamp(f,0.0, 1.0)*pi)*0.5;
}

float aastep(float x, float y, float px)
{
	return clamp(y-x,0.0,1.0/px)*px;
}

float grid(vec2 uv, vec2 size)
{
	vec2 uv0 = floor(uv);

    return aastep(uv.x - uv0.x, size.x, 40.0)
         + aastep(1.0 + uv0.x - uv.x, size.x, 40.0)
         + aastep(uv.y - uv0.y, size.y, 40.0)
         + aastep(1.0 + uv0.y - uv.y, size.y, 40.0);
}

float vector(vec2 v0, vec2 v, vec2 uv, float dotSize, float lineWidth)
{
    lineWidth *= 0.7;
    v *= 0.45;

    vec2 v1 = uv - v0;
    float v1Len = length(v1);

    float dotVal = aastep(v1Len, dotSize, 40.0);

    vec2 vDot = vec2(dot(v1,v) , dot(vec2(-v.y,v.x),v1));

    float vecVal = aastep(vDot.x,0.0,40.0)*aastep(abs(vDot.y), lineWidth, sqrt(2.0)*40.0)*aastep(v1Len, length(v), 40.0);

    return max(dotVal, vecVal);
}

float gradient(vec2 v0, vec2 v, vec2 uv)
{
    vec2 v1 = v0 - uv;   
    return dot(v1,v);
}

float pNoiseTransition(vec2 uv)
{
    //calculate positions of quad corners
    vec2 v0 = floor(uv);
    vec2 v2 = v0 + 1.0;
    vec2 v1 = vec2(v2.x, v0.y);
    vec2 v3 = vec2(v0.x, v2.y);

    //calculate colors of gradient for four vectors
	float g0 = gradient(v0, gradients[int(hash(v0)*4.0)], uv);
	float g1 = gradient(v1, gradients[int(hash(v1)*4.0)], uv);
    float g2 = gradient(v2, gradients[int(hash(v2)*4.0)], uv);
    float g3 = gradient(v3, gradients[int(hash(v3)*4.0)], uv);

    //calculate x and y distance to interpolate between four gradients
    vec2 fac = uv-v0;

    //calculate transition between gradients view and noise view.
    float transition = (clamp(sin(iTime)*0.5+0.5,0.3,0.7)-0.3)/0.4;

    //use transition to calculate final mix curve.
    //(stretch fac relative to 0.5, from normal - 1.0 scale, for noise, to 32.0 for gradients view)
    fac = (fac-0.5)*pow(32.0,transition)+0.5;

    //use clamped cosine interpolation to get smoothed step for gradient
    //and smooth transition for noise view
    fac = vec2(sFac(fac.x),sFac(fac.y));

    //Use fac.x to mix gradients in x
    float g01 = mix(g0,g1,fac.x);
    float g23 = mix(g3,g2,fac.x);

    //Use fac.y to mix gradients in y (already mixed in x)
    float g = mix(g01,g23,fac.y);

    //Move result from <-0.5,0.5> range to <0.0,1.0>
    return g*0.5+0.5;
}

float pNoise(vec2 uv)
{
    vec2 v0 = floor(uv);
    vec2 v2 = v0 + 1.0;
    vec2 v1 = vec2(v2.x, v0.y);
    vec2 v3 = vec2(v0.x, v2.y);

	float g0 = gradient(v0, gradients[int(hash(v0)*4.0)], uv);
	float g1 = gradient(v1, gradients[int(hash(v1)*4.0)], uv);
    float g2 = gradient(v2, gradients[int(hash(v2)*4.0)], uv);
    float g3 = gradient(v3, gradients[int(hash(v3)*4.0)], uv);

    vec2 fac = uv-v0;
    fac = vec2(sFac(fac.x),sFac(fac.y));

    float g01 = mix(g0,g1,fac.x);
    float g23 = mix(g3,g2,fac.x);

    float g = mix(g01,g23,fac.y);

    return g*0.5+0.5;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/50.0 + 2.0*iTime*0.1;

    //grid and vectors alpha (1.0 - transparency)
    float transition = (clamp(sin(iTime*0.5)*0.25+0.5,0.3,0.7)-0.3)/0.4;

    //draw grid
    float gridRet = grid(uv,vec2(0.025,0.025))*transition;

    //draw gradient vectors (red ones)
    vec2 v0 = floor(uv+0.5);
    float vecRet = vector(v0 , gradients[int(hash(v0)*4.0)], uv, 0.06, 0.03)*transition;


    //draw perlin noise with [gradient - noise] transition
    float gradRet = pNoiseTransition(uv);

	//calculate alpha for noise
    float NoGrad = clamp(1.0 - gridRet - vecRet, 0.0 ,1.0);

    fragColor = vec4(vec3(gradRet)*NoGrad,1.0) + vec4(0.0,(gridRet)*(1.0-vecRet),0.0,1.0) + vec4(vecRet,0.0,0.0,1.0);
}

#define SF 1./min(iResolution.x,iResolution.y)
#define SS(l,s) smoothstep(SF,-SF,l-s)
#define BLACK_COL vec3(16,21,25)/255.

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{    
    vec2 uv = (fragCoord - iResolution.xy*.5)/iResolution.y;

    float m = 0.;
    float t = iTime*2.;
    for(float i = 0.; i< 30.;i+=1.){
        float sv = sin(uv.x*10. + cos(t+i*.4))*.1;
        float y = uv.y + i*.025 - .15;
        m += (SS(y, sv) - SS(y + .001 * (0. + i*.5), sv)*.975)*(.75+i*.01) ;
    }

    vec3 col = mix(BLACK_COL, (0.5 + 0.5*cos(iTime+uv.xyx+vec3(0,2,4))), m);

    fragColor = vec4(col,1.0);
}

