// Study around the snake() function, to build uv-space shaped like a snake,
// where each section is half a circle.

#define PI 3.141593

float pbeat(float t, float p)
{
    return pow(1. - fract(t), p);
}

mat2 rot(float t)
{
    float s = sin(t);
    float c = cos(t);
    return mat2(c, s, -s, c);
}

float fill(float d)
{
    return smoothstep(.01, .0, d);
}

float sat(float x) { return clamp(x, 0., 1.); }

float triangle(vec2 uv, float radius)
{
    return min(
        fill(abs(uv.y) + (uv.x - radius) * tan(PI/6.)),
    	fill(-uv.x - radius)
    );
}

float pattern(vec2 uv)
{
    vec2 udx = floor(uv*5.);
    uv.y += mod(udx.x, 2.)/5. * 0.5;
    udx = floor(uv*5.);
    vec2 guv = fract(uv*5.)-.5;
    guv = rot(0.) * guv;
    return triangle(guv, mix(0.25, 0.45, pbeat(iTime + udx.x*.01, 10.0)));
}

float lstep(float a, float b, float x)
{
    return (x - a) / (b - a);
}

vec2 ring(vec2 uv, float innerRadius, float outerRadius)
{
    float a = atan(uv.y, uv.x);
    float r = length(uv);
    uv = vec2(a / PI, sat(smoothstep(innerRadius, outerRadius, r)));
    return uv;
}

/**
 * uv: us-space to deform
 * rad1: Radius of the top arcs
 * rad2: Radius of the bottom arcs
 * th: Thickness of the snake
 */
vec2 snake(vec2 uv, float rad1, float rad2, float th)
{
    float radsum = rad1 + th + rad2;
    vec2 uv0 = uv;
    vec2 uv2 = uv;

    uv.x = mod(uv.x - radsum, 2. * radsum) - radsum;
    uv2.x = mod(uv2.x, 2. * radsum) - radsum;

    uv = ring(uv, rad1, rad1 + th);
    uv2 = ring(uv2 * vec2(-1.,1.), rad2, rad2 + th);
    float mid = (rad2+th/2.)/radsum;
    uv2.x = mix(0.0, mid, lstep(-1.0, 0.0, uv2.x));
    uv.x = mix(mid, 1.0, lstep(0.0, 1.0, uv.x));
    uv2.y = 1. - uv2.y;
    vec2 uv3 = mix(uv, uv2, step(0., -uv0.y));

    uv = mix(uv, uv2, step(0., -uv0.y));
    return uv;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord.xy * 2. - iResolution.xy)/iResolution.x;
    vec2 uv0 = uv;
    float rad = 0.3;
    float th = 0.1;

    uv = snake(uv, rad - th, rad, th);
    uv.x *= 4./5.;

    float ss = 0.05;
    float t = mix(0., 1., fract(iTime * .5));
    float test = step(t,uv.x) * step(uv.x, t+ss);
    test *= step(.1, uv.y) * step(uv.y, 0.9);

    vec2 tuv = uv - vec2(iTime*0.1,0.);
    vec3 col = vec3(0., pattern(tuv), test);

    col = vec3(1.,1.,.9);
    col = mix(col, vec3(.95, 0.15, 0.1), fill((abs(fract(tuv.x*5.-.05)-0.5)-0.15)*2.0));
    col = mix(col, vec3(.9, 0.7, 0.2), fill((abs(fract(tuv.x*5.+.05)-0.5)-0.15)*2.0));
    col = mix(col, vec3(.05, 0.35, 0.9), fill((abs(fract(tuv.x*5.)-0.5)-0.15)*2.0));

    col = mix(col, vec3(.1), pattern(tuv * vec2(15.,1.)) * step(.001, tuv.y) * step(tuv.y, .999));

    float o = 0.07;
    uv = snake(uv0, rad-th - o, rad + o, th);
    col = mix(col, vec3(.1), fill(max(.45 - uv.y, uv.y - .55) * 0.35));

    o = -o;
    uv = snake(uv0, rad-th - o, rad + o, th);
    col = mix(col, vec3(.1), fill(max(.45 - uv.y, uv.y - .55) * 0.35));

    o -= 0.01;
    uv = snake(uv0, rad-th - o, rad + o, th);
    //col = mix(col, vec3(.1), fill((.55 - uv.y) * 0.35));

    fragColor = vec4(col, 1.0);
}

#define float2 vec2
#define float3 vec3
#define float4 vec4
#define lerp   mix
#define frac   fract
#define fmod   mod

float  noise(float param)   { return frac(sin(param) * 43758.937545312382); }

float2 noise2(float2 param) { return frac(sin(param) * 43758.937545312382); }

float3 circle(float2 uv, float seed)
{
    float rnd = noise(seed);
    float period = 2.0 + 2.0*rnd;
    float age = fmod(iTime + seed, period);
    float nAge = age / period;
    float t = floor(iTime - age + 0.5) + seed;

    float2 n = noise2(float2(t, t + 42.34231));

    float grad = length((uv*2.0-1.0) - n);

    nAge = sqrt(nAge);

    //shape
    float r = 1.0;
    r *= smoothstep(0.3*nAge, 0.8*nAge, grad);
    r *= 1.0-smoothstep(0.8*nAge, 1.0*nAge, grad);

    //opacity
    r *= sin(nAge*3.1415);
    //r *= 1.0-nAge*nAge;

    float3 clr = float3(1.0, 0.5, 0.3);

    float3 clrBase = float3(1.0, 0.8, 0.3);
    float3 clrOpposite = float3(1.0) - clrBase;

    return float3(r) * mix(clrBase, clrOpposite, 0.5 + sign(n.x-0.5) * (0.5+0.5*n.y) );

    return float3(r);// * clr * (0.3+0.7*frac(100.0*float3(n.x, n.y, 1.0-n.x*n.y)));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord / iResolution.xy; 
    uv.x *= iResolution.x / iResolution.y;

    float3 c = float3(0.0);
    c.rgb  = circle(uv, 0.321517);
    c.rgb += circle(uv, 1.454352);
    c.rgb += circle(uv, 2.332126);
    c.rgb += circle(uv, 3.285356);
    c.rgb += circle(uv, 4.194621);    

    //tone mapping
    float lum = dot(c.rgb, float3(0.3333));
    if(c.r>1.0) c.r = 2.0 - exp(-c.r + 1.0);
    if(c.g>1.0) c.g = 2.0 - exp(-c.g + 1.0);
    if(c.b>1.0) c.b = 2.0 - exp(-c.b + 1.0);

    fragColor.rgb = c*0.7;
}

// 2xpatterns (mod overlay) - del 20/05/2019 - sin for the win.

vec3 pat1(vec2 pos)
{
    pos*=0.5;
    pos+=vec2(0.5);
	float vv = pos.y*pos.y;
	vv*=sin(pos.x*3.14);
	float v = (sin(sin(pos.x*15.0)*4.0+(vv) *50.0 + iTime * 2.0))+0.65;
	float stime = 0.5+sin(iTime*4.0)*0.5;
	vec3 col = vec3( v*0.45, .35*v, 0.25+0.2*v) * (1.5-stime*0.4);
    return col;
}

mat2 rot(float a)
{
  float s = sin(a);
  float c = cos(a);
  return mat2(c, s, -s, c);
}

float pMod1(inout float p, float size)
{
	float halfsize = size*0.5;
	float c = floor((p + halfsize)/size);
	p = mod(p + halfsize, size) - halfsize;
	return c;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 pos = (fragCoord.xy - 0.5 * iResolution.xy) / iResolution.y;
    vec3 back = pat1(pos);

    float ns = 0.5+sin(iTime*0.7)*0.5;
    pos*=1.5+(ns*1.1);
    pos.x+=iTime*0.4;
    float c = pMod1(pos.x,0.58);
    pos.y+= cos(iTime*0.2+c);
    float c2 = pMod1(pos.y,0.58);
    pos  *= rot(iTime+c2*3.0);
    float d = 3.0-length(6.0*pos*pos)*7.0;

    pos*=rot(pos.x*5.0+iTime*0.6+log(d));
    pos+=vec2(0.5);
	float vv = (c2+pos.y*pos.y+pos.x*d*d*1.61) + sin(pos.x*12.14);
	vv = sin(sin(pos.x*22.6)*1.0+(vv) * 0.5 + iTime * 2.0);

    float s = sign(vv);
    vv = abs(vv);
	vec3 col1 = s>0.0 ? vec3( vv*.5, .05+0.3*vv, 0.0) : vec3( vv*0.14, 0.2*vv, 0.1*vv);
    col1 = clamp(col1*d,0.0,1.0);
    fragColor = vec4(mix(back, col1, smoothstep(0.5,1.0,d)),1.0);
}

