#define MAX_STEPS 200
#define MAX_DIST 100.
#define EPSILON .001

mat2 rotate(float a) {
  return mat2(cos(a), -sin(a), sin(a), cos(a));
}

float sphereSDF (vec3 p, float r) {
  return length(p) - r;
}

float boxSDF(vec3 p, vec3 s) {
    p = abs(p) - s;
	return length(max(p, 0.)) + min(max(p.x, max(p.y, p.z)), 0.);
}

float sceneSDF (vec3 p) {
  p.xz *= rotate(iTime * .2);
  p.yz *= rotate(iTime * .2);

  float s = sphereSDF(p - vec3(0.), 1.);
  s += sin(cos(iTime) + 5.*p.x) * sin(5.*p.y) * sin(5.*p.z) * .25;

  float c = boxSDF(p - vec3(0.), vec3(.8));

  p.xz *= rotate(iTime * .6);
  p.yz *= rotate(iTime * .5);

  float s2 = sphereSDF(p - vec3(0.), .3);
  float c2 = boxSDF(p - vec3(0.), vec3(.25));
  float d = max(c2, -s2);

  return min(max(c, -s * .4), d);
}

vec3 getNormal (vec3 p) {
  float d = sceneSDF(p);
  vec2 e = vec2(.001, 0.);

  return normalize(d - vec3(
    sceneSDF(p - e.xyy),
    sceneSDF(p - e.yxy),
    sceneSDF(p - e.yyx)));
}

float raymarch (vec3 ro, vec3 rd) {
  float depth = 0.;
  for (int i = 0; i < MAX_STEPS; i++) {
    float dist = sceneSDF(ro + rd * depth);
    if (dist < EPSILON) return depth;
    if (depth >= MAX_DIST) break;
    depth += dist;
  }
  return 0.;
}

float getLight (vec3 lightPos, vec3 p, float britghness) {
  vec3 light = normalize(lightPos - p) * britghness;
  vec3 normal = getNormal(p);

  float diff = clamp(dot(normal, light), 0., 1.);

  return diff;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
  vec2 uv = (fragCoord.xy / iResolution.xy) * 2.0 - 1.0;
  uv.x *= iResolution.x / iResolution.y;

  vec3 ro = vec3(0., 0., -5.);
  ro.z *= sin(iTime * .2) * .5 + .8;
  vec3 rd = normalize(vec3(uv, 2.));

  vec3 color = vec3(0.);
  float d = raymarch(ro, rd);
  vec3 p = ro + rd * d;

  if (d > 0.) {
    vec3 light2 = vec3(2., 0., 2.);
    light2.xz *= rotate(iTime);
    vec3 light1 = vec3(0., 0., 0.);
    color += getLight(light1, p, .8);
    color += getLight(light2, p, 1.);
  } 

  fragColor = vec4(color, 1.);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    vec2 uv = fragCoord/iResolution.xy;
    vec2 centre = vec2(0.5 * sin(iTime), 0.5 * sin(iTime));

    uv = uv * 10.0;

    vec2 pos = uv - centre;
    pos.y /= iResolution.x/iResolution.y;

    float dist = length(pos);

    dist = pow(dist, 0.5);
    dist -= iTime*0.1;
    dist *= 10.0;
    dist *= fract(dist);


    float angle = atan(pos.x, pos.y);
    // Normalized pixel coordinates (from 0 to 1)

    dist += angle;
    // Time varying pixel color
    vec3 col = 0.5 + 0.5*cos(dist + uv.xyx+vec3(0,2,4));

    // Output to screen
    fragColor = vec4(col,1.0);
}

