// these hash functions are by dave hoskins

// random vector
vec4 hash43(vec3 p)
{
	vec4 p4 = fract(vec4(p.xyzx)  * vec4(.1031, .1030, .0973, .1099));
    p4 += dot(p4, p4.wzxy+19.19);
    return fract((p4.xxyz+p4.yzzw)*p4.zywx);
}

vec3 hash33(vec3 p3)
{
	p3 = fract(p3 * vec3(.1031, .1030, .0973));
    p3 += dot(p3, p3.yxz+19.19);
    return fract((p3.xxy + p3.yxx)*p3.zyx);

}

// noise
// =============================================================================================================================

// by iq, https://www.shadertoy.com/view/4sfGzS

float fnoise(vec3 x) {

	vec3 p = floor(x);
    vec3 f = fract(x);
    f = f*f*(3.0-2.0*f);

    return mix(mix(mix( hash13(p+vec3(0,0,0)), 
                        hash13(p+vec3(1,0,0)),f.x),
                   mix( hash13(p+vec3(0,1,0)), 
                        hash13(p+vec3(1,1,0)),f.x),f.y),
               mix(mix( hash13(p+vec3(0,0,1)), 
                        hash13(p+vec3(1,0,1)),f.x),
                   mix( hash13(p+vec3(0,1,1)), 
                        hash13(p+vec3(1,1,1)),f.x),f.y),f.z);
}

vec3 vnoise(vec3 x) {

	vec3 p = floor(x);
    vec3 f = fract(x);
    f = f*f*(3.0-2.0*f);

    return mix(mix(mix( hash33(p+vec3(0,0,0)), 
                        hash33(p+vec3(1,0,0)),f.x),
                   mix( hash33(p+vec3(0,1,0)), 
                        hash33(p+vec3(1,1,0)),f.x),f.y),
               mix(mix( hash33(p+vec3(0,0,1)), 
                        hash33(p+vec3(1,0,1)),f.x),
                   mix( hash33(p+vec3(0,1,1)), 
                        hash33(p+vec3(1,1,1)),f.x),f.y),f.z);
}

